#!/usr/bin/env node

/**
 * Laravel Project Analysis Engine
 * 
 * Intelligently analyzes Laravel projects to generate adaptive Claude Code configurations.
 * This script is the core intelligence system for the `/user:setup-laravel` command.
 */

const fs = require('fs');
const path = require('path');

class LaravelProjectAnalyzer {
    constructor(projectPath = process.cwd()) {
        this.projectPath = projectPath;
        this.analysis = {
            exists: false,
            type: 'unknown',
            laravel: {
                version: null,
                isApi: false,
                hasAuth: false,
                hasQueues: false,
                hasScheduler: false
            },
            php: {
                version: null,
                extensions: []
            },
            database: {
                driver: 'mysql',
                hasMigrations: false,
                hasSeeds: false,
                hasFactories: false
            },
            frontend: {
                hasViews: false,
                stack: 'none',
                buildTool: 'none',
                frameworks: []
            },
            testing: {
                framework: 'phpunit',
                hasFeatureTests: false,
                hasUnitTests: false,
                coverage: false
            },
            packages: {
                development: [],
                production: [],
                laravel_ecosystem: [],
                security: [],
                performance: []
            },
            deployment: {
                indicators: [],
                environments: [],
                ci_cd: false
            },
            team: {
                hasGit: false,
                hasComposerLock: false,
                hasPackageLock: false,
                contributors: 0
            }
        };
    }

    /**
     * Main analysis method - runs all detection methods
     */
    async analyze() {
        console.log('üîç Analyzing Laravel project...');

        this.detectProjectStructure();
        await this.analyzeLaravelInstallation();
        await this.analyzeComposerDependencies();
        await this.analyzeFrontendStack();
        await this.analyzeDatabase();
        await this.analyzeTesting();
        await this.analyzeDeployment();
        await this.analyzeTeamStructure();

        this.determineProjectType();
        this.generateRecommendations();

        console.log('‚úÖ Project analysis complete');
        return this.analysis;
    }

    /**
     * Detect basic Laravel project structure
     */
    detectProjectStructure() {
        const laravelIndicators = [
            'artisan',
            'composer.json',
            'app/Http/Kernel.php',
            'config/app.php',
            'bootstrap/app.php'
        ];

        const foundIndicators = laravelIndicators.filter(file => 
            fs.existsSync(path.join(this.projectPath, file))
        );

        this.analysis.exists = foundIndicators.length >= 3;

        if (this.analysis.exists) {
            console.log('‚úÖ Laravel project detected');
            this.analysis.laravel.hasAuth = fs.existsSync(path.join(this.projectPath, 'app/Http/Controllers/Auth'));
            this.analysis.laravel.hasQueues = fs.existsSync(path.join(this.projectPath, 'app/Jobs'));
            this.analysis.database.hasMigrations = fs.existsSync(path.join(this.projectPath, 'database/migrations'));
            this.analysis.database.hasSeeds = fs.existsSync(path.join(this.projectPath, 'database/seeders'));
            this.analysis.database.hasFactories = fs.existsSync(path.join(this.projectPath, 'database/factories'));
            this.analysis.frontend.hasViews = fs.existsSync(path.join(this.projectPath, 'resources/views'));
        } else {
            console.log('‚ö†Ô∏è  No Laravel project detected - will offer to create new project');
        }
    }

    /**
     * Analyze Laravel installation details
     */
    async analyzeLaravelInstallation() {
        if (!this.analysis.exists) return;

        try {
            // Check Laravel version from composer.lock or artisan
            const composerLock = this.readJsonFile('composer.lock');
            if (composerLock && composerLock.packages) {
                const laravelPackage = composerLock.packages.find(pkg => 
                    pkg.name === 'laravel/framework'
                );
                if (laravelPackage) {
                    this.analysis.laravel.version = laravelPackage.version;
                }
            }

            // Check if API-only by examining routes and middleware
            const routesApi = fs.existsSync(path.join(this.projectPath, 'routes/api.php'));
            const routesWeb = fs.existsSync(path.join(this.projectPath, 'routes/web.php'));

            if (routesApi && !this.analysis.frontend.hasViews) {
                this.analysis.laravel.isApi = true;
            }

            // Check for scheduler
            const kernelPath = path.join(this.projectPath, 'app/Console/Kernel.php');
            if (fs.existsSync(kernelPath)) {
                const kernelContent = fs.readFileSync(kernelPath, 'utf8');
                this.analysis.laravel.hasScheduler = kernelContent.includes('schedule(');
            }

        } catch (error) {
            console.log('‚ö†Ô∏è  Could not fully analyze Laravel installation:', error.message);
        }
    }

    /**
     * Analyze Composer dependencies for Laravel ecosystem packages
     */
    async analyzeComposerDependencies() {
        const composer = this.readJsonFile('composer.json');
        if (!composer) return;

        const allDeps = {
            ...composer.require || {},
            ...composer['require-dev'] || {}
        };

        // Categorize packages
        const laravelEcosystem = [
            'livewire/livewire', 'inertiajs/inertia-laravel', 'laravel/telescope',
            'laravel/horizon', 'laravel/sanctum', 'laravel/passport', 'laravel/cashier',
            'laravel/scout', 'laravel/socialite', 'spatie/laravel-permission',
            'spatie/laravel-query-builder', 'spatie/laravel-medialibrary'
        ];

        const securityPackages = [
            'laravel/sanctum', 'laravel/passport', 'spatie/laravel-permission',
            'pragmarx/google2fa-laravel'
        ];

        const performancePackages = [
            'laravel/horizon', 'laravel/scout', 'predis/predis', 'pusher/pusher-php-server'
        ];

        Object.keys(allDeps).forEach(pkg => {
            if (laravelEcosystem.includes(pkg)) {
                this.analysis.packages.laravel_ecosystem.push(pkg);
            }
            if (securityPackages.includes(pkg)) {
                this.analysis.packages.security.push(pkg);
            }
            if (performancePackages.includes(pkg)) {
                this.analysis.packages.performance.push(pkg);
            }
        });

        // Store all packages
        this.analysis.packages.production = Object.keys(composer.require || {});
        this.analysis.packages.development = Object.keys(composer['require-dev'] || {});
    }

    /**
     * Analyze frontend stack and build tools
     */
    async analyzeFrontendStack() {
        const packageJson = this.readJsonFile('package.json');

        if (packageJson) {
            const deps = {
                ...packageJson.dependencies || {},
                ...packageJson.devDependencies || {}
            };

            // Detect frontend frameworks
            if (deps['@livewire/core'] || deps['livewire']) {
                this.analysis.frontend.stack = 'livewire';
                this.analysis.frontend.frameworks.push('Livewire');
            }

            if (deps['@inertiajs/inertia']) {
                this.analysis.frontend.stack = 'inertia';
                this.analysis.frontend.frameworks.push('Inertia.js');
            }

            if (deps['vue'] || deps['@vue/cli']) {
                this.analysis.frontend.frameworks.push('Vue.js');
            }

            if (deps['react'] || deps['react-dom']) {
                this.analysis.frontend.frameworks.push('React');
            }

            if (deps['alpinejs']) {
                this.analysis.frontend.frameworks.push('Alpine.js');
            }

            // Detect build tools
            if (deps['vite'] || fs.existsSync(path.join(this.projectPath, 'vite.config.js'))) {
                this.analysis.frontend.buildTool = 'vite';
            } else if (deps['laravel-mix'] || fs.existsSync(path.join(this.projectPath, 'webpack.mix.js'))) {
                this.analysis.frontend.buildTool = 'mix';
            } else if (deps['webpack']) {
                this.analysis.frontend.buildTool = 'webpack';
            }
        }

        // If no specific stack detected but has views, assume Blade
        if (this.analysis.frontend.hasViews && this.analysis.frontend.stack === 'none') {
            this.analysis.frontend.stack = 'blade';
        }
    }

    /**
     * Analyze database configuration
     */
    async analyzeDatabase() {
        if (!this.analysis.exists) return;

        // Check database configuration
        const dbConfig = path.join(this.projectPath, 'config/database.php');
        if (fs.existsSync(dbConfig)) {
            const content = fs.readFileSync(dbConfig, 'utf8');

            if (content.includes("'mysql'")) this.analysis.database.driver = 'mysql';
            else if (content.includes("'postgresql'")) this.analysis.database.driver = 'postgresql';
            else if (content.includes("'sqlite'")) this.analysis.database.driver = 'sqlite';
        }

        // Check for database tools
        const envFile = path.join(this.projectPath, '.env.example');
        if (fs.existsSync(envFile)) {
            const envContent = fs.readFileSync(envFile, 'utf8');
            if (envContent.includes('REDIS_')) {
                this.analysis.packages.performance.push('redis');
            }
        }
    }

    /**
     * Analyze testing setup
     */
    async analyzeTesting() {
        if (!this.analysis.exists) return;

        // Check for Pest
        const composer = this.readJsonFile('composer.json');
        if (composer && composer['require-dev'] && composer['require-dev']['pestphp/pest']) {
            this.analysis.testing.framework = 'pest';
        }

        // Check for test directories
        this.analysis.testing.hasFeatureTests = fs.existsSync(path.join(this.projectPath, 'tests/Feature'));
        this.analysis.testing.hasUnitTests = fs.existsSync(path.join(this.projectPath, 'tests/Unit'));

        // Check for coverage configuration
        const phpunitXml = fs.existsSync(path.join(this.projectPath, 'phpunit.xml'));
        if (phpunitXml) {
            const content = fs.readFileSync(path.join(this.projectPath, 'phpunit.xml'), 'utf8');
            this.analysis.testing.coverage = content.includes('coverage');
        }
    }

    /**
     * Analyze deployment indicators
     */
    async analyzeDeployment() {
        const deploymentFiles = [
            { file: 'Dockerfile', indicator: 'docker' },
            { file: 'docker-compose.yml', indicator: 'docker-compose' },
            { file: '.github/workflows', indicator: 'github-actions' },
            { file: 'Procfile', indicator: 'heroku' },
            { file: 'forge.yml', indicator: 'laravel-forge' },
            { file: 'vapor.yml', indicator: 'laravel-vapor' },
            { file: 'Envoy.blade.php', indicator: 'laravel-envoy' }
        ];

        deploymentFiles.forEach(({ file, indicator }) => {
            if (fs.existsSync(path.join(this.projectPath, file))) {
                this.analysis.deployment.indicators.push(indicator);
            }
        });

        // Check for multiple environment files
        const envFiles = ['production', 'staging', 'development', 'testing']
            .map(env => `.env.${env}`)
            .filter(file => fs.existsSync(path.join(this.projectPath, file)));

        this.analysis.deployment.environments = envFiles;
        this.analysis.deployment.ci_cd = this.analysis.deployment.indicators.some(ind => 
            ['github-actions', 'gitlab-ci', 'jenkins'].includes(ind)
        );
    }

    /**
     * Analyze team collaboration setup
     */
    async analyzeTeamStructure() {
        this.analysis.team.hasGit = fs.existsSync(path.join(this.projectPath, '.git'));
        this.analysis.team.hasComposerLock = fs.existsSync(path.join(this.projectPath, 'composer.lock'));
        this.analysis.team.hasPackageLock = fs.existsSync(path.join(this.projectPath, 'package-lock.json')) ||
                                           fs.existsSync(path.join(this.projectPath, 'yarn.lock'));
    }

    /**
     * Determine overall project type based on analysis
     */
    determineProjectType() {
        if (!this.analysis.exists) {
            this.analysis.type = 'new-project';
        } else if (this.analysis.laravel.isApi && !this.analysis.frontend.hasViews) {
            this.analysis.type = 'api-only';
        } else if (this.analysis.frontend.stack === 'livewire') {
            this.analysis.type = 'livewire-spa';
        } else if (this.analysis.frontend.stack === 'inertia') {
            this.analysis.type = 'inertia-spa';
        } else if (this.analysis.frontend.frameworks.length > 0) {
            this.analysis.type = 'full-stack-spa';
        } else if (this.analysis.frontend.hasViews) {
            this.analysis.type = 'traditional-web';
        } else if (fs.existsSync(path.join(this.projectPath, 'src')) && 
                   !fs.existsSync(path.join(this.projectPath, 'app'))) {
            this.analysis.type = 'package-development';
        } else {
            this.analysis.type = 'unknown';
        }
    }

    /**
     * Generate recommendations based on analysis
     */
    generateRecommendations() {
        this.analysis.recommendations = {
            commands: [],
            permissions: [],
            integrations: [],
            workflows: []
        };

        // Command recommendations based on project type
        switch (this.analysis.type) {
            case 'api-only':
                this.analysis.recommendations.commands.push(
                    'api-resource', 'api-docs', 'security-audit', 'performance-audit'
                );
                break;
            case 'livewire-spa':
                this.analysis.recommendations.commands.push(
                    'livewire-component', 'feature', 'test-suite', 'performance-audit'
                );
                break;
            case 'inertia-spa':
                this.analysis.recommendations.commands.push(
                    'inertia-page', 'feature', 'api-resource', 'test-suite'
                );
                break;
            default:
                this.analysis.recommendations.commands.push(
                    'feature', 'test-suite', 'security-audit', 'code-quality'
                );
        }

        // Integration recommendations
        if (this.analysis.database.driver === 'mysql') {
            this.analysis.recommendations.integrations.push('mysql-mcp');
        }
        if (this.analysis.packages.laravel_ecosystem.includes('laravel/telescope')) {
            this.analysis.recommendations.integrations.push('telescope-integration');
        }
    }

    /**
     * Utility method to read JSON files safely
     */
    readJsonFile(filename) {
        try {
            const filePath = path.join(this.projectPath, filename);
            if (fs.existsSync(filePath)) {
                return JSON.parse(fs.readFileSync(filePath, 'utf8'));
            }
        } catch (error) {
            console.log(`‚ö†Ô∏è  Could not read ${filename}:`, error.message);
        }
        return null;
    }

    /**
     * Generate summary report
     */
    generateReport() {
        const report = {
            project_type: this.analysis.type,
            laravel_version: this.analysis.laravel.version,
            frontend_stack: this.analysis.frontend.stack,
            testing_framework: this.analysis.testing.framework,
            key_packages: this.analysis.packages.laravel_ecosystem,
            deployment_ready: this.analysis.deployment.indicators.length > 0,
            team_collaboration: this.analysis.team.hasGit && this.analysis.team.hasComposerLock,
            recommendations: this.analysis.recommendations
        };

        return report;
    }
}

// CLI usage
if (require.main === module) {
    const analyzer = new LaravelProjectAnalyzer();
    analyzer.analyze()
        .then(analysis => {
            console.log('\nüìä Analysis Results:');
            console.log(JSON.stringify(analyzer.generateReport(), null, 2));

            // Write analysis to file for use by setup command
            fs.writeFileSync(
                path.join(process.cwd(), '.claude', 'project-analysis.json'),
                JSON.stringify(analysis, null, 2)
            );
            console.log('\nüíæ Analysis saved to .claude/project-analysis.json');
        })
        .catch(error => {
            console.error('‚ùå Analysis failed:', error);
            process.exit(1);
        });
}

module.exports = LaravelProjectAnalyzer;

