        const phpunitXml = fs.existsSync(path.join(this.projectPath, 'phpunit.xml'));
        if (phpunitXml) {
            const content = fs.readFileSync(path.join(this.projectPath, 'phpunit.xml'), 'utf8');
            this.analysis.testing.coverage = content.includes('coverage');
        }
    }

    /**
     * Analyze deployment indicators
     */
    async analyzeDeployment() {
        const deploymentFiles = [
            { file: 'Dockerfile', indicator: 'docker' },
            { file: 'docker-compose.yml', indicator: 'docker-compose' },
            { file: '.github/workflows', indicator: 'github-actions' },
            { file: 'Procfile', indicator: 'heroku' },
            { file: 'forge.yml', indicator: 'laravel-forge' },
            { file: 'vapor.yml', indicator: 'laravel-vapor' },
            { file: 'Envoy.blade.php', indicator: 'laravel-envoy' }
        ];

        deploymentFiles.forEach(({ file, indicator }) => {
            if (fs.existsSync(path.join(this.projectPath, file))) {
                this.analysis.deployment.indicators.push(indicator);
            }
        });

        // Check for multiple environment files
        const envFiles = ['production', 'staging', 'development', 'testing']
            .map(env => `.env.${env}`)
            .filter(file => fs.existsSync(path.join(this.projectPath, file)));

        this.analysis.deployment.environments = envFiles;
        this.analysis.deployment.ci_cd = this.analysis.deployment.indicators.some(ind => 
            ['github-actions', 'gitlab-ci', 'jenkins'].includes(ind)
        );
    }

    /**
     * Analyze team collaboration setup
     */
    async analyzeTeamStructure() {
        this.analysis.team.hasGit = fs.existsSync(path.join(this.projectPath, '.git'));
        this.analysis.team.hasComposerLock = fs.existsSync(path.join(this.projectPath, 'composer.lock'));
        this.analysis.team.hasPackageLock = fs.existsSync(path.join(this.projectPath, 'package-lock.json')) ||
                                           fs.existsSync(path.join(this.projectPath, 'yarn.lock'));
    }

    /**
     * Determine overall project type based on analysis
     */
    determineProjectType() {
        if (!this.analysis.exists) {
            this.analysis.type = 'new-project';
        } else if (this.analysis.laravel.isApi && !this.analysis.frontend.hasViews) {
            this.analysis.type = 'api-only';
        } else if (this.analysis.frontend.stack === 'livewire') {
            this.analysis.type = 'livewire-spa';
        } else if (this.analysis.frontend.stack === 'inertia') {
            this.analysis.type = 'inertia-spa';
        } else if (this.analysis.frontend.frameworks.length > 0) {
            this.analysis.type = 'full-stack-spa';
        } else if (this.analysis.frontend.hasViews) {
            this.analysis.type = 'traditional-web';
        } else if (fs.existsSync(path.join(this.projectPath, 'src')) && 
                   !fs.existsSync(path.join(this.projectPath, 'app'))) {
            this.analysis.type = 'package-development';
        } else {
            this.analysis.type = 'unknown';
        }
    }

    /**
     * Generate recommendations based on analysis
     */
    generateRecommendations() {
        this.analysis.recommendations = {
            commands: [],
            permissions: [],
            integrations: [],
            workflows: []
        };

        // Command recommendations based on project type
        switch (this.analysis.type) {
            case 'api-only':
                this.analysis.recommendations.commands.push(
                    'api-resource', 'api-docs', 'security-audit', 'performance-audit'
                );
                break;
            case 'livewire-spa':
                this.analysis.recommendations.commands.push(
                    'livewire-component', 'feature', 'test-suite', 'performance-audit'
                );
                break;
            case 'inertia-spa':
                this.analysis.recommendations.commands.push(
                    'inertia-page', 'feature', 'api-resource', 'test-suite'
                );
                break;
            default:
                this.analysis.recommendations.commands.push(
                    'feature', 'test-suite', 'security-audit', 'code-quality'
                );
        }

        // Integration recommendations
        if (this.analysis.database.driver === 'mysql') {
            this.analysis.recommendations.integrations.push('mysql-mcp');
        }
        if (this.analysis.packages.laravel_ecosystem.includes('laravel/telescope')) {
            this.analysis.recommendations.integrations.push('telescope-integration');
        }
    }

    /**
     * Utility method to read JSON files safely
     */
    readJsonFile(filename) {
        try {
            const filePath = path.join(this.projectPath, filename);
            if (fs.existsSync(filePath)) {
                return JSON.parse(fs.readFileSync(filePath, 'utf8'));
            }
        } catch (error) {
            console.log(`‚ö†Ô∏è  Could not read ${filename}:`, error.message);
        }
        return null;
    }

    /**
     * Generate summary report
     */
    generateReport() {
        const report = {
            project_type: this.analysis.type,
            laravel_version: this.analysis.laravel.version,
            frontend_stack: this.analysis.frontend.stack,
            testing_framework: this.analysis.testing.framework,
            key_packages: this.analysis.packages.laravel_ecosystem,
            deployment_ready: this.analysis.deployment.indicators.length > 0,
            team_collaboration: this.analysis.team.hasGit && this.analysis.team.hasComposerLock,
            recommendations: this.analysis.recommendations
        };

        return report;
    }
}

// CLI usage
if (require.main === module) {
    const analyzer = new LaravelProjectAnalyzer();
    analyzer.analyze()
        .then(analysis => {
            console.log('\nüìä Analysis Results:');
            console.log(JSON.stringify(analyzer.generateReport(), null, 2));

            // Write analysis to file for use by setup command
            fs.writeFileSync(
                path.join(process.cwd(), '.claude', 'project-analysis.json'),
                JSON.stringify(analysis, null, 2)
            );
            console.log('\nüíæ Analysis saved to .claude/project-analysis.json');
        })
        .catch(error => {
            console.error('‚ùå Analysis failed:', error);
            process.exit(1);
        });
}

module.exports = LaravelProjectAnalyzer;

