#!/usr/bin/env node

/**
 * Adaptive Permission System Generator for Laravel Projects
 * 
 * Generates intelligent permission matrices based on project analysis,
 * environment detection, and security best practices.
 */

const fs = require('fs');
const path = require('path');

class LaravelPermissionGenerator {
    constructor(projectPath = process.cwd(), analysis = null) {
        this.projectPath = projectPath;
        this.analysis = analysis;
        this.permissions = {
            allow: [],
            deny: [],
            environments: {
                local: { allow: [], deny: [] },
                staging: { allow: [], deny: [] },
                production: { allow: [], deny: [] }
            },
            conditions: []
        };
    }

    /**
     * Generate comprehensive permission system
     */
    async generate() {
        console.log('üîê Generating adaptive permission system...');

        // Load project analysis if not provided
        if (!this.analysis) {
            this.loadAnalysis();
        }

        this.generateBaseLaravelPermissions();
        this.generatePackageSpecificPermissions();
        this.generateFrontendPermissions();
        this.generateDatabasePermissions();
        this.generateTestingPermissions();
        this.generateDeploymentPermissions();
        this.generateEnvironmentSpecificPermissions();
        this.generateSecurityPolicies();
        this.generateConditionalPermissions();

        console.log('‚úÖ Permission system generated');
        return this.permissions;
    }

    /**
     * Load project analysis from file
     */
    loadAnalysis() {
        try {
            const analysisFile = path.join(this.projectPath, '.claude', 'project-analysis.json');
            if (fs.existsSync(analysisFile)) {
                this.analysis = JSON.parse(fs.readFileSync(analysisFile, 'utf8'));
            }
        } catch (error) {
            console.log('‚ö†Ô∏è  Could not load project analysis, using defaults');
            this.analysis = { type: 'unknown', exists: false };
        }
    }

    /**
     * Generate base Laravel permissions that are safe for all projects
     */
    generateBaseLaravelPermissions() {
        // Core Artisan commands that are always safe
        const safeArtisanCommands = [
            // Make commands (code generation)
            'Bash(php artisan make:*)',

            // Information commands
            'Bash(php artisan route:list*)',
            'Bash(php artisan route:cache*)',
            'Bash(php artisan route:clear*)',
            'Bash(php artisan config:show*)',
            'Bash(php artisan config:cache*)',
            'Bash(php artisan config:clear*)',
            'Bash(php artisan view:cache*)',
            'Bash(php artisan view:clear*)',
            'Bash(php artisan event:cache*)',
            'Bash(php artisan event:clear*)',
            'Bash(php artisan optimize*)',
            'Bash(php artisan optimize:clear*)',

            // Development utilities
            'Bash(php artisan tinker --execute=*)',
            'Bash(php artisan inspire*)',
            'Bash(php artisan list*)',
            'Bash(php artisan help*)',
            'Bash(php artisan --version*)',

            // Queue information (not processing)
            'Bash(php artisan queue:monitor*)',
            'Bash(php artisan queue:failed*)',
            'Bash(php artisan horizon:status*)',
        ];

        // Migration commands with safety checks
        const safeMigrationCommands = [
            'Bash(php artisan migrate:status*)',
            'Bash(php artisan migrate:fresh --seed*)', // Only in local
            'Bash(php artisan migrate:refresh --seed*)', // Only in local
            'Bash(php artisan migrate:rollback --step=1*)', // Limited rollback
            'Bash(php artisan migrate --pretend*)', // Dry run
        ];

        // Composer commands
        const safeComposerCommands = [
            'Bash(composer install*)',
            'Bash(composer update*)',
            'Bash(composer require *)',
            'Bash(composer remove *)',
            'Bash(composer show*)',
            'Bash(composer outdated*)',
            'Bash(composer validate*)',
            'Bash(composer diagnose*)',
            'Bash(composer dump-autoload*)',
        ];

        // File system operations within project
        const safeFileOperations = [
            'Read(*)',
            'Write(app/**/*)',
            'Write(database/**/*)',
            'Write(resources/**/*)',
            'Write(routes/**/*)',
            'Write(tests/**/*)',
            'Write(config/**/*)',
            'Edit(app/**/*)',
            'Edit(database/**/*)',
            'Edit(resources/**/*)',
            'Edit(routes/**/*)',
            'Edit(tests/**/*)',
            'Edit(config/**/*)',
            'Glob(*)',
            'Grep(*)',
            'LS(*)',
        ];

        this.permissions.allow.push(
            ...safeArtisanCommands,
            ...safeMigrationCommands,
            ...safeComposerCommands,
            ...safeFileOperations
        );

        // Always deny dangerous operations
        const dangerousOperations = [
            'Bash(rm -rf*)',
            'Bash(sudo*)',
            'Bash(chmod 777*)',
            'Bash(php artisan migrate:fresh*)', // Without explicit local env
            'Bash(php artisan db:wipe*)',
            'Bash(php artisan down*)', // Maintenance mode
            'Write(/.env*)', // Environment files
            'Write(/composer.json*)', // Core files without review
            'Bash(git push*)', // Prevent accidental pushes
        ];

        this.permissions.deny.push(...dangerousOperations);
    }

    /**
     * Generate permissions for detected Laravel packages
     */
    generatePackageSpecificPermissions() {
        if (!this.analysis.packages) return;

        const packagePermissions = {
            'laravel/telescope': [
                'Bash(php artisan telescope:install*)',
                'Bash(php artisan telescope:publish*)',
                'Bash(php artisan telescope:clear*)',
                'Bash(php artisan telescope:prune*)',
            ],
            'laravel/horizon': [
                'Bash(php artisan horizon*)',
                'Bash(php artisan horizon:status*)',
                'Bash(php artisan horizon:pause*)',
                'Bash(php artisan horizon:continue*)',
                'Bash(php artisan horizon:terminate*)',
            ],
            'livewire/livewire': [
                'Bash(php artisan livewire:make*)',
                'Bash(php artisan livewire:copy*)',
                'Bash(php artisan livewire:move*)',
                'Bash(php artisan livewire:delete*)',
            ],
            'spatie/laravel-permission': [
                'Bash(php artisan permission:*)',
                'Bash(php artisan make:permission*)',
            ],
            'laravel/sanctum': [
                'Bash(php artisan sanctum:prune-expired*)',
                'Bash(php artisan vendor:publish --provider="Laravel\\Sanctum\\SanctumServiceProvider"*)',
            ],
            'pestphp/pest': [
                'Bash(./vendor/bin/pest*)',
                'Bash(php artisan pest:*)',
            ]
        };

        // Add permissions for detected packages
        Object.keys(packagePermissions).forEach(packageName => {
            if (this.analysis.packages.laravel_ecosystem?.includes(packageName) ||
                this.analysis.packages.production?.includes(packageName) ||
                this.analysis.packages.development?.includes(packageName)) {
                this.permissions.allow.push(...packagePermissions[packageName]);
            }
        });
    }

    /**
     * Generate frontend-specific permissions
     */
    generateFrontendPermissions() {
        if (!this.analysis.frontend) return;

        const frontendCommands = [
            'Bash(npm install*)',
            'Bash(npm run dev*)',
            'Bash(npm run build*)',
            'Bash(npm run watch*)',
            'Bash(npm run hot*)',
            'Bash(npm run production*)',
            'Bash(yarn install*)',
            'Bash(yarn dev*)',
            'Bash(yarn build*)',
            'Bash(yarn watch*)',
            'Bash(yarn hot*)',
            'Bash(yarn production*)',
        ];

        // Build tool specific commands
        if (this.analysis.frontend.buildTool === 'vite') {
            frontendCommands.push(
                'Bash(npx vite*)',
                'Bash(npx vite build*)',
                'Bash(npx vite preview*)',
            );
        } else if (this.analysis.frontend.buildTool === 'mix') {
            frontendCommands.push(
                'Bash(npx mix*)',
                'Bash(npx webpack*)',
            );
        }

        // Frontend framework specific
        if (this.analysis.frontend.frameworks?.includes('Vue.js')) {
            frontendCommands.push(
                'Bash(npx vue-cli-service*)',
                'Bash(npm run lint*)',
            );
        }

        if (this.analysis.frontend.frameworks?.includes('React')) {
            frontendCommands.push(
                'Bash(npx react-scripts*)',
                'Bash(npm run test*)',
            );
        }

        this.permissions.allow.push(...frontendCommands);
    }

    /**
     * Generate database-specific permissions
     */
    generateDatabasePermissions() {
        if (!this.analysis.database) return;

        const databaseCommands = [
            'Bash(php artisan migrate*)',
            'Bash(php artisan migrate:rollback*)',
            'Bash(php artisan migrate:reset*)',
            'Bash(php artisan db:seed*)',
            'Bash(php artisan make:migration*)',
            'Bash(php artisan make:seeder*)',
            'Bash(php artisan make:factory*)',
            'Bash(php artisan make:model*)',
        ];

        // Database-specific tools
        if (this.analysis.database.driver === 'mysql') {
            databaseCommands.push(
                'Bash(mysql --version*)',
                'Bash(mysqldump*)', // For backups only
            );
        } else if (this.analysis.database.driver === 'postgresql') {
            databaseCommands.push(
                'Bash(psql --version*)',
                'Bash(pg_dump*)', // For backups only
            );
        }

        this.permissions.allow.push(...databaseCommands);

        // Dangerous database operations
        this.permissions.deny.push(
            'Bash(php artisan migrate:fresh*)', // Unless in local
            'Bash(php artisan db:wipe*)',
            'Bash(mysql -e "DROP DATABASE*")',
            'Bash(psql -c "DROP DATABASE*")',
        );
    }

    /**
     * Generate testing-specific permissions
     */
    generateTestingPermissions() {
        const testingCommands = [
            'Bash(php artisan test*)',
            'Bash(phpunit*)',
            'Bash(./vendor/bin/phpunit*)',
            'Bash(php artisan make:test*)',
        ];

        if (this.analysis.testing?.framework === 'pest') {
            testingCommands.push(
                'Bash(./vendor/bin/pest*)',
                'Bash(php artisan pest:*)',
            );
        }

        // Code quality tools
        const qualityCommands = [
            'Bash(./vendor/bin/phpstan*)',
            'Bash(./vendor/bin/php-cs-fixer*)',
            'Bash(./vendor/bin/pint*)',
            'Bash(./vendor/bin/psalm*)',
            'Bash(./vendor/bin/phpmd*)',
        ];

        this.permissions.allow.push(...testingCommands, ...qualityCommands);
    }

    /**
     * Generate deployment-specific permissions
     */
    generateDeploymentPermissions() {
        if (!this.analysis.deployment) return;

        // Safe deployment preparation commands
        const deploymentCommands = [
            'Bash(php artisan optimize*)',
            'Bash(php artisan cache:clear*)',
            'Bash(php artisan config:cache*)',
            'Bash(php artisan route:cache*)',
            'Bash(php artisan view:cache*)',
            'Bash(php artisan storage:link*)',
        ];

        // Deployment tool specific
        if (this.analysis.deployment.indicators?.includes('laravel-envoy')) {
            deploymentCommands.push('Bash(php artisan envoy:run*)',);
        }

        if (this.analysis.deployment.indicators?.includes('docker')) {
            deploymentCommands.push(
                'Bash(docker-compose up -d*)',
                'Bash(docker-compose down*)',
                'Bash(docker-compose logs*)',
                'Bash(docker ps*)',
            );
        }

        this.permissions.allow.push(...deploymentCommands);

        // Dangerous deployment operations
        this.permissions.deny.push(
            'Bash(php artisan down*)', // Maintenance mode
            'Bash(git push origin main*)', // Prevent accidental production pushes
            'Bash(docker-compose down -v*)', // Prevent volume deletion
        );
    }

    /**
     * Generate environment-specific permissions
     */
    generateEnvironmentSpecificPermissions() {
        // Local development permissions (most permissive)
        this.permissions.environments.local.allow.push(
            'Bash(php artisan migrate:fresh --seed*)',
            'Bash(php artisan migrate:refresh --seed*)',
            'Bash(php artisan db:wipe*)',
            'Bash(php artisan tinker*)',
            'Bash(php artisan queue:work*)',
            'Bash(php artisan schedule:work*)',
            'Write(.env*)', // Allow env changes in local
        );

        // Staging permissions (moderate)
        this.permissions.environments.staging.allow.push(
            'Bash(php artisan migrate*)',
            'Bash(php artisan migrate:rollback --step=1*)',
            'Bash(php artisan cache:clear*)',
            'Bash(php artisan optimize*)',
        );

        this.permissions.environments.staging.deny.push(
            'Bash(php artisan migrate:fresh*)',
            'Bash(php artisan db:wipe*)',
            'Write(.env*)',
        );

        // Production permissions (most restrictive)
        this.permissions.environments.production.allow.push(
            'Bash(php artisan migrate --force*)', // Only with explicit force
            'Bash(php artisan optimize*)',
            'Bash(php artisan cache:clear*)',
            'Bash(php artisan queue:restart*)',
        );

        this.permissions.environments.production.deny.push(
            'Bash(php artisan migrate:fresh*)',
            'Bash(php artisan migrate:refresh*)',
            'Bash(php artisan db:wipe*)',
            'Bash(php artisan tinker*)',
            'Write(.env*)',
            'Write(config/**/*)',
            'Bash(composer require*)', // Prevent package changes
        );
    }

    /**
     * Generate security policies and conditional permissions
     */
    generateSecurityPolicies() {
        // Time-based restrictions
        this.permissions.conditions.push({
            type: 'time_restriction',
            description: 'Restrict dangerous operations during business hours',
            condition: 'business_hours',
            apply_to: [
                'Bash(php artisan migrate:fresh*)',
                'Bash(php artisan db:wipe*)',
                'Bash(php artisan down*)',
            ]
        });

        // File size restrictions
        this.permissions.conditions.push({
            type: 'file_size_limit',
            description: 'Limit file operations to reasonable sizes',
            max_size: '10MB',
            apply_to: ['Write(*)', 'Edit(*)']
        });

        // Confirmation required for dangerous operations
        this.permissions.conditions.push({
            type: 'confirmation_required',
            description: 'Require explicit confirmation for destructive operations',
            apply_to: [
                'Bash(php artisan migrate:fresh*)',
                'Bash(php artisan db:wipe*)',
                'Bash(rm *)',
                'Bash(composer remove*)',
            ]
        });
    }

    /**
     * Generate conditional permissions based on context
     */
    generateConditionalPermissions() {
        // Git status-based permissions
        this.permissions.conditions.push({
            type: 'git_status',
            description: 'Allow certain operations only with clean git status',
            condition: 'clean_working_directory',
            apply_to: [
                'Bash(php artisan migrate:fresh*)',
                'Bash(composer update*)',
            ]
        });

        // Testing requirement
        if (this.analysis.testing?.hasFeatureTests || this.analysis.testing?.hasUnitTests) {
            this.permissions.conditions.push({
                type: 'test_passing',
                description: 'Require tests to pass before certain operations',
                condition: 'tests_passing',
                apply_to: [
                    'Bash(git push*)',
                    'Bash(php artisan optimize*)',
                ]
            });
        }
    }

    /**
     * Generate final permission configuration
     */
    generateConfig() {
        const config = {
            permissions: {
                allow: this.permissions.allow,
                deny: this.permissions.deny
            },
            environment_overrides: this.permissions.environments,
            conditional_permissions: this.permissions.conditions,
            security_policies: {
                auto_approve_safe_operations: true,
                require_confirmation_for_destructive: true,
                respect_environment_restrictions: true,
                enable_audit_logging: true
            },
            metadata: {
                generated_at: new Date().toISOString(),
                project_type: this.analysis.type,
                laravel_version: this.analysis.laravel?.version,
                generator_version: '1.0.0'
            }
        };

        return config;
    }

    /**
     * Save permission configuration
     */
    async saveConfig() {
        const config = this.generateConfig();

        // Save comprehensive config for reference
        const fullConfigPath = path.join(this.projectPath, '.claude', 'permissions-full.json');
        fs.writeFileSync(fullConfigPath, JSON.stringify(config, null, 2));

        // Save simplified config for Claude Code
        const simpleConfig = {
            permissions: config.permissions
        };

        return { full: config, simple: simpleConfig };
    }
}

// CLI usage
if (require.main === module) {
    const generator = new LaravelPermissionGenerator();
    generator.generate()
        .then(() => generator.saveConfig())
        .then(configs => {
            console.log('\nüîê Permission System Generated:');
            console.log(`‚úÖ Allow rules: ${configs.simple.permissions.allow.length}`);
            console.log(`‚ùå Deny rules: ${configs.simple.permissions.deny.length}`);
            console.log(`üìã Conditional rules: ${configs.full.conditional_permissions.length}`);
            console.log('\nüíæ Configurations saved to .claude/permissions-full.json');
        })
        .catch(error => {
            console.error('‚ùå Permission generation failed:', error);
            process.exit(1);
        });
}

module.exports = LaravelPermissionGenerator;

