#!/usr/bin/env node

/**
 * Intelligent CLAUDE.md Documentation Generator
 * 
 * Generates comprehensive, context-aware documentation that helps Claude
 * understand Laravel project structure, conventions, and workflows.
 */

const fs = require('fs');
const path = require('path');

class ClaudeDocumentationGenerator {
    constructor(projectPath = process.cwd(), analysis = null) {
        this.projectPath = projectPath;
        this.analysis = analysis;
        this.sections = [];
    }

    /**
     * Generate comprehensive CLAUDE.md documentation
     */
    async generate() {
        console.log('üìù Generating intelligent CLAUDE.md documentation...');

        // Load project analysis if not provided
        if (!this.analysis) {
            this.loadAnalysis();
        }

        this.generateProjectOverview();
        this.generateArchitectureSection();
        this.generateDevelopmentWorkflows();
        this.generateCodingStandards();
        this.generateTestingGuidelines();
        this.generateDeploymentInformation();
        this.generateTroubleshootingGuide();
        this.generateContextualTips();
        this.generateCommandReference();

        const documentation = this.compileSections();
        console.log('‚úÖ CLAUDE.md documentation generated');
        return documentation;
    }

    /**
     * Load project analysis from file
     */
    loadAnalysis() {
        try {
            const analysisFile = path.join(this.projectPath, '.claude', 'project-analysis.json');
            if (fs.existsSync(analysisFile)) {
                this.analysis = JSON.parse(fs.readFileSync(analysisFile, 'utf8'));
            }
        } catch (error) {
            console.log('‚ö†Ô∏è  Could not load project analysis, using defaults');
            this.analysis = { type: 'unknown', exists: false };
        }
    }

    /**
     * Generate project overview section
     */
    generateProjectOverview() {
        const projectName = this.getProjectName();
        const overview = `# ${projectName} - Laravel Project

## Project Overview

**Project Type**: ${this.formatProjectType()}
**Laravel Version**: ${this.analysis.laravel?.version || 'Unknown'}
**PHP Version**: ${this.analysis.php?.version || 'Detected from composer.json'}
**Database**: ${(this.analysis.database?.driver || 'mysql').toUpperCase()}
**Frontend Stack**: ${this.formatFrontendStack()}
**Testing Framework**: ${(this.analysis.testing?.framework || 'PHPUnit').toUpperCase()}

## Quick Context for Claude

This is a ${this.formatProjectType()} Laravel application. When working on this project:
${this.generateQuickTips()}

## Key Features & Packages
${this.generatePackageOverview()}`;

        this.sections.push(overview);
    }

    /**
     * Generate architecture section
     */
    generateArchitectureSection() {
        const architecture = `## Architecture & Structure

### Project Structure
\`\`\`
${this.generateDirectoryTree()}
\`\`\`

### Key Architectural Decisions
${this.generateArchitecturalDecisions()}

### Domain Organization
${this.generateDomainOrganization()}

### Service Layer
${this.generateServiceLayerInfo()}`;

        this.sections.push(architecture);
    }

    /**
     * Generate development workflows section
     */
    generateDevelopmentWorkflows() {
        const workflows = `## Development Workflows

### Feature Development Process
${this.generateFeatureDevelopmentProcess()}

### Database Workflow
${this.generateDatabaseWorkflow()}

### Frontend Development
${this.generateFrontendWorkflow()}

### Testing Workflow
${this.generateTestingWorkflow()}

