        if (this.analysis.packages?.security?.includes('laravel/sanctum')) {
            tips.push('- Configure Sanctum tokens with appropriate expiration');
        }

        return tips.join('\n');
    }

    compileSections() {
        return this.sections.join('\n\n---\n\n');
    }

    /**
     * Save documentation to file
     */
    async saveDocumentation() {
        const documentation = await this.generate();
        const claudePath = path.join(this.projectPath, '.claude', 'CLAUDE.md');
        fs.writeFileSync(claudePath, documentation);
        return documentation;
    }
}

// CLI usage
if (require.main === module) {
    const generator = new ClaudeDocumentationGenerator();
    generator.saveDocumentation()
        .then(() => {
            console.log('\nüìù CLAUDE.md Documentation Generated Successfully!');
            console.log('üíæ Saved to .claude/CLAUDE.md');
        })
        .catch(error => {
            console.error('‚ùå Documentation generation failed:', error);
            process.exit(1);
        });
}

module.exports = ClaudeDocumentationGenerator;

