class ProductFilter
{
    public function apply($query, $filters)
    {
        foreach ($filters as $filter => $value) {
            $method = 'filter' . ucfirst($filter);
            if (method_exists($this, $method)) {
                $this->$method($query, $value);
            }
        }
        return $query;
    }

    protected function filterPrice($query, $value)
    {
        if (isset($value['min'])) {
            $query->where('price', '>=', $value['min']);
        }
        if (isset($value['max'])) {
            $query->where('price', '<=', $value['max']);
        }
    }

    protected function filterCategory($query, $value)
    {
        $query->whereHas('category', function($q) use ($value) {
            $q->where('slug', $value);
        });
    }
}
