    }

    public function test_can_delete_product()
    {
        $product = Product::factory()->create();

        Livewire::test(ProductTable::class)
            ->call('delete', $product->id)
            ->assertSuccessful();

        $this->assertDatabaseMissing('products', ['id' => $product->id]);
