<?php

namespace App\Http\Livewire;

use App\Models\User;
use Livewire\Component;
use Livewire\WithFileUploads;

class UserProfile extends Component
{
    use WithFileUploads;

    // Form properties
    public User $user;
    public string $name = '';
    public string $email = '';
    public string $bio = '';
    public $avatar;
    public bool $notifications_enabled = true;

    // UI state
    public bool $isEditing = false;
    public bool $showSuccessMessage = false;

    protected $rules = [
        'name' => 'required|string|max:255',
        'email' => 'required|email|unique:users,email',
        'bio' => 'nullable|string|max:500',
        'avatar' => 'nullable|image|max:1024',
        'notifications_enabled' => 'boolean',
    ];

    protected $messages = [
        'name.required' => 'Name is required.',
        'email.unique' => 'This email is already taken.',
        'avatar.max' => 'Avatar must be less than 1MB.',
    ];

    public function mount(User $user)
    {
        $this->user = $user;
        $this->fill($user->only(['name', 'email', 'bio', 'notifications_enabled']));
    }

    public function render()
    {
        return view('livewire.user-profile');
    }

    public function startEditing()
    {
        $this->isEditing = true;
    }

    public function cancelEditing()
    {
        $this->isEditing = false;
        $this->fill($this->user->only(['name', 'email', 'bio', 'notifications_enabled']));
        $this->resetValidation();
    }

    public function save()
    {
        $this->validate();

        $data = [
            'name' => $this->name,
            'email' => $this->email,
            'bio' => $this->bio,
            'notifications_enabled' => $this->notifications_enabled,
        ];

        if ($this->avatar) {
            $data['avatar'] = $this->avatar->store('avatars', 'public');
        }

        $this->user->update($data);

        $this->isEditing = false;
        $this->showSuccessMessage = true;
        $this->avatar = null;

        // Hide success message after 3 seconds
        $this->dispatchBrowserEvent('success-message', [
            'message' => 'Profile updated successfully!'
        ]);
    }

    public function updated($propertyName)
    {
        $this->validateOnly($propertyName);
    }
}
```

### Real-time Component with Broadcasting
```php
<?php

namespace App\Http\Livewire;

use App\Events\MessageSent;
use App\Models\ChatMessage;
use Livewire\Component;

class ChatWidget extends Component
{
    public string $message = '';
    public int $chatId;
    public $messages;

    protected $listeners = [
        'echo:chat.{chatId},MessageSent' => 'messageReceived',
        'refreshMessages' => 'loadMessages',
    ];

    protected $rules = [
        'message' => 'required|string|max:500',
    ];

    public function mount($chatId)
    {
        $this->chatId = $chatId;
        $this->loadMessages();
    }

    public function render()
    {
        return view('livewire.chat-widget');
    }

    public function sendMessage()
    {
        $this->validate();

        $chatMessage = ChatMessage::create([
            'chat_id' => $this->chatId,
            'user_id' => auth()->id(),
            'message' => $this->message,
        ]);

        // Broadcast the message
        broadcast(new MessageSent($chatMessage))->toOthers();

        $this->message = '';
        $this->loadMessages();

        // Scroll to bottom
        $this->dispatchBrowserEvent('scroll-to-bottom');
    }

    public function messageReceived($payload)
    {
        $this->loadMessages();
        $this->dispatchBrowserEvent('new-message-sound');
    }

    private function loadMessages()
    {
        $this->messages = ChatMessage::where('chat_id', $this->chatId)
            ->with('user')
            ->latest()
            ->take(50)
            ->get()
            ->reverse();
    }
}
