                  {/* Pagination */}
                  {products.links.length > 3 && (
                    <div className="mt-6">
                      <Pagination links={products.links} />
                    </div>
                  )}
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    </AppLayout>
  )
}
