import React, { useState, useCallback, useMemo } from 'react'
import { router, Link } from '@inertiajs/react'
import { debounce } from 'lodash'
import AppLayout from '@/Layouts/AppLayout'
import ProductCard from '@/Components/Products/ProductCard'
import Pagination from '@/Components/Pagination'

export default function ProductsIndex({ products, categories, filters, sort }) {
  const [localFilters, setLocalFilters] = useState({
    search: filters.search || '',
    category: filters.category || '',
    price_range: filters.price_range || '',
  })

  const [sortBy, setSortBy] = useState(
    `${sort.sort_by || 'created_at'},${sort.sort_order || 'desc'}`
  )

  // Memoized values
  const hasActiveFilters = useMemo(() => {
    return Object.values(localFilters).some(value => value !== '')
  }, [localFilters])

  // Debounced search function
  const debouncedSearch = useCallback(
    debounce(() => {
      applyFilters()
    }, 300),
    [localFilters]
  )

  const applyFilters = useCallback(() => {
    const query = { ...localFilters }

    // Remove empty filters
    Object.keys(query).forEach(key => {
      if (query[key] === '') delete query[key]
    })

    router.get(route('products.index'), query, {
      preserveState: true,
      preserveScroll: true,
    })
  }, [localFilters])

  const applySort = useCallback(() => {
    const [sort_by, sort_order] = sortBy.split(',')

    router.get(route('products.index'), {
      ...localFilters,
      sort_by,
      sort_order,
    }, {
      preserveState: true,
      preserveScroll: true,
    })
  }, [localFilters, sortBy])

  const handleFilterChange = (key, value) => {
    setLocalFilters(prev => ({ ...prev, [key]: value }))
    if (key === 'search') {
      debouncedSearch()
    } else {
      applyFilters()
    }
  }

  const clearFilters = () => {
    setLocalFilters({
      search: '',
      category: '',
      price_range: '',
    })
    setSortBy('created_at,desc')

    router.get(route('products.index'), {}, {
      preserveState: true,
    })
  }

  const handleDelete = (productId) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      router.delete(route('products.destroy', productId), {
        preserveScroll: true,
        onSuccess: () => {
          // Handle success notification
        }
      })
    }
  }

  return (
    <AppLayout title="Products">
      <div className="py-12">
        <div className="max-w-7xl mx-auto sm:px-6 lg:px-8">
          {/* Header */}
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-2xl font-semibold text-gray-900">Products</h1>
            <Link
              href={route('products.create')}
              className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Add Product
            </Link>
          </div>

          {/* Filters */}
          <div className="bg-white overflow-hidden shadow-sm sm:rounded-lg mb-6">
            <div className="p-6 bg-white border-b border-gray-200">
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                {/* Search */}
                <div>
                  <label htmlFor="search" className="block text-sm font-medium text-gray-700">
                    Search
                  </label>
                  <input
                    id="search"
                    type="text"
                    value={localFilters.search}
                    onChange={(e) => handleFilterChange('search', e.target.value)}
                    placeholder="Search products..."
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                  />
                </div>

                {/* Category Filter */}
                <div>
                  <label htmlFor="category" className="block text-sm font-medium text-gray-700">
                    Category
                  </label>
                  <select
                    id="category"
                    value={localFilters.category}
                    onChange={(e) => handleFilterChange('category', e.target.value)}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                  >
                    <option value="">All Categories</option>
                    {categories.map((category) => (
                      <option key={category.id} value={category.id}>
                        {category.name}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Price Range */}
                <div>
                  <label htmlFor="priceRange" className="block text-sm font-medium text-gray-700">
                    Price Range
                  </label>
                  <select
                    id="priceRange"
                    value={localFilters.price_range}
                    onChange={(e) => handleFilterChange('price_range', e.target.value)}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                  >
                    <option value="">All Prices</option>
                    <option value="0,50">$0 - $50</option>
                    <option value="50,100">$50 - $100</option>
                    <option value="100,500">$100 - $500</option>
                    <option value="500,9999">$500+</option>
                  </select>
                </div>

                {/* Sort */}
                <div>
                  <label htmlFor="sort" className="block text-sm font-medium text-gray-700">
                    Sort By
                  </label>
                  <select
                    id="sort"
                    value={sortBy}
                    onChange={(e) => {
                      setSortBy(e.target.value)
                      applySort()
                    }}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                  >
                    <option value="created_at,desc">Newest First</option>
                    <option value="created_at,asc">Oldest First</option>
                    <option value="name,asc">Name A-Z</option>
                    <option value="name,desc">Name Z-A</option>
                    <option value="price,asc">Price Low to High</option>
                    <option value="price,desc">Price High to Low</option>
                  </select>
                </div>
              </div>

              {/* Clear Filters */}
              {hasActiveFilters && (
                <div className="mt-4">
                  <button
                    onClick={clearFilters}
                    className="text-sm text-gray-600 hover:text-gray-800"
                  >
                    Clear all filters
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Products Grid */}
          <div className="bg-white overflow-hidden shadow-sm sm:rounded-lg">
            <div className="p-6 bg-white border-b border-gray-200">
              {products.data.length === 0 ? (
                <div className="text-center py-8">
                  <p className="text-gray-500">No products found.</p>
