<?php

namespace Tests\Feature;

use App\Models\Product;
use App\Models\Category;
use App\Models\User;
use Illuminate\Foundation\Testing\RefreshDatabase;
use Inertia\Testing\AssertableInertia as Assert;
use Tests\TestCase;

class ProductControllerTest extends TestCase
{
    use RefreshDatabase;

    protected function setUp(): void
    {
        parent::setUp();
        $this->actingAs(User::factory()->create());
    }

    public function test_can_view_products_index()
    {
        Product::factory()->count(3)->create();

        $response = $this->get(route('products.index'));

        $response->assertOk()
            ->assertInertia(fn (Assert $page) => 
                $page->component('Products/Index')
                    ->has('products.data', 3)
                    ->has('categories')
                    ->has('filters')
            );
    }

    public function test_can_search_products()
    {
        Product::factory()->create(['name' => 'iPhone']);
        Product::factory()->create(['name' => 'Samsung']);

        $response = $this->get(route('products.index', ['search' => 'iPhone']));

        $response->assertInertia(fn (Assert $page) => 
            $page->component('Products/Index')
                ->has('products.data', 1)
                ->where('products.data.0.name', 'iPhone')
        );
    }

    public function test_can_filter_by_category()
    {
        $category = Category::factory()->create();
        Product::factory()->create(['category_id' => $category->id]);
        Product::factory()->create(); // Different category

        $response = $this->get(route('products.index', ['category' => $category->id]));

        $response->assertInertia(fn (Assert $page) => 
            $page->component('Products/Index')
                ->has('products.data', 1)
                ->where('products.data.0.category_id', $category->id)
        );
    }

    public function test_can_create_product()
    {
        $category = Category::factory()->create();
        $data = [
            'name' => 'Test Product',
            'description' => 'Test Description',
            'price' => 99.99,
            'category_id' => $category->id,
        ];

        $response = $this->post(route('products.store'), $data);

        $response->assertRedirect(route('products.index'))
            ->assertSessionHas('success');

        $this->assertDatabaseHas('products', $data);
    }

    public function test_can_update_product()
    {
        $product = Product::factory()->create();
        $data = [
            'name' => 'Updated Product',
            'description' => 'Updated Description',
            'price' => 199.99,
            'category_id' => $product->category_id,
        ];

        $response = $this->put(route('products.update', $product), $data);

        $response->assertRedirect(route('products.show', $product))
            ->assertSessionHas('success');

        $this->assertDatabaseHas('products', $data);
    }
}
