        );
    }

    public function test_eager_loading_prevents_n_plus_one()
    {
        // Create test data with relationships
        $users = User::factory()->count(10)->create();
        $posts = Post::factory()->count(50)->create();

        DB::enableQueryLog();

        // Test the optimized controller method
        $posts = Post::with(['user', 'comments'])->get();

        $queries = DB::getQueryLog();

        // Should be exactly 3 queries: posts, users, comments
        $this->assertLessThanOrEqual(3, count($queries),
            'Eager loading should prevent N+1 queries. Query count: ' . count($queries)
        );
    }

    public function test_pagination_performance()
    {
        // Create large dataset
        Post::factory()->count(1000)->create();

        DB::enableQueryLog();

        $response = $this->get('/posts?page=1');

        $response->assertOk();

        $queries = DB::getQueryLog();

        // Pagination should not load all records
        $this->assertLessThanOrEqual(5, count($queries));

        // Check that LIMIT is being used
        $sqlQueries = collect($queries)->pluck('sql');
        $this->assertTrue(
            $sqlQueries->contains(function ($sql) {
                return str_contains(strtolower($sql), 'limit');
            }),
            'Pagination queries should include LIMIT clause'
        );
    }
}
