class PostController extends Controller
{
    public function index(Request $request)
    {
        $query = Post::query();

        // Conditionally eager load based on request
        if ($request->has('include_user')) {
            $query->with('user');
        }

        if ($request->has('include_comments')) {
            $query->with(['comments' => function ($q) {
                $q->with('user')->latest();
            }]);
        }

        // For API: eager load based on requested includes
        if ($request->has('include')) {
            $includes = explode(',', $request->include);
            $validIncludes = ['user', 'comments', 'tags', 'category'];
            $includes = array_intersect($includes, $validIncludes);

            if (!empty($includes)) {
                $query->with($includes);
            }
        }

        $posts = $query->paginate(15);

        return PostResource::collection($posts);
    }
}
