            'id' => $this->id,
            'title' => $this->title,
            'slug' => $this->slug,
            'excerpt' => $this->excerpt,
            'content' => $this->when($request->routeIs('posts.show'), $this->content),
            'status' => $this->status,
            'created_at' => $this->created_at->toISOString(),
            'updated_at' => $this->updated_at->toISOString(),

            // Conditional relationships (only load when already eager loaded)
            'user' => new UserResource($this->whenLoaded('user')),
            'category' => new CategoryResource($this->whenLoaded('category')),
            'comments' => CommentResource::collection($this->whenLoaded('comments')),
            'tags' => TagResource::collection($this->whenLoaded('tags')),

            // Computed attributes (only when needed)
            'comment_count' => $this->when(
                $this->relationLoaded('comments') || isset($this->comments_count),
                $this->comments_count ?? $this->comments->count()
            ),
            'reading_time' => $this->when(
                $request->has('include_reading_time'),
                $this->getReadingTimeAttribute()
            ),

            // Conditional includes based on request
            'full_content' => $this->when(
                $request->has('full_content') && $this->userCan('view', $this->resource),
                $this->content
            ),
        ];
    }

    // Optimize collection queries
    public static function collection($resource)
    {
        return parent::collection($resource)->additional([
            'meta' => [
                'total' => $resource->total(),
                'per_page' => $resource->perPage(),
                'optimizations' => [
                    'eager_loaded' => ['user', 'category'],
                    'query_count' => DB::getQueryLog() ? count(DB::getQueryLog()) : null,
                ]
            ]
        ]);
    }
}
