```php
<?php

namespace App\Traits;

use Illuminate\Support\Facades\Cache;
use Illuminate\Support\Facades\DB;

trait QueryCacheable
{
    public function scopeCached($query, $key = null, $ttl = 3600)
    {
        $key = $key ?: $this->generateQueryCacheKey($query);

        return Cache::tags([
            'queries',
            strtolower(class_basename($this))
        ])->remember($key, $ttl, function () use ($query) {
            return $query->get();
        });
    }

    public function scopeRememberForever($query, $key)
    {
        return Cache::tags([
            'queries',
            'forever',
            strtolower(class_basename($this))
        ])->rememberForever($key, function () use ($query) {
            return $query->get();
        });
    }

    protected function generateQueryCacheKey($query)
    {
        return md5($query->toSql() . serialize($query->getBindings()));
    }
}

// Usage in models
