### 8. Cache Performance Monitoring

```php
<?php

namespace App\Http\Middleware;

use Closure;
use Illuminate\Support\Facades\Cache;
use Illuminate\Support\Facades\Log;

class CacheMonitoring
{
    protected $hitCount = 0;
    protected $missCount = 0;
    protected $startTime;

    public function handle($request, Closure $next)
    {
        $this->startTime = microtime(true);

        // Monitor cache hits/misses
        Cache::listen(function ($event) {
            if ($event instanceof \Illuminate\Cache\Events\CacheHit) {
                $this->hitCount++;
            } elseif ($event instanceof \Illuminate\Cache\Events\CacheMissed) {
                $this->missCount++;
            }
        });

        $response = $next($request);

        $this->logCacheStats($request);
        $this->addCacheHeaders($response);

        return $response;
    }

    protected function logCacheStats($request)
    {
        $duration = microtime(true) - $this->startTime;
        $totalOperations = $this->hitCount + $this->missCount;
        $hitRate = $totalOperations > 0 ? ($this->hitCount / $totalOperations) * 100 : 0;

        Log::info('Cache performance', [
            'url' => $request->url(),
            'method' => $request->method(),
            'cache_hits' => $this->hitCount,
            'cache_misses' => $this->missCount,
            'hit_rate' => round($hitRate, 2) . '%',
            'duration' => round($duration * 1000, 2) . 'ms',
        ]);
    }

    protected function addCacheHeaders($response)
    {
        if (app()->environment('local')) {
            $response->headers->set('X-Cache-Hits', $this->hitCount);
            $response->headers->set('X-Cache-Misses', $this->missCount);
            $response->headers->set('X-Cache-Hit-Rate', 
                $this->hitCount + $this->missCount > 0 
                    ? round(($this->hitCount / ($this->hitCount + $this->missCount)) * 100, 2) . '%'
                    : '0%'
            );
        }
    }
}
