                $this->warmPostsCache();
                $this->warmConfigCache();
                $this->warmApiCache();
                break;
        }

        $this->info('Cache warming completed successfully!');
    }

    protected function warmPostsCache()
    {
        $this->info('Warming posts cache...');

        $repository = app(PostRepository::class);

        // Warm popular posts
        $repository->getPopular(10, '7days');
