### 3. Frontend Asset Performance

```php
<?php

namespace App\Services;

class AssetPerformanceAnalyzer
{
    public function analyzeAssets()
    {
        $analysis = [
            'css_analysis' => $this->analyzeCSSFiles(),
            'js_analysis' => $this->analyzeJSFiles(),
            'image_analysis' => $this->analyzeImages(),
            'font_analysis' => $this->analyzeFonts(),
            'bundle_analysis' => $this->analyzeBundles(),
        ];

        return $analysis;
    }

    protected function analyzeCSSFiles()
    {
        $publicPath = public_path();
        $cssFiles = glob($publicPath . '/css/*.css');
        $analysis = [];

        foreach ($cssFiles as $file) {
            $size = filesize($file);
            $content = file_get_contents($file);

            $analysis[basename($file)] = [
                'size' => $this->formatBytes($size),
                'size_bytes' => $size,
                'gzipped_size' => $this->formatBytes(strlen(gzencode($content))),
                'unused_css' => $this->detectUnusedCSS($content),
                'optimization_potential' => $this->calculateCSSOptimization($content, $size),
                'recommendations' => $this->generateCSSRecommendations($content, $size),
            ];
        }

        return $analysis;
    }

    protected function analyzeJSFiles()
    {
        $publicPath = public_path();
        $jsFiles = glob($publicPath . '/js/*.js');
        $analysis = [];

        foreach ($jsFiles as $file) {
            $size = filesize($file);
            $content = file_get_contents($file);

            $analysis[basename($file)] = [
                'size' => $this->formatBytes($size),
                'size_bytes' => $size,
                'gzipped_size' => $this->formatBytes(strlen(gzencode($content))),
                'minified' => $this->isMinified($content),
                'modules_detected' => $this->detectJSModules($content),
                'recommendations' => $this->generateJSRecommendations($content, $size),
            ];
        }

        return $analysis;
    }

    protected function analyzeImages()
    {
        $imagePaths = [
            'public/images',
            'storage/app/public/images',
            'resources/images'
        ];

        $analysis = [];
        $totalSize = 0;
        $imageCount = 0;

        foreach ($imagePaths as $path) {
            $fullPath = base_path($path);
            if (!is_dir($fullPath)) continue;

            $images = glob($fullPath . '/*.{jpg,jpeg,png,gif,webp,svg}', GLOB_BRACE);

            foreach ($images as $image) {
                $size = filesize($image);
                $totalSize += $size;
                $imageCount++;

                $imageInfo = getimagesize($image);
                $analysis[basename($image)] = [
                    'size' => $this->formatBytes($size),
                    'dimensions' => $imageInfo ? $imageInfo[0] . 'x' . $imageInfo[1] : 'unknown',
                    'type' => $imageInfo ? $imageInfo['mime'] : 'unknown',
                    'optimization_potential' => $this->calculateImageOptimization($image, $size),
                ];
            }
        }

        $analysis['summary'] = [
            'total_images' => $imageCount,
            'total_size' => $this->formatBytes($totalSize),
            'average_size' => $imageCount > 0 ? $this->formatBytes($totalSize / $imageCount) : '0 B',
            'recommendations' => $this->generateImageRecommendations($totalSize, $imageCount),
        ];

        return $analysis;
    }

    protected function generateOptimizationReport($analysis)
    {
        $report = [
            'overall_score' => $this->calculateOverallScore($analysis),
            'critical_issues' => [],
            'recommendations' => [],
            'quick_wins' => [],
            'performance_budget' => $this->checkPerformanceBudget($analysis),
        ];

        // Identify critical issues
        foreach ($analysis['css_analysis'] as $file => $data) {
            if ($data['size_bytes'] > 100 * 1024) { // > 100KB
                $report['critical_issues'][] = [
                    'type' => 'Large CSS File',
                    'file' => $file,
                    'issue' => "CSS file is {$data['size']} which may block rendering",
                    'priority' => 'high',
                ];
            }
        }

        // Quick wins
        if ($analysis['bundle_analysis']['compression_enabled'] === false) {
            $report['quick_wins'][] = [
                'action' => 'Enable GZIP compression',
                'impact' => 'Reduce asset sizes by 60-80%',
                'effort' => 'low',
            ];
        }

        return $report;
    }
}
