### 4. API Performance Testing

```php
<?php

namespace App\Services;

use Illuminate\Support\Facades\Http;
use Illuminate\Support\Facades\Route;

class ApiPerformanceTester
{
    protected $baseUrl;
    protected $results = [];

    public function __construct()
    {
        $this->baseUrl = config('app.url');
    }

    public function testAllApiRoutes()
    {
        $apiRoutes = collect(Route::getRoutes())
            ->filter(fn($route) => str_starts_with($route->uri(), 'api/'))
            ->filter(fn($route) => in_array('GET', $route->methods()));

        foreach ($apiRoutes as $route) {
            $this->testRoute($route);
        }

        return $this->generateApiReport();
    }

    protected function testRoute($route)
    {
        $uri = $route->uri();
        $url = $this->baseUrl . '/' . $uri;

        // Replace route parameters with test values
        $url = $this->replaceRouteParameters($url);

        $startTime = microtime(true);
        $startMemory = memory_get_usage(true);

        try {
            $response = Http::timeout(30)->get($url);
            $endTime = microtime(true);
            $endMemory = memory_get_usage(true);

            $this->results[$uri] = [
                'status' => $response->status(),
                'response_time' => round(($endTime - $startTime) * 1000, 2), // ms
                'memory_used' => $endMemory - $startMemory,
                'response_size' => strlen($response->body()),
                'headers' => $response->headers(),
                'performance_grade' => $this->gradePerformance($endTime - $startTime),
                'recommendations' => $this->generateRouteRecommendations($response, $endTime - $startTime),
            ];

        } catch (\Exception $e) {
            $this->results[$uri] = [
                'error' => $e->getMessage(),
                'status' => 'error',
            ];
        }
    }

    protected function gradePerformance($responseTime)
    {
        if ($responseTime < 0.1) return 'A'; // < 100ms
        if ($responseTime < 0.3) return 'B'; // < 300ms
        if ($responseTime < 0.5) return 'C'; // < 500ms
        if ($responseTime < 1.0) return 'D'; // < 1s
        return 'F'; // >= 1s
    }

    protected function generateRouteRecommendations($response, $responseTime)
    {
        $recommendations = [];

        // Check for caching headers
        if (!$response->header('Cache-Control') && !$response->header('ETag')) {
            $recommendations[] = [
                'type' => 'caching',
                'message' => 'Add caching headers to improve performance',
                'priority' => 'medium',
            ];
        }

        // Check response time
        if ($responseTime > 0.5) {
            $recommendations[] = [
                'type' => 'performance',
                'message' => 'Response time is slow (> 500ms), consider optimization',
                'priority' => 'high',
            ];
        }

        // Check response size
        $responseSize = strlen($response->body());
        if ($responseSize > 1024 * 1024) { // > 1MB
            $recommendations[] = [
                'type' => 'payload_size',
                'message' => 'Response payload is large, consider pagination or compression',
                'priority' => 'medium',
            ];
        }

        return $recommendations;
    }

    public function generateApiReport()
    {
        $totalRoutes = count($this->results);
        $successfulRoutes = count(array_filter($this->results, fn($r) => isset($r['status']) && $r['status'] === 200));
        $averageResponseTime = collect($this->results)
            ->where('status', 200)
            ->avg('response_time');

        $performanceGrades = collect($this->results)
            ->where('status', 200)
            ->groupBy('performance_grade')
            ->map->count();

        return [
            'summary' => [
                'total_routes' => $totalRoutes,
                'successful_routes' => $successfulRoutes,
                'success_rate' => round(($successfulRoutes / $totalRoutes) * 100, 2) . '%',
                'average_response_time' => round($averageResponseTime, 2) . 'ms',
                'performance_distribution' => $performanceGrades->toArray(),
            ],
            'slow_endpoints' => collect($this->results)
                ->where('status', 200)
                ->where('response_time', '>', 500)
                ->sortByDesc('response_time')
                ->take(10)
                ->toArray(),
            'recommendations' => $this->generateOverallApiRecommendations(),
            'detailed_results' => $this->results,
        ];
    }
}
