<?php

namespace App\Services;

class ProductionReadinessChecker
{
    public function checkReadiness()
    {
        $checks = [
            'environment' => $this->checkEnvironmentConfiguration(),
            'database' => $this->checkDatabaseOptimization(),
            'caching' => $this->checkCachingConfiguration(),
            'security' => $this->checkSecurityConfiguration(),
            'performance' => $this->checkPerformanceConfiguration(),
            'monitoring' => $this->checkMonitoringSetup(),
            'assets' => $this->checkAssetOptimization(),
        ];

        return [
            'overall_score' => $this->calculateOverallScore($checks),
            'readiness_level' => $this->determineReadinessLevel($checks),
            'checks' => $checks,
            'critical_issues' => $this->identifyCriticalIssues($checks),
            'recommendations' => $this->generateProductionRecommendations($checks),
        ];
    }

    protected function checkEnvironmentConfiguration()
    {
        $checks = [];

        // APP_ENV should be production
        $checks['app_env'] = [
            'status' => config('app.env') === 'production' ? 'pass' : 'fail',
            'message' => config('app.env') === 'production' 
                ? 'APP_ENV is correctly set to production'
                : 'APP_ENV should be set to "production"',
        ];

        // APP_DEBUG should be false
        $checks['debug_mode'] = [
            'status' => !config('app.debug') ? 'pass' : 'fail',
            'message' => !config('app.debug')
                ? 'Debug mode is correctly disabled'
                : 'APP_DEBUG should be false in production',
        ];

        // Check for .env file
        $checks['env_file'] = [
            'status' => file_exists(base_path('.env')) ? 'pass' : 'fail',
            'message' => file_exists(base_path('.env'))
                ? '.env file exists'
                : '.env file is missing',
        ];

        return $checks;
    }

    protected function checkDatabaseOptimization()
    {
        $checks = [];

        // Check for connection pooling
        $checks['connection_config'] = [
            'status' => $this->isDatabaseOptimized() ? 'pass' : 'warning',
            'message' => 'Database connection configuration checked',
        ];

        // Check for query caching
        $checks['query_cache'] = [
            'status' => config('cache.default') !== 'array' ? 'pass' : 'fail',
            'message' => config('cache.default') !== 'array'
                ? 'Query caching is configured'
                : 'Query caching is not properly configured',
        ];

        return $checks;
    }

    protected function checkCachingConfiguration()
    {
        $checks = [];

        // Redis/Memcached for production
        $cacheDriver = config('cache.default');
        $checks['cache_driver'] = [
            'status' => in_array($cacheDriver, ['redis', 'memcached']) ? 'pass' : 'warning',
            'message' => in_array($cacheDriver, ['redis', 'memcached'])
                ? "Using {$cacheDriver} for caching"
                : "Consider using Redis or Memcached instead of {$cacheDriver}",
        ];

        // Session driver
        $sessionDriver = config('session.driver');
        $checks['session_driver'] = [
            'status' => in_array($sessionDriver, ['redis', 'database']) ? 'pass' : 'warning',
            'message' => in_array($sessionDriver, ['redis', 'database'])
                ? "Using {$sessionDriver} for sessions"
                : "Consider using Redis or database for session storage",
        ];

        return $checks;
    }
}
