# Laravel Security Audit

**Command**: `/laravel:security-audit [options]`

**Description**: Conducts comprehensive security analysis of your Laravel application, identifying vulnerabilities, implementing security best practices, and generating detailed security reports.

## Usage

```bash
/laravel:security-audit --full-scan
/laravel:security-audit --check-dependencies --scan-code
/laravel:security-audit --auth-analysis --permission-review
/laravel:security-audit --environment-check --generate-report
```

## Options

- `--full-scan` - Comprehensive security analysis of entire application
- `--check-dependencies` - Analyze composer dependencies for known vulnerabilities
- `--scan-code` - Static code analysis for security issues
- `--auth-analysis` - Deep analysis of authentication implementation
- `--permission-review` - Review authorization and access control
- `--environment-check` - Validate environment security configuration
- `--csrf-analysis` - Analyze CSRF protection implementation
- `--sql-injection-check` - Check for SQL injection vulnerabilities
- `--xss-protection` - Analyze XSS protection measures
- `--generate-report` - Create detailed security report with recommendations

## Security Analysis Categories

### 1. Authentication & Authorization Security

```php
<?php

namespace App\Security\Analyzers;

class AuthSecurityAnalyzer
{
    public function analyzeAuthentication()
    {
        $findings = [];

        // Check authentication configuration
        $findings['auth_config'] = $this->checkAuthConfig();

        // Analyze password policies
        $findings['password_policies'] = $this->analyzePasswordPolicies();

        // Check session security
        $findings['session_security'] = $this->checkSessionSecurity();

        // Analyze multi-factor authentication
        $findings['mfa_implementation'] = $this->checkMFAImplementation();

        // Check rate limiting
        $findings['rate_limiting'] = $this->analyzeRateLimiting();

        return $findings;
    }

    protected function checkAuthConfig()
    {
        $issues = [];

        // Check for secure password hashing
        $hashDriver = config('hashing.driver');
        if ($hashDriver !== 'bcrypt' && $hashDriver !== 'argon2i' && $hashDriver !== 'argon2id') {
            $issues[] = [
                'severity' => 'high',
                'issue' => 'Weak password hashing algorithm',
                'recommendation' => 'Use bcrypt, argon2i, or argon2id for password hashing',
                'config' => 'config/hashing.php',
            ];
        }

        // Check session lifetime
        $sessionLifetime = config('session.lifetime');
        if ($sessionLifetime > 120) { // More than 2 hours
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'Long session lifetime',
                'recommendation' => 'Consider reducing session lifetime for better security',
                'current_value' => $sessionLifetime,
            ];
        }

        // Check for secure session configuration
        if (!config('session.secure') && app()->environment('production')) {
            $issues[] = [
                'severity' => 'high',
                'issue' => 'Sessions not marked as secure in production',
                'recommendation' => 'Set SESSION_SECURE_COOKIE=true in production',
            ];
        }

        if (!config('session.http_only')) {
            $issues[] = [
                'severity' => 'high',
                'issue' => 'Sessions accessible via JavaScript',
                'recommendation' => 'Set SESSION_HTTP_ONLY=true to prevent XSS attacks',
            ];
        }

        return $issues;
    }

    protected function analyzePasswordPolicies()
    {
        $issues = [];

        // Check if password validation rules exist
        $userModel = app(config('auth.providers.users.model'));
        $validationRules = $this->extractPasswordValidationRules();

        if (!str_contains($validationRules, 'min:8')) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'Weak minimum password length',
                'recommendation' => 'Require passwords to be at least 8 characters',
            ];
        }

        if (!$this->hasPasswordComplexityRules($validationRules)) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'No password complexity requirements',
                'recommendation' => 'Implement password complexity rules (uppercase, lowercase, numbers, symbols)',
            ];
        }

        // Check for password history
        if (!$this->hasPasswordHistoryImplemented()) {
            $issues[] = [
                'severity' => 'low',
                'issue' => 'No password history implementation',
                'recommendation' => 'Consider implementing password history to prevent reuse',
            ];
        }

        return $issues;
    }

    protected function checkSessionSecurity()
    {
        $issues = [];

        // Check session driver security
        $sessionDriver = config('session.driver');
        if ($sessionDriver === 'file' && app()->environment('production')) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'Using file session driver in production',
                'recommendation' => 'Use database or Redis session driver for better security and scalability',
            ];
        }

        // Check session regeneration
        if (!$this->hasSessionRegenerationImplemented()) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'Session ID not regenerated on login',
                'recommendation' => 'Regenerate session ID on authentication state changes',
            ];
        }

        return $issues;
    }

    protected function analyzeRateLimiting()
    {
        $issues = [];

        // Check for login rate limiting
        if (!$this->hasLoginRateLimiting()) {
            $issues[] = [
                'severity' => 'high',
                'issue' => 'No rate limiting on login attempts',
                'recommendation' => 'Implement rate limiting to prevent brute force attacks',
                'example' => 'Use Laravel Fortify or custom middleware for rate limiting',
            ];
        }

        // Check for API rate limiting
        if (!$this->hasApiRateLimiting()) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'No rate limiting on API endpoints',
                'recommendation' => 'Implement API rate limiting to prevent abuse',
            ];
        }

        return $issues;
    }
}
```

### 2. Input Validation & SQL Injection Prevention

```php
<?php

namespace App\Security\Analyzers;

class InputSecurityAnalyzer
{
    public function analyzeSQLInjectionRisks()
    {
        $findings = [];

        // Scan for raw SQL queries
        $findings['raw_queries'] = $this->scanForRawQueries();

        // Check query parameter binding
        $findings['parameter_binding'] = $this->checkParameterBinding();

        // Analyze user input handling
        $findings['input_validation'] = $this->analyzeInputValidation();

        // Check for dynamic query construction
        $findings['dynamic_queries'] = $this->checkDynamicQueries();

        return $findings;
    }

    protected function scanForRawQueries()
    {
        $issues = [];
        $controllerPath = app_path('Http/Controllers');

        $files = glob($controllerPath . '/*.php', GLOB_BRACE);

        foreach ($files as $file) {
            $content = file_get_contents($file);

            // Look for potentially dangerous raw SQL patterns
            $patterns = [
                '/DB::raw\s*\(\s*["\'].*\$.*["\']/' => 'Raw SQL with variable interpolation',
                '/DB::statement\s*\(\s*["\'].*\$.*["\']/' => 'Raw statement with variable interpolation',
                '/->whereRaw\s*\(\s*["\'].*\$.*["\']/' => 'Raw where clause with variable interpolation',
                '/->selectRaw\s*\(\s*["\'].*\$.*["\']/' => 'Raw select with variable interpolation',
            ];

            foreach ($patterns as $pattern => $description) {
                if (preg_match($pattern, $content, $matches)) {
                    $issues[] = [
                        'severity' => 'high',
                        'file' => basename($file),
                        'issue' => $description,
                        'match' => trim($matches[0]),
                        'recommendation' => 'Use parameter binding instead of string interpolation',
                    ];
                }
            }
        }

        return $issues;
    }

    protected function analyzeInputValidation()
    {
        $issues = [];

        // Check if form requests are being used
        $requestsPath = app_path('Http/Requests');
        if (!is_dir($requestsPath) || count(glob($requestsPath . '/*.php')) === 0) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'No form request classes found',
                'recommendation' => 'Use form request classes for input validation',
            ];
        }

        // Analyze validation rules in controllers
        $validationPatterns = $this->scanControllerValidation();

        foreach ($validationPatterns as $pattern) {
            if ($this->hasWeakValidation($pattern)) {
                $issues[] = [
                    'severity' => 'medium',
                    'file' => $pattern['file'],
                    'issue' => 'Weak or missing validation rules',
                    'recommendation' => 'Implement comprehensive validation rules',
                ];
            }
        }

        return $issues;
    }

    protected function checkDynamicQueries()
    {
        $issues = [];
        $modelPath = app_path('Models');

        $files = glob($modelPath . '/*.php');

        foreach ($files as $file) {
            $content = file_get_contents($file);

            // Look for dynamic query construction
            if (preg_match('/->where\s*\(\s*\$.*,/', $content)) {
                $issues[] = [
                    'severity' => 'medium',
                    'file' => basename($file),
                    'issue' => 'Dynamic where clause construction detected',
                    'recommendation' => 'Validate column names against whitelist before using in queries',
                ];
            }

            // Check for order by with user input
            if (preg_match('/->orderBy\s*\(\s*\$.*[,)]/', $content)) {
                $issues[] = [
                    'severity' => 'medium',
                    'file' => basename($file),
                    'issue' => 'Dynamic order by clause detected',
                    'recommendation' => 'Validate sort columns against allowed list',
                ];
            }
        }

        return $issues;
    }
}
```

### 3. Cross-Site Scripting (XSS) Protection

```php
<?php

namespace App\Security\Analyzers;

class XSSProtectionAnalyzer
{
    public function analyzeXSSProtection()
    {
        $findings = [];

        // Check CSRF protection
        $findings['csrf_protection'] = $this->checkCSRFProtection();

        // Analyze output escaping
        $findings['output_escaping'] = $this->analyzeOutputEscaping();

        // Check Content Security Policy
        $findings['csp_headers'] = $this->checkCSPHeaders();

        // Analyze user-generated content handling
        $findings['user_content'] = $this->analyzeUserContentHandling();

        return $findings;
    }

    protected function checkCSRFProtection()
    {
        $issues = [];

        // Check if CSRF middleware is enabled globally
        $kernelFile = app_path('Http/Kernel.php');
        $kernelContent = file_get_contents($kernelFile);

        if (!str_contains($kernelContent, 'VerifyCsrfToken')) {
            $issues[] = [
                'severity' => 'high',
                'issue' => 'CSRF protection middleware not found in Kernel',
                'recommendation' => 'Add VerifyCsrfToken middleware to web middleware group',
            ];
        }

        // Check for forms without CSRF tokens
        $bladeFiles = glob(resource_path('views') . '/**/*.blade.php', GLOB_BRACE);

        foreach ($bladeFiles as $file) {
            $content = file_get_contents($file);

            // Look for forms without CSRF tokens
            if (preg_match('/<form[^>]*method=["\']post["\'][^>]*>/i', $content)) {
                if (!str_contains($content, '@csrf') && !str_contains($content, 'csrf_token()')) {
                    $issues[] = [
                        'severity' => 'high',
                        'file' => str_replace(resource_path('views/'), '', $file),
                        'issue' => 'Form with POST method missing CSRF token',
                        'recommendation' => 'Add @csrf directive to all POST forms',
                    ];
                }
            }
        }

        return $issues;
    }

    protected function analyzeOutputEscaping()
    {
        $issues = [];
        $bladeFiles = glob(resource_path('views') . '/**/*.blade.php', GLOB_BRACE);

        foreach ($bladeFiles as $file) {
            $content = file_get_contents($file);

            // Look for raw output (potential XSS)
            preg_match_all('/\{!!\s*\$[^}]+\s*!!\}/', $content, $matches);

            foreach ($matches[0] as $match) {
                $issues[] = [
                    'severity' => 'medium',
                    'file' => str_replace(resource_path('views/'), '', $file),
                    'issue' => 'Raw output detected: ' . $match,
                    'recommendation' => 'Use {{ }} for automatic escaping unless raw HTML is specifically needed',
                ];
            }

            // Check for dangerous HTML attributes
            if (preg_match('/\{\{\s*\$[^}]*\s*\}\}.*(?:onclick|onload|onerror)/i', $content)) {
                $issues[] = [
                    'severity' => 'high',
                    'file' => str_replace(resource_path('views/'), '', $file),
                    'issue' => 'Variable output in dangerous HTML attributes',
                    'recommendation' => 'Sanitize data before outputting in HTML attributes',
                ];
            }
        }

        return $issues;
    }

    protected function checkCSPHeaders()
    {
        $issues = [];

        // Check if CSP middleware exists
        $middlewarePath = app_path('Http/Middleware');
        $cspFiles = glob($middlewarePath . '/*CSP*.php');

        if (empty($cspFiles)) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'No Content Security Policy implementation found',
                'recommendation' => 'Implement CSP headers to prevent XSS attacks',
                'example' => 'Use spatie/laravel-csp package or custom middleware',
            ];
        }

        // Check for inline scripts in blade files
        $bladeFiles = glob(resource_path('views') . '/**/*.blade.php', GLOB_BRACE);

        foreach ($bladeFiles as $file) {
            $content = file_get_contents($file);

            if (preg_match('/<script[^>]*>[^<]*</i', $content)) {
                $issues[] = [
                    'severity' => 'medium',
                    'file' => str_replace(resource_path('views/'), '', $file),
                    'issue' => 'Inline scripts detected',
                    'recommendation' => 'Move scripts to external files and use CSP nonces',
                ];
            }
        }

        return $issues;
    }
}
```

### 4. Environment & Configuration Security

```php
<?php

namespace App\Security\Analyzers;

class EnvironmentSecurityAnalyzer
{
    public function analyzeEnvironmentSecurity()
    {
        $findings = [];

        // Check environment configuration
        $findings['env_config'] = $this->checkEnvironmentConfig();

        // Analyze file permissions
        $findings['file_permissions'] = $this->checkFilePermissions();

        // Check for sensitive data exposure
        $findings['sensitive_data'] = $this->checkSensitiveDataExposure();

        // Analyze error handling
        $findings['error_handling'] = $this->analyzeErrorHandling();

        // Check HTTPS configuration
        $findings['https_config'] = $this->checkHTTPSConfiguration();

        return $findings;
    }

    protected function checkEnvironmentConfig()
    {
        $issues = [];

        // Check if .env file exists and is not in version control
        if (!file_exists(base_path('.env'))) {
            $issues[] = [
                'severity' => 'high',
                'issue' => '.env file does not exist',
                'recommendation' => 'Create .env file for environment configuration',
            ];
        }

        // Check if .env.example exists
        if (!file_exists(base_path('.env.example'))) {
            $issues[] = [
                'severity' => 'low',
                'issue' => '.env.example file does not exist',
                'recommendation' => 'Create .env.example as template for environment variables',
            ];
        }

        // Check critical environment variables
        $criticalVars = ['APP_KEY', 'DB_PASSWORD', 'MAIL_PASSWORD'];
        foreach ($criticalVars as $var) {
            if (empty(env($var))) {
                $issues[] = [
                    'severity' => $var === 'APP_KEY' ? 'high' : 'medium',
                    'issue' => "{$var} is not set or empty",
                    'recommendation' => "Set {$var} in .env file",
                ];
            }
        }

        // Check APP_KEY strength
        $appKey = config('app.key');
        if ($appKey && strlen(base64_decode(substr($appKey, 7))) < 32) {
            $issues[] = [
                'severity' => 'high',
                'issue' => 'APP_KEY appears to be weak',
                'recommendation' => 'Generate new APP_KEY using php artisan key:generate',
            ];
        }

        // Check for debug mode in production
        if (config('app.debug') && app()->environment('production')) {
            $issues[] = [
                'severity' => 'high',
                'issue' => 'Debug mode enabled in production',
                'recommendation' => 'Set APP_DEBUG=false in production environment',
            ];
        }

        return $issues;
    }

    protected function checkFilePermissions()
    {
        $issues = [];

        // Check critical directory permissions
        $criticalPaths = [
            storage_path() => '755',
            storage_path('logs') => '755',
            bootstrap_path('cache') => '755',
            base_path('.env') => '600',
        ];

        foreach ($criticalPaths as $path => $expectedPerm) {
            if (file_exists($path)) {
                $currentPerm = substr(sprintf('%o', fileperms($path)), -3);

                if ($currentPerm !== $expectedPerm) {
                    $issues[] = [
                        'severity' => 'medium',
                        'issue' => "Incorrect permissions on {$path}",
                        'current' => $currentPerm,
                        'expected' => $expectedPerm,
                        'recommendation' => "chmod {$expectedPerm} {$path}",
                    ];
                }
            }
        }

        return $issues;
    }

    protected function checkSensitiveDataExposure()
    {
        $issues = [];

        // Check for sensitive files in public directory
        $publicPath = public_path();
        $sensitiveFiles = ['.env', '.git', 'composer.json', 'composer.lock'];

        foreach ($sensitiveFiles as $file) {
            if (file_exists($publicPath . '/' . $file)) {
                $issues[] = [
                    'severity' => 'high',
                    'issue' => "Sensitive file {$file} found in public directory",
                    'recommendation' => "Remove {$file} from public directory",
                ];
            }
        }

        // Check for exposed configuration files
        $configFiles = glob(public_path() . '/*.{ini,conf,config}', GLOB_BRACE);
        foreach ($configFiles as $file) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'Configuration file exposed in public directory: ' . basename($file),
                'recommendation' => 'Move configuration files outside web root',
            ];
        }

        return $issues;
    }

    protected function analyzeErrorHandling()
    {
        $issues = [];

        // Check error reporting in production
        if (app()->environment('production')) {
            if (config('app.debug')) {
                $issues[] = [
                    'severity' => 'high',
                    'issue' => 'Detailed error reporting enabled in production',
                    'recommendation' => 'Disable debug mode in production to prevent information disclosure',
                ];
            }

            // Check if custom error pages exist
            $errorViews = ['401', '403', '404', '419', '429', '500', '503'];
            foreach ($errorViews as $error) {
                if (!view()->exists("errors.{$error}")) {
                    $issues[] = [
                        'severity' => 'low',
                        'issue' => "Custom error page for {$error} does not exist",
                        'recommendation' => "Create resources/views/errors/{$error}.blade.php",
                    ];
                }
            }
        }

        return $issues;
    }
}
```

### 5. Dependency Security Analysis

```php
<?php

namespace App\Security\Analyzers;

use Illuminate\Support\Facades\Http;

class DependencySecurityAnalyzer
{
    public function analyzeDependencies()
    {
        $findings = [];

        // Check composer dependencies
        $findings['composer_vulnerabilities'] = $this->checkComposerVulnerabilities();

        // Analyze package versions
        $findings['outdated_packages'] = $this->checkOutdatedPackages();

        // Check for abandoned packages
        $findings['abandoned_packages'] = $this->checkAbandonedPackages();

        // Analyze npm dependencies (if exists)
        if (file_exists(base_path('package.json'))) {
            $findings['npm_vulnerabilities'] = $this->checkNpmVulnerabilities();
        }

        return $findings;
    }

    protected function checkComposerVulnerabilities()
    {
        $issues = [];

        if (!file_exists(base_path('composer.lock'))) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'composer.lock file not found',
                'recommendation' => 'Run composer install to generate composer.lock',
            ];
            return $issues;
        }

        $composerLock = json_decode(file_get_contents(base_path('composer.lock')), true);
        $packages = array_merge(
            $composerLock['packages'] ?? [],
            $composerLock['packages-dev'] ?? []
        );

        foreach ($packages as $package) {
            $vulnerabilities = $this->checkPackageVulnerabilities($package);
            if (!empty($vulnerabilities)) {
                $issues[] = [
                    'severity' => $this->calculateVulnerabilitySeverity($vulnerabilities),
                    'package' => $package['name'],
                    'version' => $package['version'],
                    'vulnerabilities' => $vulnerabilities,
                    'recommendation' => 'Update package to latest secure version',
                ];
            }
        }

        return $issues;
    }

    protected function checkPackageVulnerabilities($package)
    {
        // This would integrate with security databases like:
        // - Packagist security advisories
        // - CVE databases
        // - GitHub security advisories

        $knownVulnerabilities = [
            'laravel/framework' => [
                '8.0.0' => ['CVE-2021-XXXX' => 'SQL injection vulnerability'],
                '9.0.0' => ['CVE-2022-XXXX' => 'XSS vulnerability'],
            ],
            'symfony/symfony' => [
                '4.4.0' => ['CVE-2020-XXXX' => 'Remote code execution'],
            ],
        ];

        $packageName = $package['name'];
        $packageVersion = $package['version'];

        if (isset($knownVulnerabilities[$packageName])) {
            foreach ($knownVulnerabilities[$packageName] as $vulnVersion => $vulns) {
                if (version_compare($packageVersion, $vulnVersion, '<=')) {
                    return $vulns;
                }
            }
        }

        return [];
    }

    protected function generateSecurityReport($findings)
    {
        $report = [
            'scan_date' => now()->toISOString(),
            'overall_score' => $this->calculateSecurityScore($findings),
            'risk_level' => $this->determineRiskLevel($findings),
            'critical_issues' => $this->extractCriticalIssues($findings),
            'recommendations' => $this->generateSecurityRecommendations($findings),
            'detailed_findings' => $findings,
        ];

        return $report;
    }

    protected function calculateSecurityScore($findings)
    {
        $totalIssues = 0;
        $criticalIssues = 0;
        $highIssues = 0;
        $mediumIssues = 0;

        foreach ($findings as $category => $issues) {
            if (is_array($issues)) {
                foreach ($issues as $issue) {
                    $totalIssues++;
                    switch ($issue['severity'] ?? 'medium') {
                        case 'critical':
                            $criticalIssues++;
                            break;
                        case 'high':
                            $highIssues++;
                            break;
                        case 'medium':
                            $mediumIssues++;
                            break;
                    }
                }
            }
        }

        if ($totalIssues === 0) return 100;

        // Calculate score based on issue severity
        $score = 100 - ($criticalIssues * 25) - ($highIssues * 15) - ($mediumIssues * 5);

        return max(0, $score);
    }
}
