### 3. Cross-Site Scripting (XSS) Protection

```php
<?php

namespace App\Security\Analyzers;

class XSSProtectionAnalyzer
{
    public function analyzeXSSProtection()
    {
        $findings = [];

        // Check CSRF protection
        $findings['csrf_protection'] = $this->checkCSRFProtection();

        // Analyze output escaping
        $findings['output_escaping'] = $this->analyzeOutputEscaping();

        // Check Content Security Policy
        $findings['csp_headers'] = $this->checkCSPHeaders();

        // Analyze user-generated content handling
        $findings['user_content'] = $this->analyzeUserContentHandling();

        return $findings;
    }

    protected function checkCSRFProtection()
    {
        $issues = [];

        // Check if CSRF middleware is enabled globally
        $kernelFile = app_path('Http/Kernel.php');
        $kernelContent = file_get_contents($kernelFile);

        if (!str_contains($kernelContent, 'VerifyCsrfToken')) {
            $issues[] = [
                'severity' => 'high',
                'issue' => 'CSRF protection middleware not found in Kernel',
                'recommendation' => 'Add VerifyCsrfToken middleware to web middleware group',
            ];
        }

        // Check for forms without CSRF tokens
        $bladeFiles = glob(resource_path('views') . '/**/*.blade.php', GLOB_BRACE);

        foreach ($bladeFiles as $file) {
            $content = file_get_contents($file);

            // Look for forms without CSRF tokens
            if (preg_match('/<form[^>]*method=["\']post["\'][^>]*>/i', $content)) {
                if (!str_contains($content, '@csrf') && !str_contains($content, 'csrf_token()')) {
                    $issues[] = [
                        'severity' => 'high',
                        'file' => str_replace(resource_path('views/'), '', $file),
                        'issue' => 'Form with POST method missing CSRF token',
                        'recommendation' => 'Add @csrf directive to all POST forms',
                    ];
                }
            }
        }

        return $issues;
    }

    protected function analyzeOutputEscaping()
    {
        $issues = [];
        $bladeFiles = glob(resource_path('views') . '/**/*.blade.php', GLOB_BRACE);

        foreach ($bladeFiles as $file) {
            $content = file_get_contents($file);

            // Look for raw output (potential XSS)
            preg_match_all('/\{!!\s*\$[^}]+\s*!!\}/', $content, $matches);

            foreach ($matches[0] as $match) {
                $issues[] = [
                    'severity' => 'medium',
                    'file' => str_replace(resource_path('views/'), '', $file),
                    'issue' => 'Raw output detected: ' . $match,
                    'recommendation' => 'Use {{ }} for automatic escaping unless raw HTML is specifically needed',
                ];
            }

            // Check for dangerous HTML attributes
            if (preg_match('/\{\{\s*\$[^}]*\s*\}\}.*(?:onclick|onload|onerror)/i', $content)) {
                $issues[] = [
                    'severity' => 'high',
                    'file' => str_replace(resource_path('views/'), '', $file),
                    'issue' => 'Variable output in dangerous HTML attributes',
                    'recommendation' => 'Sanitize data before outputting in HTML attributes',
                ];
            }
        }

        return $issues;
    }

    protected function checkCSPHeaders()
    {
        $issues = [];

        // Check if CSP middleware exists
        $middlewarePath = app_path('Http/Middleware');
        $cspFiles = glob($middlewarePath . '/*CSP*.php');

        if (empty($cspFiles)) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'No Content Security Policy implementation found',
                'recommendation' => 'Implement CSP headers to prevent XSS attacks',
                'example' => 'Use spatie/laravel-csp package or custom middleware',
            ];
        }

        // Check for inline scripts in blade files
        $bladeFiles = glob(resource_path('views') . '/**/*.blade.php', GLOB_BRACE);

        foreach ($bladeFiles as $file) {
            $content = file_get_contents($file);

            if (preg_match('/<script[^>]*>[^<]*</i', $content)) {
                $issues[] = [
                    'severity' => 'medium',
                    'file' => str_replace(resource_path('views/'), '', $file),
                    'issue' => 'Inline scripts detected',
                    'recommendation' => 'Move scripts to external files and use CSP nonces',
                ];
            }
        }

        return $issues;
    }
}
```

### 4. Environment & Configuration Security

```php
<?php

namespace App\Security\Analyzers;

class EnvironmentSecurityAnalyzer
{
    public function analyzeEnvironmentSecurity()
    {
        $findings = [];

        // Check environment configuration
        $findings['env_config'] = $this->checkEnvironmentConfig();

        // Analyze file permissions
        $findings['file_permissions'] = $this->checkFilePermissions();

        // Check for sensitive data exposure
        $findings['sensitive_data'] = $this->checkSensitiveDataExposure();

        // Analyze error handling
        $findings['error_handling'] = $this->analyzeErrorHandling();

        // Check HTTPS configuration
        $findings['https_config'] = $this->checkHTTPSConfiguration();

        return $findings;
    }

    protected function checkEnvironmentConfig()
    {
        $issues = [];

        // Check if .env file exists and is not in version control
        if (!file_exists(base_path('.env'))) {
            $issues[] = [
                'severity' => 'high',
                'issue' => '.env file does not exist',
                'recommendation' => 'Create .env file for environment configuration',
            ];
        }

        // Check if .env.example exists
        if (!file_exists(base_path('.env.example'))) {
            $issues[] = [
                'severity' => 'low',
                'issue' => '.env.example file does not exist',
                'recommendation' => 'Create .env.example as template for environment variables',
            ];
        }

        // Check critical environment variables
        $criticalVars = ['APP_KEY', 'DB_PASSWORD', 'MAIL_PASSWORD'];
        foreach ($criticalVars as $var) {
            if (empty(env($var))) {
                $issues[] = [
                    'severity' => $var === 'APP_KEY' ? 'high' : 'medium',
                    'issue' => "{$var} is not set or empty",
                    'recommendation' => "Set {$var} in .env file",
                ];
            }
        }

        // Check APP_KEY strength
        $appKey = config('app.key');
        if ($appKey && strlen(base64_decode(substr($appKey, 7))) < 32) {
            $issues[] = [
                'severity' => 'high',
                'issue' => 'APP_KEY appears to be weak',
                'recommendation' => 'Generate new APP_KEY using php artisan key:generate',
            ];
        }

        // Check for debug mode in production
        if (config('app.debug') && app()->environment('production')) {
            $issues[] = [
                'severity' => 'high',
                'issue' => 'Debug mode enabled in production',
                'recommendation' => 'Set APP_DEBUG=false in production environment',
            ];
        }

        return $issues;
    }

    protected function checkFilePermissions()
    {
        $issues = [];

        // Check critical directory permissions
        $criticalPaths = [
            storage_path() => '755',
            storage_path('logs') => '755',
            bootstrap_path('cache') => '755',
            base_path('.env') => '600',
        ];

        foreach ($criticalPaths as $path => $expectedPerm) {
            if (file_exists($path)) {
                $currentPerm = substr(sprintf('%o', fileperms($path)), -3);

                if ($currentPerm !== $expectedPerm) {
                    $issues[] = [
                        'severity' => 'medium',
                        'issue' => "Incorrect permissions on {$path}",
                        'current' => $currentPerm,
                        'expected' => $expectedPerm,
                        'recommendation' => "chmod {$expectedPerm} {$path}",
                    ];
                }
            }
        }

        return $issues;
    }

    protected function checkSensitiveDataExposure()
    {
        $issues = [];

        // Check for sensitive files in public directory
        $publicPath = public_path();
        $sensitiveFiles = ['.env', '.git', 'composer.json', 'composer.lock'];

        foreach ($sensitiveFiles as $file) {
            if (file_exists($publicPath . '/' . $file)) {
                $issues[] = [
                    'severity' => 'high',
                    'issue' => "Sensitive file {$file} found in public directory",
                    'recommendation' => "Remove {$file} from public directory",
                ];
            }
        }

        // Check for exposed configuration files
        $configFiles = glob(public_path() . '/*.{ini,conf,config}', GLOB_BRACE);
        foreach ($configFiles as $file) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'Configuration file exposed in public directory: ' . basename($file),
                'recommendation' => 'Move configuration files outside web root',
            ];
        }

        return $issues;
    }

    protected function analyzeErrorHandling()
    {
        $issues = [];

        // Check error reporting in production
        if (app()->environment('production')) {
            if (config('app.debug')) {
                $issues[] = [
                    'severity' => 'high',
                    'issue' => 'Detailed error reporting enabled in production',
                    'recommendation' => 'Disable debug mode in production to prevent information disclosure',
                ];
            }

            // Check if custom error pages exist
            $errorViews = ['401', '403', '404', '419', '429', '500', '503'];
            foreach ($errorViews as $error) {
                if (!view()->exists("errors.{$error}")) {
                    $issues[] = [
                        'severity' => 'low',
                        'issue' => "Custom error page for {$error} does not exist",
                        'recommendation' => "Create resources/views/errors/{$error}.blade.php",
                    ];
                }
            }
        }

        return $issues;
    }
}
```

### 5. Dependency Security Analysis

```php
<?php

namespace App\Security\Analyzers;

use Illuminate\Support\Facades\Http;

class DependencySecurityAnalyzer
{
    public function analyzeDependencies()
    {
        $findings = [];

        // Check composer dependencies
        $findings['composer_vulnerabilities'] = $this->checkComposerVulnerabilities();

        // Analyze package versions
        $findings['outdated_packages'] = $this->checkOutdatedPackages();

        // Check for abandoned packages
        $findings['abandoned_packages'] = $this->checkAbandonedPackages();

        // Analyze npm dependencies (if exists)
        if (file_exists(base_path('package.json'))) {
            $findings['npm_vulnerabilities'] = $this->checkNpmVulnerabilities();
        }

        return $findings;
    }

    protected function checkComposerVulnerabilities()
    {
        $issues = [];

        if (!file_exists(base_path('composer.lock'))) {
            $issues[] = [
                'severity' => 'medium',
                'issue' => 'composer.lock file not found',
                'recommendation' => 'Run composer install to generate composer.lock',
            ];
            return $issues;
        }

        $composerLock = json_decode(file_get_contents(base_path('composer.lock')), true);
        $packages = array_merge(
            $composerLock['packages'] ?? [],
            $composerLock['packages-dev'] ?? []
        );

        foreach ($packages as $package) {
            $vulnerabilities = $this->checkPackageVulnerabilities($package);
            if (!empty($vulnerabilities)) {
                $issues[] = [
                    'severity' => $this->calculateVulnerabilitySeverity($vulnerabilities),
                    'package' => $package['name'],
                    'version' => $package['version'],
                    'vulnerabilities' => $vulnerabilities,
                    'recommendation' => 'Update package to latest secure version',
                ];
            }
        }

        return $issues;
    }

    protected function checkPackageVulnerabilities($package)
    {
        // This would integrate with security databases like:
        // - Packagist security advisories
        // - CVE databases
        // - GitHub security advisories

        $knownVulnerabilities = [
            'laravel/framework' => [
                '8.0.0' => ['CVE-2021-XXXX' => 'SQL injection vulnerability'],
                '9.0.0' => ['CVE-2022-XXXX' => 'XSS vulnerability'],
            ],
            'symfony/symfony' => [
                '4.4.0' => ['CVE-2020-XXXX' => 'Remote code execution'],
            ],
        ];

        $packageName = $package['name'];
        $packageVersion = $package['version'];

        if (isset($knownVulnerabilities[$packageName])) {
            foreach ($knownVulnerabilities[$packageName] as $vulnVersion => $vulns) {
                if (version_compare($packageVersion, $vulnVersion, '<=')) {
                    return $vulns;
                }
            }
        }

        return [];
    }

    protected function generateSecurityReport($findings)
    {
        $report = [
            'scan_date' => now()->toISOString(),
            'overall_score' => $this->calculateSecurityScore($findings),
            'risk_level' => $this->determineRiskLevel($findings),
            'critical_issues' => $this->extractCriticalIssues($findings),
            'recommendations' => $this->generateSecurityRecommendations($findings),
            'detailed_findings' => $findings,
        ];

        return $report;
    }

    protected function calculateSecurityScore($findings)
    {
        $totalIssues = 0;
        $criticalIssues = 0;
        $highIssues = 0;
        $mediumIssues = 0;

        foreach ($findings as $category => $issues) {
            if (is_array($issues)) {
                foreach ($issues as $issue) {
                    $totalIssues++;
                    switch ($issue['severity'] ?? 'medium') {
                        case 'critical':
                            $criticalIssues++;
                            break;
                        case 'high':
                            $highIssues++;
                            break;
                        case 'medium':
                            $mediumIssues++;
                            break;
                    }
                }
            }
        }

        if ($totalIssues === 0) return 100;

        // Calculate score based on issue severity
        $score = 100 - ($criticalIssues * 25) - ($highIssues * 15) - ($mediumIssues * 5);

        return max(0, $score);
    }
}
