# Laravel Code Quality Analyzer

**Command**: `/laravel:code-quality [options]`

**Description**: Analyzes code quality, enforces PSR standards, validates Laravel conventions, and provides detailed improvement recommendations for maintainable, professional code.

## Usage

```bash
/laravel:code-quality --full-analysis
/laravel:code-quality --psr-compliance --laravel-conventions
/laravel:code-quality --complexity-analysis --documentation-check
/laravel:code-quality --fix-issues --generate-report
```

## Options

- `--full-analysis` - Comprehensive code quality analysis
- `--psr-compliance` - Check PSR-12 coding standards compliance
- `--laravel-conventions` - Validate Laravel naming conventions and patterns
- `--complexity-analysis` - Analyze cyclomatic complexity and maintainability
- `--documentation-check` - Validate code documentation and comments
- `--architecture-review` - Review application architecture patterns
- `--fix-issues` - Automatically fix code style issues where possible
- `--generate-report` - Create detailed HTML quality report
- `--strict-mode` - Apply stricter quality standards
- `--exclude-vendor` - Exclude vendor directory from analysis

## Code Quality Analysis Categories

### 1. PSR Standards Compliance

```php
<?php

namespace App\Analyzers;

class PSRComplianceAnalyzer
{
    public function analyzePSRCompliance()
    {
        $findings = [];

        // PSR-1: Basic Coding Standard
        $findings['psr1'] = $this->checkPSR1Compliance();

        // PSR-12: Extended Coding Style
        $findings['psr12'] = $this->checkPSR12Compliance();

        // PSR-4: Autoloading Standard
        $findings['psr4'] = $this->checkPSR4Compliance();

        return $findings;
    }

    protected function checkPSR1Compliance()
    {
        $issues = [];
        $files = $this->getPhpFiles();

        foreach ($files as $file) {
            $content = file_get_contents($file);
            $issues = array_merge($issues, $this->checkPSR1Rules($file, $content));
        }

        return $issues;
    }

    protected function checkPSR1Rules($file, $content)
    {
        $issues = [];

        // Check for opening PHP tag
        if (!str_starts_with($content, '<?php')) {
            $issues[] = [
                'rule' => 'PSR-1.1',
                'file' => $file,
                'issue' => 'File must start with <?php tag',
                'severity' => 'error',
                'line' => 1,
            ];
        }

        // Check for closing PHP tag in pure PHP files
        if (str_ends_with(trim($content), '?>') && !$this->isTemplateFile($file)) {
            $issues[] = [
                'rule' => 'PSR-1.2',
                'file' => $file,
                'issue' => 'Pure PHP files must not have closing ?> tag',
                'severity' => 'error',
                'line' => substr_count($content, "\n") + 1,
            ];
        }

        // Check namespace declaration
        if (!preg_match('/^namespace\s+[A-Za-z\\\\][A-Za-z0-9\\\\]*;$/m', $content)) {
            if (!$this->isConfigFile($file) && !$this->isMigrationFile($file)) {
                $issues[] = [
                    'rule' => 'PSR-1.3',
                    'file' => $file,
                    'issue' => 'Classes must be in a namespace',
                    'severity' => 'error',
                ];
            }
        }

        // Check class naming
        preg_match_all('/class\s+([A-Za-z_][A-Za-z0-9_]*)/m', $content, $matches, PREG_OFFSET_CAPTURE);
        foreach ($matches[1] as $match) {
            $className = $match[0];
            if (!preg_match('/^[A-Z][a-zA-Z0-9]*$/', $className)) {
                $line = substr_count(substr($content, 0, $match[1]), "\n") + 1;
                $issues[] = [
                    'rule' => 'PSR-1.4',
                    'file' => $file,
                    'issue' => "Class name '{$className}' must be in StudlyCase",
                    'severity' => 'error',
                    'line' => $line,
                ];
            }
        }

        // Check method naming
        preg_match_all('/function\s+([A-Za-z_][A-Za-z0-9_]*)/m', $content, $matches, PREG_OFFSET_CAPTURE);
        foreach ($matches[1] as $match) {
            $methodName = $match[0];
            if (!preg_match('/^[a-z][a-zA-Z0-9]*$/', $methodName) && $methodName !== '__construct') {
                $line = substr_count(substr($content, 0, $match[1]), "\n") + 1;
                $issues[] = [
                    'rule' => 'PSR-1.5',
                    'file' => $file,
                    'issue' => "Method name '{$methodName}' must be in camelCase",
                    'severity' => 'error',
                    'line' => $line,
                ];
            }
        }

        return $issues;
    }

    protected function checkPSR12Compliance()
    {
        $issues = [];
        $files = $this->getPhpFiles();

        foreach ($files as $file) {
            $content = file_get_contents($file);
            $lines = explode("\n", $content);

            $issues = array_merge($issues, $this->checkPSR12Rules($file, $content, $lines));
        }

        return $issues;
    }

    protected function checkPSR12Rules($file, $content, $lines)
    {
        $issues = [];

        // Check indentation (4 spaces, no tabs)
        foreach ($lines as $lineNum => $line) {
            if (str_contains($line, "\t")) {
                $issues[] = [
                    'rule' => 'PSR-12.1',
                    'file' => $file,
                    'issue' => 'Use 4 spaces for indentation, not tabs',
                    'severity' => 'error',
                    'line' => $lineNum + 1,
                ];
            }

            // Check line length (120 characters soft limit)
            if (strlen($line) > 120) {
                $issues[] = [
                    'rule' => 'PSR-12.2',
                    'file' => $file,
                    'issue' => 'Line exceeds 120 characters (' . strlen($line) . ' chars)',
                    'severity' => 'warning',
                    'line' => $lineNum + 1,
                ];
            }
        }

        // Check brace placement for classes
        if (preg_match_all('/class\s+\w+[^{]*\n\s*{/m', $content, $matches, PREG_OFFSET_CAPTURE)) {
            foreach ($matches[0] as $match) {
                $line = substr_count(substr($content, 0, $match[1]), "\n") + 1;
                $issues[] = [
                    'rule' => 'PSR-12.3',
                    'file' => $file,
                    'issue' => 'Opening brace for class must be on next line',
                    'severity' => 'error',
                    'line' => $line,
                ];
            }
        }

        // Check method visibility
        preg_match_all('/function\s+([A-Za-z_][A-Za-z0-9_]*)/m', $content, $matches, PREG_OFFSET_CAPTURE);
        foreach ($matches[0] as $i => $match) {
            $methodDeclaration = $match[0];
            $line = substr_count(substr($content, 0, $match[1]), "\n") + 1;

            if (!preg_match('/(public|private|protected)\s+function/', $methodDeclaration)) {
                $issues[] = [
                    'rule' => 'PSR-12.4',
                    'file' => $file,
                    'issue' => 'Method visibility must be declared',
                    'severity' => 'error',
                    'line' => $line,
                ];
            }
        }

        return $issues;
    }
}
```

### 2. Laravel Conventions Analysis

```php
<?php

namespace App\Analyzers;

class LaravelConventionsAnalyzer
{
    public function analyzeLaravelConventions()
    {
        $findings = [];

        // Model conventions
        $findings['models'] = $this->checkModelConventions();

        // Controller conventions
        $findings['controllers'] = $this->checkControllerConventions();

        // Migration conventions
        $findings['migrations'] = $this->checkMigrationConventions();

        // Route conventions
        $findings['routes'] = $this->checkRouteConventions();

        // View conventions
        $findings['views'] = $this->checkViewConventions();

        return $findings;
    }

    protected function checkModelConventions()
    {
        $issues = [];
        $modelPath = app_path('Models');

        if (!is_dir($modelPath)) {
            $issues[] = [
                'type' => 'structure',
                'issue' => 'Models directory does not exist',
                'recommendation' => 'Create app/Models directory for model classes',
                'severity' => 'warning',
            ];
            return $issues;
        }

        $modelFiles = glob($modelPath . '/*.php');

        foreach ($modelFiles as $file) {
            $content = file_get_contents($file);
            $className = basename($file, '.php');

            // Check model naming convention
            if (!preg_match('/^[A-Z][a-zA-Z0-9]*$/', $className)) {
                $issues[] = [
                    'type' => 'naming',
                    'file' => $file,
                    'issue' => "Model name '{$className}' should be singular and in StudlyCase",
                    'recommendation' => 'Use singular form: User, Post, Product',
                    'severity' => 'error',
                ];
            }

            // Check if model extends Model
            if (!str_contains($content, 'extends Model')) {
                $issues[] = [
                    'type' => 'inheritance',
                    'file' => $file,
                    'issue' => "Model '{$className}' should extend Illuminate\\Database\\Eloquent\\Model",
                    'severity' => 'error',
                ];
            }

            // Check for proper fillable or guarded
            if (!str_contains($content, '$fillable') && !str_contains($content, '$guarded')) {
                $issues[] = [
                    'type' => 'mass_assignment',
                    'file' => $file,
                    'issue' => "Model '{$className}' should define \$fillable or \$guarded",
                    'recommendation' => 'Add protected $fillable = [...] property',
                    'severity' => 'warning',
                ];
            }

            // Check for relationship method conventions
            $this->checkRelationshipConventions($file, $content, $issues);

            // Check for proper use of casts
            $this->checkCastConventions($file, $content, $issues);
        }

        return $issues;
    }

    protected function checkControllerConventions()
    {
        $issues = [];
        $controllerPath = app_path('Http/Controllers');

        $controllerFiles = glob($controllerPath . '/*.php');

        foreach ($controllerFiles as $file) {
            $content = file_get_contents($file);
            $className = basename($file, '.php');

            // Check controller naming
            if (!str_ends_with($className, 'Controller')) {
                $issues[] = [
                    'type' => 'naming',
                    'file' => $file,
                    'issue' => "Controller name '{$className}' should end with 'Controller'",
                    'severity' => 'error',
                ];
            }

            // Check if controller extends Controller
            if (!str_contains($content, 'extends Controller')) {
                $issues[] = [
                    'type' => 'inheritance',
                    'file' => $file,
                    'issue' => "Controller '{$className}' should extend base Controller",
                    'severity' => 'error',
                ];
            }

            // Check for resource controller methods
            $this->checkResourceControllerMethods($file, $content, $issues);

            // Check for proper dependency injection
            $this->checkDependencyInjection($file, $content, $issues);

            // Check method complexity
            $this->checkMethodComplexity($file, $content, $issues);
        }

        return $issues;
    }

    protected function checkMigrationConventions()
    {
        $issues = [];
        $migrationPath = database_path('migrations');

        $migrationFiles = glob($migrationPath . '/*.php');

        foreach ($migrationFiles as $file) {
            $content = file_get_contents($file);
            $filename = basename($file);

            // Check migration naming convention
            if (!preg_match('/^\d{4}_\d{2}_\d{2}_\d{6}_[a-z_]+\.php$/', $filename)) {
                $issues[] = [
                    'type' => 'naming',
                    'file' => $file,
                    'issue' => "Migration filename '{$filename}' doesn't follow Laravel convention",
                    'recommendation' => 'Use: YYYY_MM_DD_HHMMSS_description_in_snake_case.php',
                    'severity' => 'warning',
                ];
            }

            // Check for proper up/down methods
            if (!str_contains($content, 'public function up()')) {
                $issues[] = [
                    'type' => 'structure',
                    'file' => $file,
                    'issue' => 'Migration missing up() method',
                    'severity' => 'error',
                ];
            }

            if (!str_contains($content, 'public function down()')) {
                $issues[] = [
                    'type' => 'structure',
                    'file' => $file,
                    'issue' => 'Migration missing down() method',
                    'severity' => 'error',
                ];
            }

            // Check for proper Schema usage
            $this->checkSchemaUsage($file, $content, $issues);
        }

        return $issues;
    }

    protected function checkRelationshipConventions($file, $content, &$issues)
    {
        // Check belongsTo relationships
        preg_match_all('/function\s+(\w+)\(\)\s*{[^}]*return\s+\$this->belongsTo\([^)]+\)/s', $content, $matches);
        foreach ($matches[1] as $relationName) {
            if (!preg_match('/^[a-z][a-zA-Z0-9]*$/', $relationName)) {
                $issues[] = [
                    'type' => 'relationship',
                    'file' => $file,
                    'issue' => "BelongsTo relationship '{$relationName}' should be singular camelCase",
                    'recommendation' => 'Use: user(), category(), author()',
                    'severity' => 'warning',
                ];
            }
        }

        // Check hasMany relationships
        preg_match_all('/function\s+(\w+)\(\)\s*{[^}]*return\s+\$this->hasMany\([^)]+\)/s', $content, $matches);
        foreach ($matches[1] as $relationName) {
            if (!preg_match('/^[a-z][a-zA-Z0-9]*s$/', $relationName)) {
                $issues[] = [
                    'type' => 'relationship',
                    'file' => $file,
                    'issue' => "HasMany relationship '{$relationName}' should be plural camelCase",
                    'recommendation' => 'Use: users(), posts(), comments()',
                    'severity' => 'warning',
                ];
            }
        }
    }

    protected function checkCastConventions($file, $content, &$issues)
    {
        if (preg_match('/protected\s+\$casts\s*=\s*\[(.*?)\]/s', $content, $matches)) {
            $castsContent = $matches[1];

            // Check for proper date casting
            if (str_contains($castsContent, 'created_at') || str_contains($castsContent, 'updated_at')) {
                $issues[] = [
                    'type' => 'casting',
                    'file' => $file,
                    'issue' => 'Do not cast created_at/updated_at timestamps (automatically handled)',
                    'severity' => 'info',
                ];
            }

            // Check for boolean casting
            if (preg_match_all("/['\"](\w+)['\"].*['\"]bool(ean)?['\"]/", $castsContent, $boolMatches)) {
                foreach ($boolMatches[1] as $field) {
                    if (!str_starts_with($field, 'is_') && !str_starts_with($field, 'has_')) {
                        $issues[] = [
                            'type' => 'casting',
                            'file' => $file,
                            'issue' => "Boolean field '{$field}' should be prefixed with 'is_' or 'has_'",
                            'recommendation' => "Use: is_{$field} or has_{$field}",
                            'severity' => 'suggestion',
                        ];
                    }
                }
            }
        }
    }
}
