### 3. Code Complexity Analysis

```php
<?php

namespace App\Analyzers;

class ComplexityAnalyzer
{
    public function analyzeComplexity()
    {
        $findings = [];

        // Cyclomatic complexity
        $findings['cyclomatic'] = $this->analyzeCyclomaticComplexity();

        // NPath complexity
        $findings['npath'] = $this->analyzeNPathComplexity();

        // Class coupling
        $findings['coupling'] = $this->analyzeClassCoupling();

        // Method length
        $findings['method_length'] = $this->analyzeMethodLength();

        // Class size
        $findings['class_size'] = $this->analyzeClassSize();

        return $findings;
    }

    protected function analyzeCyclomaticComplexity()
    {
        $issues = [];
        $files = $this->getPhpFiles();

        foreach ($files as $file) {
            $content = file_get_contents($file);
            $methods = $this->extractMethods($content);

            foreach ($methods as $method) {
                $complexity = $this->calculateCyclomaticComplexity($method['body']);

                if ($complexity > 10) {
                    $issues[] = [
                        'file' => $file,
                        'method' => $method['name'],
                        'complexity' => $complexity,
                        'severity' => $complexity > 15 ? 'high' : 'medium',
                        'issue' => "Method has high cyclomatic complexity ({$complexity})",
                        'recommendation' => 'Consider breaking method into smaller methods',
                        'line' => $method['line'],
                    ];
                }
            }
        }

        return $issues;
    }

    protected function calculateCyclomaticComplexity($methodBody)
    {
        // Start with base complexity of 1
        $complexity = 1;

        // Count decision points
        $patterns = [
            '/\bif\s*\(/' => 'if statement',
            '/\belse\s+if\b/' => 'else if statement', 
            '/\bwhile\s*\(/' => 'while loop',
            '/\bfor\s*\(/' => 'for loop',
            '/\bforeach\s*\(/' => 'foreach loop',
            '/\bswitch\s*\(/' => 'switch statement',
            '/\bcase\s+/' => 'case statement',
            '/\bcatch\s*\(/' => 'catch block',
            '/\?\s*[^:]*\s*:/' => 'ternary operator',
            '/&&/' => 'logical AND',
            '/\|\|/' => 'logical OR',
        ];

        foreach ($patterns as $pattern => $description) {
            $complexity += preg_match_all($pattern, $methodBody);
        }

        return $complexity;
    }

    protected function analyzeMethodLength()
    {
        $issues = [];
        $files = $this->getPhpFiles();

        foreach ($files as $file) {
            $content = file_get_contents($file);
            $methods = $this->extractMethods($content);

            foreach ($methods as $method) {
                $lineCount = substr_count($method['body'], "\n");

                if ($lineCount > 50) {
                    $issues[] = [
                        'file' => $file,
                        'method' => $method['name'],
                        'lines' => $lineCount,
                        'severity' => $lineCount > 100 ? 'high' : 'medium',
                        'issue' => "Method is too long ({$lineCount} lines)",
                        'recommendation' => 'Break method into smaller, focused methods',
                        'line' => $method['line'],
                    ];
                }
            }
        }

        return $issues;
    }

    protected function analyzeClassSize()
    {
        $issues = [];
        $files = $this->getPhpFiles();

        foreach ($files as $file) {
            $content = file_get_contents($file);
            $lineCount = substr_count($content, "\n");

            if ($lineCount > 500) {
                $className = $this->extractClassName($content);
                $issues[] = [
                    'file' => $file,
                    'class' => $className,
                    'lines' => $lineCount,
                    'severity' => $lineCount > 1000 ? 'high' : 'medium',
                    'issue' => "Class is too large ({$lineCount} lines)",
                    'recommendation' => 'Consider breaking class into smaller classes',
                ];
            }
        }

        return $issues;
    }

    protected function analyzeClassCoupling()
    {
        $issues = [];
        $files = $this->getPhpFiles();

        foreach ($files as $file) {
            $content = file_get_contents($file);
            $dependencies = $this->countClassDependencies($content);

            if ($dependencies > 20) {
                $className = $this->extractClassName($content);
                $issues[] = [
                    'file' => $file,
                    'class' => $className,
                    'dependencies' => $dependencies,
                    'severity' => $dependencies > 30 ? 'high' : 'medium',
                    'issue' => "Class has high coupling ({$dependencies} dependencies)",
                    'recommendation' => 'Reduce dependencies through dependency injection or service patterns',
                ];
            }
        }

        return $issues;
    }
}
