### 4. Documentation Analysis

```php
<?php

namespace App\Analyzers;

class DocumentationAnalyzer
{
    public function analyzeDocumentation()
    {
        $findings = [];

        // Class documentation
        $findings['classes'] = $this->checkClassDocumentation();

        // Method documentation
        $findings['methods'] = $this->checkMethodDocumentation();

        // Property documentation
        $findings['properties'] = $this->checkPropertyDocumentation();

        // README and documentation files
        $findings['project_docs'] = $this->checkProjectDocumentation();

        return $findings;
    }

    protected function checkClassDocumentation()
    {
        $issues = [];
        $files = $this->getPhpFiles();

        foreach ($files as $file) {
            $content = file_get_contents($file);

            // Find class declarations
            preg_match_all('/class\s+(\w+)/m', $content, $matches, PREG_OFFSET_CAPTURE);

            foreach ($matches[1] as $match) {
                $className = $match[0];
                $position = $match[1];

                // Check for class docblock
                $beforeClass = substr($content, 0, $position);
                $lines = explode("\n", $beforeClass);
                $relevantLines = array_slice($lines, -10); // Last 10 lines before class

                $hasDocblock = false;
                foreach ($relevantLines as $line) {
                    if (str_contains($line, '/**') || str_contains($line, ' * ')) {
                        $hasDocblock = true;
                        break;
                    }
                }

                if (!$hasDocblock) {
                    $issues[] = [
                        'file' => $file,
                        'class' => $className,
                        'issue' => 'Class missing docblock documentation',
                        'recommendation' => 'Add /** docblock */ describing class purpose',
                        'severity' => 'warning',
                    ];
                }
            }
        }

        return $issues;
    }

    protected function checkMethodDocumentation()
    {
        $issues = [];
        $files = $this->getPhpFiles();

        foreach ($files as $file) {
            $content = file_get_contents($file);
            $methods = $this->extractMethods($content);

            foreach ($methods as $method) {
                $docblockIssues = $this->analyzeMethodDocblock($method, $file);
                $issues = array_merge($issues, $docblockIssues);
            }
        }

        return $issues;
    }

    protected function analyzeMethodDocblock($method, $file)
    {
        $issues = [];
        $methodName = $method['name'];
        $methodBody = $method['body'];

        // Skip magic methods and constructors for basic docblock requirement
        $skipMethods = ['__construct', '__destruct', '__get', '__set', '__call'];
        if (in_array($methodName, $skipMethods)) {
            return $issues;
        }

        // Check if method has docblock
        if (!str_contains($method['docblock'], '/**')) {
            $issues[] = [
                'file' => $file,
                'method' => $methodName,
                'issue' => 'Method missing docblock documentation',
                'recommendation' => 'Add docblock describing method purpose, parameters, and return value',
                'severity' => 'warning',
                'line' => $method['line'],
            ];
            return $issues;
        }

        $docblock = $method['docblock'];

        // Check for @param documentation
        $paramCount = preg_match_all('/function\s+\w+\s*\([^)]*\$(\w+)/', $method['signature'], $paramMatches);
        $docParamCount = preg_match_all('/@param\s/', $docblock);

        if ($paramCount > 0 && $docParamCount < $paramCount) {
            $issues[] = [
                'file' => $file,
                'method' => $methodName,
                'issue' => 'Missing @param documentation for some parameters',
                'recommendation' => 'Document all method parameters with @param',
                'severity' => 'info',
                'line' => $method['line'],
            ];
        }

        // Check for @return documentation
        if (str_contains($methodBody, 'return ') && !str_contains($docblock, '@return')) {
            $issues[] = [
                'file' => $file,
                'method' => $methodName,
                'issue' => 'Missing @return documentation',
                'recommendation' => 'Add @return tag describing return value',
                'severity' => 'info',
                'line' => $method['line'],
            ];
        }

        // Check for @throws documentation
        if (str_contains($methodBody, 'throw ') && !str_contains($docblock, '@throws')) {
            $issues[] = [
                'file' => $file,
                'method' => $methodName,
                'issue' => 'Missing @throws documentation',
                'recommendation' => 'Document exceptions with @throws',
                'severity' => 'info',
                'line' => $method['line'],
            ];
        }

        return $issues;
    }
}
