### 5. Architecture Review

```php
<?php

namespace App\Analyzers;

class ArchitectureAnalyzer
{
    public function analyzeArchitecture()
    {
        $findings = [];

        // SOLID principles
        $findings['solid'] = $this->checkSOLIDPrinciples();

        // Design patterns usage
        $findings['patterns'] = $this->analyzeDesignPatterns();

        // Dependency management
        $findings['dependencies'] = $this->analyzeDependencyManagement();

        // Layer separation
        $findings['layers'] = $this->analyzeLayerSeparation();

        return $findings;
    }

    protected function checkSOLIDPrinciples()
    {
        $issues = [];

        // Single Responsibility Principle
        $issues['srp'] = $this->checkSingleResponsibility();

        // Open/Closed Principle
        $issues['ocp'] = $this->checkOpenClosed();

        // Liskov Substitution Principle
        $issues['lsp'] = $this->checkLiskovSubstitution();

        // Interface Segregation Principle
        $issues['isp'] = $this->checkInterfaceSegregation();

        // Dependency Inversion Principle
        $issues['dip'] = $this->checkDependencyInversion();

        return $issues;
    }

    protected function checkSingleResponsibility()
    {
        $issues = [];
        $controllerPath = app_path('Http/Controllers');

        $controllerFiles = glob($controllerPath . '/*.php');

        foreach ($controllerFiles as $file) {
            $content = file_get_contents($file);
            $methodCount = preg_match_all('/public\s+function\s+\w+/', $content);

            // Controllers with too many methods likely violate SRP
            if ($methodCount > 15) {
                $className = basename($file, '.php');
                $issues[] = [
                    'principle' => 'Single Responsibility',
                    'file' => $file,
                    'class' => $className,
                    'issue' => "Controller has {$methodCount} methods (SRP violation)",
                    'recommendation' => 'Split controller into smaller, focused controllers',
                    'severity' => 'medium',
                ];
            }

            // Check for mixed responsibilities
            $this->checkMixedResponsibilities($file, $content, $issues);
        }

        return $issues;
    }

    protected function analyzeDependencyManagement()
    {
        $issues = [];

        // Check for service container usage
        $issues['container'] = $this->checkServiceContainerUsage();

        // Check for dependency injection
        $issues['injection'] = $this->checkDependencyInjection();

        // Check for facade usage
        $issues['facades'] = $this->analyzeFacadeUsage();

        return $issues;
    }

    protected function generateQualityReport($findings)
    {
        $report = [
            'scan_date' => now()->toISOString(),
            'overall_score' => $this->calculateQualityScore($findings),
            'maintainability_index' => $this->calculateMaintainabilityIndex($findings),
            'technical_debt' => $this->calculateTechnicalDebt($findings),
            'recommendations' => $this->generatePriorityRecommendations($findings),
            'detailed_findings' => $findings,
        ];

        return $report;
    }

    protected function calculateQualityScore($findings)
    {
        $totalIssues = 0;
        $weightedScore = 0;

        $weights = [
            'error' => 10,
            'high' => 8,
            'medium' => 5,
            'warning' => 3,
            'low' => 2,
            'info' => 1,
        ];

        foreach ($findings as $category => $issues) {
            if (is_array($issues)) {
                foreach ($issues as $issue) {
                    $severity = $issue['severity'] ?? 'medium';
                    $weight = $weights[$severity] ?? 5;
                    $totalIssues++;
                    $weightedScore += $weight;
                }
            }
        }

        if ($totalIssues === 0) return 100;

        $maxPossibleScore = $totalIssues * 10;
        $score = 100 - (($weightedScore / $maxPossibleScore) * 100);

        return max(0, round($score));
    }
}
