# Laravel Vulnerability Scanner

**Command**: `/laravel:vulnerability-scan [options]`

**Description**: Scans your Laravel application for known security vulnerabilities in dependencies, identifies outdated packages, and provides detailed remediation guidance.

## Usage

```bash
/laravel:vulnerability-scan --comprehensive
/laravel:vulnerability-scan --composer-only --check-advisories
/laravel:vulnerability-scan --npm-audit --generate-report
/laravel:vulnerability-scan --auto-fix --exclude-dev
```

## Options

- `--comprehensive` - Full vulnerability scan including all dependencies
- `--composer-only` - Scan only Composer (PHP) dependencies
- `--npm-audit` - Include npm/yarn dependency scanning
- `--check-advisories` - Check against security advisory databases
- `--auto-fix` - Automatically update vulnerable packages where safe
- `--exclude-dev` - Skip development dependencies
- `--generate-report` - Create detailed vulnerability report
- `--severity-threshold=high` - Only report vulnerabilities above threshold
- `--json-output` - Output results in JSON format for CI/CD integration

## Vulnerability Detection Categories

### 1. Composer Dependencies Scanning

```php
<?php

namespace App\Security\Scanners;

use Illuminate\Support\Facades\Http;

class ComposerVulnerabilityScanner
{
    protected $advisoryDatabases = [
        'packagist' => 'https://packagist.org/api/security-advisories',
        'friends_of_php' => 'https://security.sensiolabs.org/api/security-advisories',
        'github' => 'https://api.github.com/advisories',
    ];

    public function scanComposerDependencies()
    {
        $results = [
            'scan_date' => now()->toISOString(),
            'vulnerabilities' => [],
            'outdated_packages' => [],
            'abandoned_packages' => [],
            'summary' => [],
        ];

        // Load composer.lock for exact versions
        $composerLock = $this->loadComposerLock();
        if (!$composerLock) {
            return $this->createError('composer.lock not found or invalid');
        }

        // Check all packages
        $allPackages = array_merge(
            $composerLock['packages'] ?? [],
            $composerLock['packages-dev'] ?? []
        );

        foreach ($allPackages as $package) {
            $vulnerabilities = $this->checkPackageVulnerabilities($package);
            if (!empty($vulnerabilities)) {
                $results['vulnerabilities'][] = [
                    'package' => $package['name'],
                    'installed_version' => $package['version'],
                    'vulnerabilities' => $vulnerabilities,
                    'severity' => $this->calculateMaxSeverity($vulnerabilities),
                    'fix_available' => $this->checkFixAvailable($package, $vulnerabilities),
                ];
            }

            // Check for outdated packages
            $latestVersion = $this->getLatestVersion($package['name']);
            if ($this->isOutdated($package['version'], $latestVersion)) {
                $results['outdated_packages'][] = [
                    'package' => $package['name'],
                    'installed' => $package['version'],
                    'latest' => $latestVersion,
                    'behind_by' => $this->calculateVersionDistance($package['version'], $latestVersion),
                ];
            }

            // Check for abandoned packages
            if ($this->isAbandoned($package)) {
                $results['abandoned_packages'][] = [
                    'package' => $package['name'],
                    'replacement' => $package['replacement'] ?? null,
                    'last_update' => $package['time'] ?? null,
                ];
            }
        }

        $results['summary'] = $this->generateSummary($results);

        return $results;
    }

    protected function checkPackageVulnerabilities($package)
    {
        $vulnerabilities = [];

        // Check against known vulnerability databases
        foreach ($this->advisoryDatabases as $source => $url) {
            $advisories = $this->fetchAdvisories($source, $package['name']);

            foreach ($advisories as $advisory) {
                if ($this->isVersionVulnerable($package['version'], $advisory['affected_versions'])) {
                    $vulnerabilities[] = [
                        'source' => $source,
                        'cve' => $advisory['cve'] ?? null,
                        'title' => $advisory['title'],
                        'description' => $advisory['description'],
                        'severity' => $advisory['severity'],
                        'affected_versions' => $advisory['affected_versions'],
                        'fixed_versions' => $advisory['fixed_versions'] ?? [],
                        'published_date' => $advisory['published_date'],
                        'reference_urls' => $advisory['references'] ?? [],
                    ];
                }
            }
        }

        // Check Laravel-specific vulnerabilities
        if (str_starts_with($package['name'], 'laravel/')) {
            $laravelVulns = $this->checkLaravelSpecificVulnerabilities($package);
            $vulnerabilities = array_merge($vulnerabilities, $laravelVulns);
        }

        return $vulnerabilities;
    }

    protected function checkLaravelSpecificVulnerabilities($package)
    {
        $vulnerabilities = [];

        // Known Laravel vulnerabilities database
        $laravelVulns = [
            'laravel/framework' => [
                [
                    'cve' => 'CVE-2021-43808',
                    'title' => 'Laravel Framework SQL Injection',
                    'description' => 'SQL injection vulnerability in query builder',
                    'severity' => 'high',
                    'affected_versions' => '>=8.0.0,<8.75.0|>=9.0.0,<9.3.0',
                    'fixed_versions' => ['8.75.0', '9.3.0'],
                    'published_date' => '2021-12-14',
                ],
                [
                    'cve' => 'CVE-2022-30778',
                    'title' => 'Laravel Framework XSS Vulnerability',
                    'description' => 'Cross-site scripting vulnerability in Blade templating',
                    'severity' => 'medium',
                    'affected_versions' => '>=8.0.0,<8.83.23|>=9.0.0,<9.19.0',
                    'fixed_versions' => ['8.83.23', '9.19.0'],
                    'published_date' => '2022-06-15',
                ],
            ],
            'laravel/passport' => [
                [
                    'cve' => 'CVE-2020-15568',
                    'title' => 'Laravel Passport CSRF Vulnerability',
                    'description' => 'CSRF vulnerability in OAuth authorization flow',
                    'severity' => 'medium',
                    'affected_versions' => '>=4.0.0,<9.3.2|>=10.0.0,<10.0.1',
                    'fixed_versions' => ['9.3.2', '10.0.1'],
                    'published_date' => '2020-07-29',
                ],
            ],
            'laravel/sanctum' => [
                [
                    'cve' => 'CVE-2021-45047',
                    'title' => 'Laravel Sanctum Authentication Bypass',
                    'description' => 'Authentication bypass in token validation',
                    'severity' => 'high',
                    'affected_versions' => '>=1.0.0,<2.14.2',
                    'fixed_versions' => ['2.14.2'],
                    'published_date' => '2021-12-22',
                ],
            ],
        ];

        $packageName = $package['name'];
        $packageVersion = $package['version'];

        if (isset($laravelVulns[$packageName])) {
            foreach ($laravelVulns[$packageName] as $vuln) {
                if ($this->isVersionVulnerable($packageVersion, $vuln['affected_versions'])) {
                    $vulnerabilities[] = $vuln;
                }
            }
        }

        return $vulnerabilities;
    }

    protected function isVersionVulnerable($version, $affectedVersions)
    {
        // Parse version constraints like ">=8.0.0,<8.75.0|>=9.0.0,<9.3.0"
        $constraints = explode('|', $affectedVersions);

        foreach ($constraints as $constraint) {
            $conditions = explode(',', $constraint);
            $matchesAll = true;

            foreach ($conditions as $condition) {
                $condition = trim($condition);

                if (!$this->evaluateVersionConstraint($version, $condition)) {
                    $matchesAll = false;
                    break;
                }
            }

            if ($matchesAll) {
                return true;
            }
        }

        return false;
    }

    protected function evaluateVersionConstraint($version, $constraint)
    {
        if (preg_match('/^(>=|>|<=|<|=)(.+)$/', $constraint, $matches)) {
            $operator = $matches[1];
            $constraintVersion = $matches[2];

            return version_compare($version, $constraintVersion, $operator);
        }

        return false;
    }

    protected function generateFixRecommendations($vulnerabilities)
    {
        $recommendations = [];

        foreach ($vulnerabilities as $vuln) {
            $package = $vuln['package'];
            $currentVersion = $vuln['installed_version'];

            $recommendation = [
                'package' => $package,
                'current_version' => $currentVersion,
                'action' => 'update',
                'priority' => $this->getPriorityFromSeverity($vuln['severity']),
            ];

            // Find the best fix version
            $fixVersions = [];
            foreach ($vuln['vulnerabilities'] as $vulnerability) {
                if (!empty($vulnerability['fixed_versions'])) {
                    $fixVersions = array_merge($fixVersions, $vulnerability['fixed_versions']);
                }
            }

            if (!empty($fixVersions)) {
                $bestFixVersion = $this->findBestFixVersion($currentVersion, $fixVersions);
                $recommendation['target_version'] = $bestFixVersion;
                $recommendation['command'] = "composer update {$package}:{$bestFixVersion}";
            } else {
                $recommendation['action'] = 'investigate';
                $recommendation['note'] = 'No fixed version available, consider alternatives';
            }

            $recommendations[] = $recommendation;
        }

        return $recommendations;
    }
}
```

### 2. NPM/Yarn Dependencies Scanning

```php
<?php

namespace App\Security\Scanners;

class NPMVulnerabilityScanner
{
    public function scanNPMDependencies()
    {
        $results = [
            'scan_date' => now()->toISOString(),
            'vulnerabilities' => [],
            'summary' => [],
        ];

        if (!$this->hasPackageJson()) {
            return $this->createError('package.json not found');
        }

        // Run npm audit
        $auditResults = $this->runNPMAudit();
        $results['vulnerabilities'] = $this->parseNPMAuditResults($auditResults);

        // Run yarn audit if yarn.lock exists
        if ($this->hasYarnLock()) {
            $yarnResults = $this->runYarnAudit();
            $results['yarn_vulnerabilities'] = $this->parseYarnAuditResults($yarnResults);
        }

        $results['summary'] = $this->generateNPMSummary($results);

        return $results;
    }

    protected function runNPMAudit()
    {
        $command = 'npm audit --json --audit-level=info';
        $output = shell_exec($command);

        return json_decode($output, true);
    }

    protected function parseNPMAuditResults($auditData)
    {
        $vulnerabilities = [];

        if (isset($auditData['vulnerabilities'])) {
            foreach ($auditData['vulnerabilities'] as $packageName => $vulnerability) {
                $vulnerabilities[] = [
                    'package' => $packageName,
                    'severity' => $vulnerability['severity'],
                    'title' => $vulnerability['via'][0]['title'] ?? 'Unknown vulnerability',
                    'description' => $vulnerability['via'][0]['url'] ?? '',
                    'installed_version' => $vulnerability['range'] ?? 'Unknown',
                    'fixed_version' => $vulnerability['fixAvailable']['version'] ?? null,
                    'path' => $vulnerability['effects'] ?? [],
                ];
            }
        }

        return $vulnerabilities;
    }

    protected function generateAutoFixCommands($vulnerabilities)
    {
        $commands = [];

        foreach ($vulnerabilities as $vuln) {
            if ($vuln['fixed_version']) {
                $commands[] = "npm update {$vuln['package']}";
            }
        }

        // Add npm audit fix command
        if (!empty($vulnerabilities)) {
            $commands[] = 'npm audit fix';
            $commands[] = 'npm audit fix --force'; // For breaking changes
        }

        return array_unique($commands);
    }
}
```

### 3. Security Advisory Integration

```php
<?php

namespace App\Security\Scanners;

class SecurityAdvisoryChecker
{
    protected $advisorySources = [
        'packagist' => 'https://packagist.org/api/security-advisories',
        'github' => 'https://api.github.com/advisories',
        'snyk' => 'https://snyk.io/api/v1/vulnerabilities',
        'ossindex' => 'https://ossindex.sonatype.org/api/v3/component-report',
    ];

    public function checkSecurityAdvisories($packages)
    {
        $advisories = [];

        foreach ($this->advisorySources as $source => $url) {
            try {
                $sourceAdvisories = $this->fetchFromSource($source, $packages);
                $advisories = array_merge($advisories, $sourceAdvisories);
            } catch (\Exception $e) {
                logger("Failed to fetch advisories from {$source}: " . $e->getMessage());
            }
        }

        return $this->deduplicateAdvisories($advisories);
    }

    protected function fetchFromPackagist($packages)
    {
        $response = Http::timeout(30)->get($this->advisorySources['packagist']);

        if (!$response->successful()) {
            throw new \Exception('Failed to fetch Packagist advisories');
        }

        $advisories = [];
        $data = $response->json();

        foreach ($packages as $package) {
            $packageName = $package['name'];

            if (isset($data['advisories'][$packageName])) {
                foreach ($data['advisories'][$packageName] as $advisory) {
                    $advisories[] = [
                        'source' => 'packagist',
                        'package' => $packageName,
                        'cve' => $advisory['cve'] ?? null,
                        'title' => $advisory['title'],
                        'description' => $advisory['description'],
                        'severity' => $this->normalizeSeverity($advisory['severity'] ?? 'medium'),
                        'affected_versions' => $advisory['affectedVersions'],
                        'fixed_versions' => $advisory['fixedVersions'] ?? [],
                        'published_date' => $advisory['publishedDate'],
                        'reference_urls' => $advisory['references'] ?? [],
                    ];
                }
            }
        }

        return $advisories;
    }

    protected function fetchFromGitHub($packages)
    {
        $advisories = [];

        foreach ($packages as $package) {
            $packageName = $package['name'];

            // GitHub API endpoint for security advisories
            $url = "https://api.github.com/repos/{$packageName}/security-advisories";

            try {
                $response = Http::withHeaders([
                    'Accept' => 'application/vnd.github.v3+json',
                    'Authorization' => 'token ' . env('GITHUB_TOKEN', ''),
                ])->timeout(10)->get($url);

                if ($response->successful()) {
                    $data = $response->json();

                    foreach ($data as $advisory) {
                        $advisories[] = [
                            'source' => 'github',
                            'package' => $packageName,
                            'cve' => $advisory['cve_id'] ?? null,
                            'title' => $advisory['summary'],
                            'description' => $advisory['description'],
                            'severity' => $this->normalizeSeverity($advisory['severity']),
                            'affected_versions' => $this->parseGitHubVersions($advisory['vulnerabilities'] ?? []),
                            'published_date' => $advisory['published_at'],
                            'reference_urls' => $advisory['references'] ?? [],
                        ];
                    }
                }
            } catch (\Exception $e) {
                // Continue with other packages if one fails
                continue;
            }
        }

        return $advisories;
    }

    protected function generateVulnerabilityReport($scanResults)
    {
        $report = [
            'executive_summary' => $this->generateExecutiveSummary($scanResults),
            'vulnerability_overview' => $this->generateVulnerabilityOverview($scanResults),
            'detailed_findings' => $scanResults,
            'remediation_plan' => $this->generateRemediationPlan($scanResults),
            'risk_assessment' => $this->generateRiskAssessment($scanResults),
            'compliance_impact' => $this->generateComplianceImpact($scanResults),
        ];

        return $report;
    }

    protected function generateExecutiveSummary($scanResults)
    {
        $totalVulns = count($scanResults['vulnerabilities'] ?? []);
        $criticalVulns = count(array_filter($scanResults['vulnerabilities'] ?? [], 
            fn($v) => ($v['severity'] ?? '') === 'critical'
        ));
        $highVulns = count(array_filter($scanResults['vulnerabilities'] ?? [], 
            fn($v) => ($v['severity'] ?? '') === 'high'
        ));

        $riskLevel = 'low';
        if ($criticalVulns > 0) {
            $riskLevel = 'critical';
        } elseif ($highVulns > 3) {
            $riskLevel = 'high';
        } elseif ($totalVulns > 10) {
            $riskLevel = 'medium';
        }

        return [
            'total_vulnerabilities' => $totalVulns,
            'critical_vulnerabilities' => $criticalVulns,
            'high_vulnerabilities' => $highVulns,
            'overall_risk_level' => $riskLevel,
            'immediate_action_required' => $criticalVulns > 0 || $highVulns > 2,
            'estimated_fix_time' => $this->estimateFixTime($scanResults),
        ];
    }

    protected function generateRemediationPlan($scanResults)
    {
        $plan = [
            'immediate_actions' => [],
            'short_term_actions' => [],
            'long_term_actions' => [],
            'automation_opportunities' => [],
        ];

        foreach ($scanResults['vulnerabilities'] ?? [] as $vuln) {
            $action = [
                'package' => $vuln['package'],
                'vulnerability' => $vuln['title'],
                'action' => $this->determineRemediationAction($vuln),
                'estimated_effort' => $this->estimateEffort($vuln),
            ];

            switch ($vuln['severity']) {
                case 'critical':
                case 'high':
                    $plan['immediate_actions'][] = $action;
                    break;
                case 'medium':
                    $plan['short_term_actions'][] = $action;
                    break;
                default:
                    $plan['long_term_actions'][] = $action;
            }
        }

        // Add automation suggestions
        if (count($scanResults['vulnerabilities'] ?? []) > 5) {
            $plan['automation_opportunities'][] = [
                'type' => 'ci_cd_integration',
                'description' => 'Integrate vulnerability scanning into CI/CD pipeline',
                'tools' => ['GitHub Security Advisories', 'Snyk', 'OWASP Dependency Check'],
            ];
        }

        return $plan;
    }
}
```

### 4. Automated Fix Generation

```php
<?php

namespace App\Security\Fixers;

class VulnerabilityAutoFixer
{
    public function generateAutoFixes($vulnerabilities)
    {
        $fixes = [];

        foreach ($vulnerabilities as $vuln) {
            $fix = $this->generateFixForVulnerability($vuln);
            if ($fix) {
                $fixes[] = $fix;
            }
        }

        return $this->prioritizeFixes($fixes);
    }

    protected function generateFixForVulnerability($vulnerability)
    {
        $package = $vulnerability['package'];
        $severity = $vulnerability['severity'];
        $fixedVersions = $vulnerability['vulnerabilities'][0]['fixed_versions'] ?? [];

        if (empty($fixedVersions)) {
            return null;
        }

        $bestFixVersion = $this->selectBestFixVersion($fixedVersions);

        return [
            'package' => $package,
            'current_version' => $vulnerability['installed_version'],
            'target_version' => $bestFixVersion,
            'severity' => $severity,
            'type' => $this->determineFixType($vulnerability),
            'command' => $this->generateUpdateCommand($package, $bestFixVersion),
            'risk_level' => $this->assessUpdateRisk($vulnerability, $bestFixVersion),
            'requires_testing' => $this->requiresTesting($vulnerability, $bestFixVersion),
            'backup_required' => $severity === 'critical',
        ];
    }

    protected function generateUpdateCommand($package, $version)
    {
        // For Composer packages
        if (str_contains($package, '/')) {
            return "composer require {$package}:{$version}";
        }

        // For NPM packages
        return "npm install {$package}@{$version}";
    }

    protected function assessUpdateRisk($vulnerability, $targetVersion)
    {
        $currentVersion = $vulnerability['installed_version'];

        // Parse semantic versioning
        $currentParts = explode('.', $currentVersion);
        $targetParts = explode('.', $targetVersion);

        if (count($currentParts) < 3 || count($targetParts) < 3) {
            return 'unknown';
        }

        // Major version change = high risk
        if ($currentParts[0] !== $targetParts[0]) {
            return 'high';
        }

        // Minor version change = medium risk
        if ($currentParts[1] !== $targetParts[1]) {
            return 'medium';
        }

        // Patch version change = low risk
        return 'low';
    }

    public function executeSafeFixes($fixes, $dryRun = false)
    {
        $results = [];

        foreach ($fixes as $fix) {
            if ($fix['risk_level'] === 'low' && !$fix['requires_testing']) {
                $result = $this->executeFix($fix, $dryRun);
                $results[] = $result;
            }
        }

        return $results;
    }

    protected function executeFix($fix, $dryRun = false)
    {
        $command = $fix['command'];

        if ($dryRun) {
            return [
                'package' => $fix['package'],
                'command' => $command,
                'status' => 'dry_run',
                'message' => 'Would execute: ' . $command,
            ];
        }

        try {
            $output = shell_exec($command . ' 2>&1');

            return [
                'package' => $fix['package'],
                'command' => $command,
                'status' => 'success',
                'output' => $output,
            ];
        } catch (\Exception $e) {
            return [
                'package' => $fix['package'],
                'command' => $command,
                'status' => 'failed',
                'error' => $e->getMessage(),
            ];
        }
    }
}
