# Laravel Test Suite Generator

**Command**: `/laravel:test-suite [target] [options]`

**Description**: Generates comprehensive test suites with feature tests, unit tests, integration tests, and performance tests for your Laravel application.

## Usage

```bash
/laravel:test-suite --full-coverage
/laravel:test-suite models --with-factories --pest
/laravel:test-suite api --authentication --rate-limiting
/laravel:test-suite controllers --feature-tests --unit-tests
```

## Options

- `--full-coverage` - Generate complete test suite for entire application
- `--pest` - Use Pest testing framework instead of PHPUnit
- `--with-factories` - Generate model factories for test data
- `--with-seeders` - Create database seeders for testing
- `--feature-tests` - Generate feature/integration tests
- `--unit-tests` - Generate isolated unit tests
- `--authentication` - Include authentication test scenarios
- `--rate-limiting` - Test API rate limiting
- `--performance-tests` - Add performance and load testing
- `--browser-tests` - Generate Laravel Dusk browser tests
- `--parallel-testing` - Configure parallel test execution

## Test Generation Categories

### 1. Model Testing with Factories

```php
<?php

namespace App\Generators;

class ModelTestGenerator
{
    public function generateModelTests($model)
    {
        $className = class_basename($model);
        $tests = [];

        // Generate basic model tests
        $tests['unit'] = $this->generateUnitTests($className, $model);

        // Generate relationship tests
        $tests['relationships'] = $this->generateRelationshipTests($className, $model);

        // Generate validation tests
        $tests['validation'] = $this->generateValidationTests($className, $model);

        // Generate factory tests
        $tests['factory'] = $this->generateFactoryTests($className, $model);

        return $tests;
    }

    protected function generateUnitTests($className, $model)
    {
        $testClass = "
<?php

namespace Tests\\Unit\\Models;

use App\\Models\\{$className};
use Illuminate\\Foundation\\Testing\\RefreshDatabase;
use Tests\\TestCase;

class {$className}Test extends TestCase
{
    use RefreshDatabase;

    /** @test */
    public function it_can_be_created()
    {
        \$model = {$className}::factory()->create();

        \$this->assertInstanceOf({$className}::class, \$model);
        \$this->assertDatabaseHas('{$this->getTableName($model)}', [
            'id' => \$model->id,
        ]);
    }

    /** @test */
    public function it_has_fillable_attributes()
    {
        \$model = new {$className}();
        \$fillable = {$this->generateFillableArray($model)};

        \$this->assertEquals(\$fillable, \$model->getFillable());
    }

    /** @test */
    public function it_casts_attributes_correctly()
    {
        \$model = new {$className}();
        \$expectedCasts = {$this->generateCastsArray($model)};

        \$this->assertEquals(\$expectedCasts, \$model->getCasts());
    }

    /** @test */
    public function it_has_correct_table_name()
    {
        \$model = new {$className}();
        \$expectedTable = '{$this->getTableName($model)}';

        \$this->assertEquals(\$expectedTable, \$model->getTable());
    }

    /** @test */
    public function it_uses_correct_primary_key()
    {
        \$model = new {$className}();

        \$this->assertEquals('id', \$model->getKeyName());
        \$this->assertTrue(\$model->getIncrementing());
    }

    /** @test */
    public function it_handles_timestamps_correctly()
    {
        \$model = {$className}::factory()->create();

        \$this->assertNotNull(\$model->created_at);
        \$this->assertNotNull(\$model->updated_at);
        \$this->assertInstanceOf(\\Carbon\\Carbon::class, \$model->created_at);
        \$this->assertInstanceOf(\\Carbon\\Carbon::class, \$model->updated_at);
    }

    {$this->generateAttributeTests($model)}
}";

        return $testClass;
    }

    protected function generateRelationshipTests($className, $model)
    {
        $relationships = $this->extractRelationships($model);
        $tests = [];

        foreach ($relationships as $relationship) {
            $tests[] = $this->generateRelationshipTest($className, $relationship);
        }

        $testClass = "
<?php

namespace Tests\\Unit\\Models;

use App\\Models\\{$className};
" . $this->generateRelationshipImports($relationships) . "
use Illuminate\\Foundation\\Testing\\RefreshDatabase;
use Tests\\TestCase;

class {$className}RelationshipTest extends TestCase
{
    use RefreshDatabase;

    " . implode("\n\n    ", $tests) . "
}";

        return $testClass;
    }

    protected function generateRelationshipTest($className, $relationship)
    {
        $relationName = $relationship['name'];
        $relatedModel = $relationship['model'];
        $type = $relationship['type'];

        switch ($type) {
            case 'belongsTo':
                return "/** @test */
    public function it_belongs_to_{$relationName}()
    {
        \${$relationName} = {$relatedModel}::factory()->create();
        \$model = {$className}::factory()->create([
            '{$relationship['foreign_key']}' => \${$relationName}->id,
        ]);

        \$this->assertInstanceOf({$relatedModel}::class, \$model->{$relationName});
        \$this->assertEquals(\${$relationName}->id, \$model->{$relationName}->id);
    }";

            case 'hasMany':
                return "/** @test */
    public function it_has_many_{$relationName}()
    {
        \$model = {$className}::factory()->create();
        \${$relationName} = {$relatedModel}::factory()->count(3)->create([
            '{$relationship['foreign_key']}' => \$model->id,
        ]);

        \$this->assertCount(3, \$model->{$relationName});
        \$this->assertInstanceOf(\\Illuminate\\Database\\Eloquent\\Collection::class, \$model->{$relationName});
        \$this->assertInstanceOf({$relatedModel}::class, \$model->{$relationName}->first());
    }";

            case 'belongsToMany':
                return "/** @test */
    public function it_belongs_to_many_{$relationName}()
    {
        \$model = {$className}::factory()->create();
        \${$relationName} = {$relatedModel}::factory()->count(3)->create();

        \$model->{$relationName}()->attach(\${$relationName}->pluck('id'));

        \$this->assertCount(3, \$model->fresh()->{$relationName});
        \$this->assertInstanceOf(\\Illuminate\\Database\\Eloquent\\Collection::class, \$model->{$relationName});
    }";

            default:
                return "/** @test */
    public function it_has_{$relationName}_relationship()
    {
        \$model = {$className}::factory()->create();

        \$this->assertTrue(method_exists(\$model, '{$relationName}'));
        // Add specific relationship tests based on your requirements
    }";
        }
    }

    protected function generateFactoryTests($className, $model)
    {
        $factory = "
<?php

namespace Tests\\Unit\\Factories;

use App\\Models\\{$className};
use Illuminate\\Foundation\\Testing\\RefreshDatabase;
use Tests\\TestCase;

class {$className}FactoryTest extends TestCase
{
    use RefreshDatabase;

    /** @test */
    public function factory_creates_valid_model()
    {
        \$model = {$className}::factory()->create();

        \$this->assertInstanceOf({$className}::class, \$model);
        \$this->assertDatabaseHas('{$this->getTableName($model)}', [
            'id' => \$model->id,
        ]);
    }

    /** @test */
    public function factory_can_override_attributes()
    {
        \$customData = {$this->generateCustomFactoryData($model)};

        \$model = {$className}::factory()->create(\$customData);

        " . $this->generateFactoryAssertions($model) . "
    }

    /** @test */
    public function factory_can_create_multiple_models()
    {
        \$models = {$className}::factory()->count(5)->create();

        \$this->assertCount(5, \$models);
        \$this->assertEquals(5, {$className}::count());
    }

    /** @test */
    public function factory_creates_unique_data()
    {
        \$models = {$className}::factory()->count(3)->create();

        \$uniqueFields = {$this->getUniqueFields($model)};

        foreach (\$uniqueFields as \$field) {
            \$values = \$models->pluck(\$field)->toArray();
            \$this->assertCount(3, array_unique(\$values), \"Field {\$field} should be unique\");
        }
    }

    {$this->generateFactoryStateTests($className, $model)}
}";

        return $factory;
    }
}
```

### 2. Feature/Integration Testing

```php
<?php

namespace App\Generators;

class FeatureTestGenerator
{
    public function generateControllerTests($controller)
    {
        $className = class_basename($controller);
        $modelName = str_replace('Controller', '', $className);

        $tests = $this->generateCRUDTests($className, $modelName);

        return $tests;
    }

    protected function generateCRUDTests($controllerName, $modelName)
    {
        $testClass = "
<?php

namespace Tests\\Feature;

use App\\Models\\{$modelName};
use App\\Models\\User;
use Illuminate\\Foundation\\Testing\\RefreshDatabase;
use Illuminate\\Foundation\\Testing\\WithFaker;
use Tests\\TestCase;

class {$controllerName}Test extends TestCase
{
    use RefreshDatabase, WithFaker;

    protected \$user;

    protected function setUp(): void
    {
        parent::setUp();

        \$this->user = User::factory()->create();
        \$this->actingAs(\$this->user);
    }

    /** @test */
    public function it_can_display_index_page()
    {
        {$modelName}::factory()->count(3)->create();

        \$response = \$this->get(route('{$this->getRouteName($modelName)}.index'));

        \$response->assertOk();
        \$response->assertViewIs('{$this->getViewName($modelName)}.index');
        \$response->assertViewHas('{$this->getPluralName($modelName)}');
    }

    /** @test */
    public function it_can_display_create_page()
    {
        \$response = \$this->get(route('{$this->getRouteName($modelName)}.create'));

        \$response->assertOk();
        \$response->assertViewIs('{$this->getViewName($modelName)}.create');
    }

    /** @test */
    public function it_can_store_valid_data()
    {
        \$data = {$modelName}::factory()->make()->toArray();

        \$response = \$this->post(route('{$this->getRouteName($modelName)}.store'), \$data);

        \$response->assertRedirect(route('{$this->getRouteName($modelName)}.index'));
        \$this->assertDatabaseHas('{$this->getTableName($modelName)}', \$data);
    }

    /** @test */
    public function it_validates_required_fields_on_store()
    {
        \$response = \$this->post(route('{$this->getRouteName($modelName)}.store'), []);

        \$response->assertSessionHasErrors({$this->getRequiredFields($modelName)});
    }

    /** @test */
    public function it_can_display_show_page()
    {
        \$model = {$modelName}::factory()->create();

        \$response = \$this->get(route('{$this->getRouteName($modelName)}.show', \$model));

        \$response->assertOk();
