### 3. Performance Testing

```php
<?php

namespace App\Generators;

class PerformanceTestGenerator
{
    public function generatePerformanceTests($model)
    {
        $className = class_basename($model);

        $testClass = "
<?php

namespace Tests\\Performance;

use App\\Models\\{$className};
use App\\Models\\User;
use Illuminate\\Foundation\\Testing\\RefreshDatabase;
use Illuminate\\Support\\Facades\\DB;
use Tests\\TestCase;

class {$className}PerformanceTest extends TestCase
{
    use RefreshDatabase;

    protected \$user;

    protected function setUp(): void
    {
        parent::setUp();

        \$this->user = User::factory()->create();
        \$this->actingAs(\$this->user);
    }

    /** @test */
    public function index_page_loads_within_acceptable_time()
    {
        // Create large dataset
        {$className}::factory()->count(100)->create();

        \$startTime = microtime(true);
        \$response = \$this->get(route('{$this->getRouteName($className)}.index'));
        \$endTime = microtime(true);

        \$responseTime = \$endTime - \$startTime;

        \$response->assertOk();
        \$this->assertLessThan(2.0, \$responseTime, 'Index page should load within 2 seconds');
    }

    /** @test */
    public function queries_are_optimized_for_index_page()
    {
        {$className}::factory()->count(50)->create();

        DB::enableQueryLog();

        \$this->get(route('{$this->getRouteName($className)}.index'));

        \$queries = DB::getQueryLog();

        // Should use minimal queries (ideally < 5)
        \$this->assertLessThan(5, count(\$queries), 'Index page should use minimal database queries');
    }

    /** @test */
    public function bulk_operations_perform_efficiently()
    {
        \$data = {$className}::factory()->count(1000)->make()->toArray();

        \$startTime = microtime(true);
        {$className}::insert(\$data);
        \$endTime = microtime(true);

        \$insertTime = \$endTime - \$startTime;

        \$this->assertLessThan(5.0, \$insertTime, 'Bulk insert should complete within 5 seconds');
        \$this->assertEquals(1000, {$className}::count());
    }

    /** @test */
    public function search_functionality_performs_well()
    {
        // Create searchable data
        {$className}::factory()->count(1000)->create();

        \$startTime = microtime(true);
        \$results = {$className}::where('name', 'like', '%test%')->get();
        \$endTime = microtime(true);

        \$searchTime = \$endTime - \$startTime;

        \$this->assertLessThan(1.0, \$searchTime, 'Search should complete within 1 second');
    }

    /** @test */
    public function pagination_handles_large_datasets()
    {
        {$className}::factory()->count(10000)->create();

        \$startTime = microtime(true);
        \$response = \$this->get(route('{$this->getRouteName($className)}.index', ['page' => 100]));
        \$endTime = microtime(true);

        \$responseTime = \$endTime - \$startTime;

        \$response->assertOk();
        \$this->assertLessThan(2.0, \$responseTime, 'Pagination should handle large datasets efficiently');
    }

    /** @test */
    public function memory_usage_stays_within_limits()
    {
        \$initialMemory = memory_get_usage(true);

        {$className}::factory()->count(1000)->create();
        \$models = {$className}::all();

        \$peakMemory = memory_get_peak_usage(true);
        \$memoryIncrease = \$peakMemory - \$initialMemory;

        // Should not use more than 50MB for 1000 records
        \$this->assertLessThan(50 * 1024 * 1024, \$memoryIncrease, 'Memory usage should stay within acceptable limits');
    }

    {$this->generateConcurrencyTests($className)}
}";

        return $testClass;
    }

    protected function generateConcurrencyTests($className)
    {
        return "
    /** @test */
    public function handles_concurrent_requests()
    {
        \$model = {$className}::factory()->create();

        // Simulate concurrent updates
        \$promises = [];
        for (\$i = 0; \$i < 10; \$i++) {
            \$promises[] = \$this->putAsync(route('{$this->getRouteName($className)}.update', \$model), [
                'name' => 'Updated Name ' . \$i,
            ]);
        }

        // Wait for all requests to complete
        foreach (\$promises as \$promise) {
            \$response = \$promise->wait();
            \$this->assertIn(\$response->getStatusCode(), [200, 422]);
        }

        // Verify data integrity
        \$model->refresh();
        \$this->assertStringStartsWith('Updated Name', \$model->name);
    }";
    }
}
