# Laravel API Documentation Generator

**Command**: `/laravel:api-docs [options]`

**Description**: Automatically generates comprehensive OpenAPI/Swagger documentation for your Laravel API with interactive documentation, code examples, and automated testing integration.

## Usage

```bash
/laravel:api-docs --generate-all
/laravel:api-docs --routes=api/* --format=openapi
/laravel:api-docs --interactive --postman-collection
/laravel:api-docs --auto-update --include-examples
```

## Options

- `--generate-all` - Generate documentation for all API routes
- `--routes=pattern` - Generate docs for specific route patterns
- `--format=openapi|swagger|postman` - Output format selection
- `--interactive` - Generate interactive Swagger UI
- `--postman-collection` - Create Postman collection export
- `--auto-update` - Set up automatic documentation updates
- `--include-examples` - Generate realistic request/response examples
- `--authentication` - Document authentication schemes
- `--rate-limiting` - Include rate limiting information
- `--versioning` - Support API versioning documentation

## Documentation Generation Features

### 1. OpenAPI Specification Generation

```php
<?php

namespace App\Documentation;

use Illuminate\Routing\Route;
use Illuminate\Support\Facades\Route as RouteFacade;
use ReflectionClass;
use ReflectionMethod;

class OpenAPIGenerator
{
    protected $openAPISpec = [
        'openapi' => '3.0.0',
        'info' => [],
        'servers' => [],
        'paths' => [],
        'components' => [
            'schemas' => [],
            'securitySchemes' => [],
            'responses' => [],
        ],
    ];

    public function generate()
    {
        $this->generateInfo();
        $this->generateServers();
        $this->generatePaths();
        $this->generateComponents();
        $this->generateSecuritySchemes();

        return $this->openAPISpec;
    }

    protected function generateInfo()
    {
        $this->openAPISpec['info'] = [
            'title' => config('app.name') . ' API',
            'description' => $this->generateApiDescription(),
            'version' => $this->getApiVersion(),
            'contact' => [
                'name' => 'API Support',
                'email' => config('mail.from.address'),
                'url' => config('app.url') . '/support',
            ],
            'license' => [
                'name' => 'MIT',
                'url' => 'https://opensource.org/licenses/MIT',
            ],
        ];
    }

    protected function generatePaths()
    {
        $routes = RouteFacade::getRoutes();

        foreach ($routes as $route) {
            if ($this->isApiRoute($route)) {
                $this->processApiRoute($route);
            }
        }
    }

    protected function processApiRoute(Route $route)
    {
        $uri = $this->formatRouteUri($route->uri());
        $methods = $route->methods();

        foreach ($methods as $method) {
            if (in_array(strtolower($method), ['get', 'post', 'put', 'patch', 'delete'])) {
                $this->openAPISpec['paths'][$uri][strtolower($method)] = 
                    $this->generateRouteDocumentation($route, $method);
            }
        }
    }

    protected function generateRouteDocumentation(Route $route, string $method)
    {
        $controller = $this->getRouteController($route);
        $action = $this->getRouteAction($route);

        $documentation = [
            'tags' => [$this->getRouteTag($route)],
            'summary' => $this->generateSummary($controller, $action, $method),
            'description' => $this->generateDescription($controller, $action),
            'operationId' => $this->generateOperationId($route, $method),
            'parameters' => $this->generateParameters($route, $controller, $action),
            'responses' => $this->generateResponses($controller, $action, $method),
        ];

        // Add request body for POST/PUT/PATCH
        if (in_array(strtolower($method), ['post', 'put', 'patch'])) {
            $documentation['requestBody'] = $this->generateRequestBody($controller, $action);
        }

        // Add security if route is protected
        if ($this->isProtectedRoute($route)) {
            $documentation['security'] = $this->getRouteSecurity($route);
        }

        return $documentation;
    }

    protected function generateParameters(Route $route, $controller, $action)
    {
        $parameters = [];

        // Path parameters
        $pathParams = $this->extractPathParameters($route->uri());
        foreach ($pathParams as $param) {
            $parameters[] = [
                'name' => $param,
                'in' => 'path',
                'required' => true,
                'schema' => [
                    'type' => $this->inferParameterType($param),
                ],
                'description' => $this->generateParameterDescription($param),
            ];
        }

        // Query parameters
        $queryParams = $this->extractQueryParameters($controller, $action);
        foreach ($queryParams as $param) {
            $parameters[] = [
                'name' => $param['name'],
                'in' => 'query',
                'required' => $param['required'] ?? false,
                'schema' => [
                    'type' => $param['type'] ?? 'string',
                ],
                'description' => $param['description'] ?? '',
            ];
        }

        return $parameters;
    }

    protected function generateRequestBody($controller, $action)
    {
        $formRequest = $this->getFormRequestClass($controller, $action);

        if ($formRequest) {
            $schema = $this->generateSchemaFromFormRequest($formRequest);

            return [
                'required' => true,
                'content' => [
                    'application/json' => [
                        'schema' => $schema,
                        'examples' => $this->generateRequestExamples($formRequest),
                    ],
                    'multipart/form-data' => [
                        'schema' => $schema,
                    ],
                ],
            ];
        }

        return [
            'required' => true,
            'content' => [
                'application/json' => [
                    'schema' => [
                        'type' => 'object',
                        'properties' => [],
                    ],
                ],
            ],
        ];
    }

    protected function generateResponses($controller, $action, $method)
    {
        $responses = [];

        // Success responses
        switch (strtolower($method)) {
            case 'get':
                $responses['200'] = $this->generateSuccessResponse($controller, $action, 'Retrieved successfully');
                break;
            case 'post':
                $responses['201'] = $this->generateSuccessResponse($controller, $action, 'Created successfully');
                break;
            case 'put':
            case 'patch':
                $responses['200'] = $this->generateSuccessResponse($controller, $action, 'Updated successfully');
                break;
            case 'delete':
                $responses['204'] = [
                    'description' => 'Deleted successfully',
                ];
                break;
        }

        // Error responses
        $responses['400'] = $this->generateErrorResponse('Bad Request', 'Invalid request data');
        $responses['401'] = $this->generateErrorResponse('Unauthorized', 'Authentication required');
        $responses['403'] = $this->generateErrorResponse('Forbidden', 'Access denied');
        $responses['404'] = $this->generateErrorResponse('Not Found', 'Resource not found');
        $responses['422'] = $this->generateErrorResponse('Unprocessable Entity', 'Validation failed');
        $responses['500'] = $this->generateErrorResponse('Internal Server Error', 'Server error');

        return $responses;
    }

    protected function generateSuccessResponse($controller, $action, $description)
    {
        $resourceClass = $this->getResourceClass($controller, $action);

        if ($resourceClass) {
            $schema = $this->generateSchemaFromResource($resourceClass);

            return [
                'description' => $description,
                'content' => [
                    'application/json' => [
                        'schema' => $schema,
                        'examples' => $this->generateResponseExamples($resourceClass),
                    ],
                ],
            ];
        }

        return [
            'description' => $description,
            'content' => [
                'application/json' => [
                    'schema' => [
                        'type' => 'object',
                        'properties' => [
                            'message' => [
                                'type' => 'string',
                                'example' => $description,
                            ],
                        ],
                    ],
                ],
            ],
        ];
    }

    protected function generateSchemaFromModel($modelClass)
    {
        $model = new $modelClass();
        $fillable = $model->getFillable();
        $casts = $model->getCasts();

        $properties = [];

        // Always include ID
        $properties['id'] = [
            'type' => 'integer',
            'format' => 'int64',
            'example' => 1,
        ];

        foreach ($fillable as $field) {
            $type = $this->inferFieldType($field, $casts);
            $properties[$field] = [
                'type' => $type['type'],
                'example' => $type['example'],
            ];

            if (isset($type['format'])) {
                $properties[$field]['format'] = $type['format'];
            }

            if (isset($type['description'])) {
                $properties[$field]['description'] = $type['description'];
            }
        }

        // Add timestamps
        if ($model->usesTimestamps()) {
            $properties['created_at'] = [
                'type' => 'string',
                'format' => 'date-time',
                'example' => '2024-01-15T10:30:00Z',
            ];
            $properties['updated_at'] = [
                'type' => 'string',
                'format' => 'date-time',
                'example' => '2024-01-15T10:30:00Z',
            ];
        }

        return [
            'type' => 'object',
            'properties' => $properties,
        ];
    }

    protected function generateSchemaFromFormRequest($formRequestClass)
    {
        $formRequest = new $formRequestClass();
        $rules = $formRequest->rules();

        $properties = [];
        $required = [];

        foreach ($rules as $field => $ruleSet) {
            $fieldRules = is_string($ruleSet) ? explode('|', $ruleSet) : $ruleSet;

            $fieldSchema = $this->parseValidationRules($fieldRules);
            $properties[$field] = $fieldSchema;

            if (in_array('required', $fieldRules)) {
                $required[] = $field;
            }
        }

        $schema = [
            'type' => 'object',
            'properties' => $properties,
        ];

        if (!empty($required)) {
            $schema['required'] = $required;
        }

        return $schema;
    }

    protected function parseValidationRules(array $rules)
    {
        $schema = ['type' => 'string'];

        foreach ($rules as $rule) {
            if (is_string($rule)) {
                switch (true) {
                    case str_starts_with($rule, 'integer'):
                        $schema['type'] = 'integer';
                        break;
                    case str_starts_with($rule, 'numeric'):
                        $schema['type'] = 'number';
                        break;
                    case str_starts_with($rule, 'boolean'):
                        $schema['type'] = 'boolean';
                        break;
                    case str_starts_with($rule, 'array'):
                        $schema['type'] = 'array';
                        $schema['items'] = ['type' => 'string'];
                        break;
                    case str_starts_with($rule, 'email'):
                        $schema['format'] = 'email';
                        break;
                    case str_starts_with($rule, 'url'):
                        $schema['format'] = 'uri';
                        break;
                    case str_starts_with($rule, 'date'):
                        $schema['format'] = 'date';
                        break;
                    case str_starts_with($rule, 'min:'):
                        $min = (int) substr($rule, 4);
                        if ($schema['type'] === 'string') {
                            $schema['minLength'] = $min;
                        } else {
                            $schema['minimum'] = $min;
                        }
                        break;
                    case str_starts_with($rule, 'max:'):
                        $max = (int) substr($rule, 4);
                        if ($schema['type'] === 'string') {
                            $schema['maxLength'] = $max;
                        } else {
                            $schema['maximum'] = $max;
                        }
                        break;
                }
            }
        }

        // Add example based on type
        $schema['example'] = $this->generateExampleValue($schema);

        return $schema;
    }

    protected function generateExampleValue($schema)
    {
        switch ($schema['type']) {
            case 'integer':
                return 1;
            case 'number':
                return 1.0;
            case 'boolean':
                return true;
            case 'array':
                return ['example'];
            case 'string':
                if (isset($schema['format'])) {
                    switch ($schema['format']) {
                        case 'email':
                            return 'user@example.com';
                        case 'uri':
                            return 'https://example.com';
                        case 'date':
                            return '2024-01-15';
                        case 'date-time':
                            return '2024-01-15T10:30:00Z';
                    }
                }
                return 'example string';
            default:
                return null;
        }
    }
}
