```php
<?php

namespace App\Documentation;

class SwaggerUIGenerator
{
    public function generateSwaggerUI($openApiSpec)
    {
        $swaggerConfig = [
            'swaggerVersion' => '3.52.5',
            'apiSpec' => $openApiSpec,
            'customCSS' => $this->getCustomCSS(),
            'customJS' => $this->getCustomJS(),
        ];

        return $this->renderSwaggerTemplate($swaggerConfig);
    }

    protected function renderSwaggerTemplate($config)
    {
        return "
<!DOCTYPE html>
<html lang=\"en\">
<head>
    <meta charset=\"UTF-8\">
    <title>{$config['apiSpec']['info']['title']} - API Documentation</title>
    <link rel=\"stylesheet\" type=\"text/css\" href=\"https://unpkg.com/swagger-ui-dist@{$config['swaggerVersion']}/swagger-ui-bundle.css\" />
    <link rel=\"icon\" type=\"image/png\" href=\"/favicon.ico\" sizes=\"32x32\" />
    <style>
        {$config['customCSS']}
    </style>
</head>
<body>
    <div id=\"swagger-ui\"></div>

    <script src=\"https://unpkg.com/swagger-ui-dist@{$config['swaggerVersion']}/swagger-ui-bundle.js\"></script>
    <script src=\"https://unpkg.com/swagger-ui-dist@{$config['swaggerVersion']}/swagger-ui-standalone-preset.js\"></script>
    <script>
        window.onload = function() {
            const ui = SwaggerUIBundle({
                url: '/api/documentation/openapi.json',
                dom_id: '#swagger-ui',
                deepLinking: true,
                presets: [
                    SwaggerUIBundle.presets.apis,
                    SwaggerUIStandalonePreset
                ],
                plugins: [
                    SwaggerUIBundle.plugins.DownloadUrl
                ],
                layout: \"StandaloneLayout\",
                tryItOutEnabled: true,
                requestInterceptor: function(request) {
                    // Add authentication token if available
                    const token = localStorage.getItem('api_token');
                    if (token) {
                        request.headers['Authorization'] = 'Bearer ' + token;
                    }
                    return request;
                },
                responseInterceptor: function(response) {
                    // Handle response logging or processing
                    return response;
                },
                onComplete: function(swaggerApi, swaggerUi) {
                    console.log('Swagger UI loaded');
                    {$config['customJS']}
                }
            });
        };
    </script>
</body>
</html>";
    }

    protected function getCustomCSS()
    {
        return "
        .swagger-ui .topbar { 
            background-color: #2563eb; 
        }
        .swagger-ui .info .title { 
            color: #1f2937; 
        }
        .swagger-ui .scheme-container { 
            background: #f9fafb; 
            border: 1px solid #e5e7eb; 
        }
        .swagger-ui .opblock.opblock-get .opblock-summary { 
            border-color: #10b981; 
        }
        .swagger-ui .opblock.opblock-post .opblock-summary { 
            border-color: #3b82f6; 
        }
        .swagger-ui .opblock.opblock-put .opblock-summary { 
            border-color: #f59e0b; 
        }
        .swagger-ui .opblock.opblock-delete .opblock-summary { 
            border-color: #ef4444; 
        }
        ";
    }

    protected function getCustomJS()
    {
        return "
        // Add authentication helper
        function authenticateAPI() {
            const token = prompt('Enter your API token:');
            if (token) {
                localStorage.setItem('api_token', token);
                ui.preauthorizeApiKey('bearerAuth', 'Bearer ' + token);
                alert('Authentication token saved!');
            }
        }

        // Add custom button for authentication
        const authButton = document.createElement('button');
        authButton.innerHTML = 'Authenticate';
        authButton.onclick = authenticateAPI;
        authButton.style.cssText = 'position: absolute; top: 10px; right: 100px; z-index: 1000; padding: 8px 16px; background: #2563eb; color: white; border: none; border-radius: 4px; cursor: pointer;';
        document.body.appendChild(authButton);
        ";
    }
}
