class PostmanCollectionGenerator
{
    public function generateCollection($openApiSpec)
    {
        $collection = [
            'info' => $this->generateCollectionInfo($openApiSpec),
            'item' => [],
            'auth' => $this->generateAuth($openApiSpec),
            'event' => $this->generateEvents(),
            'variable' => $this->generateVariables(),
        ];

        foreach ($openApiSpec['paths'] as $path => $methods) {
            foreach ($methods as $method => $operation) {
                $collection['item'][] = $this->generateRequestItem($path, $method, $operation);
            }
        }

        return $collection;
    }

    protected function generateCollectionInfo($openApiSpec)
    {
        return [
            'name' => $openApiSpec['info']['title'],
            'description' => $openApiSpec['info']['description'],
            'version' => $openApiSpec['info']['version'],
            'schema' => 'https://schema.getpostman.com/json/collection/v2.1.0/collection.json',
        ];
    }

    protected function generateRequestItem($path, $method, $operation)
    {
        $request = [
            'name' => $operation['summary'] ?? ucfirst($method) . ' ' . $path,
            'request' => [
                'method' => strtoupper($method),
                'header' => $this->generateHeaders($operation),
                'url' => [
                    'raw' => '{{base_url}}' . $this->convertPathParameters($path),
                    'host' => ['{{base_url}}'],
                    'path' => explode('/', trim($path, '/')),
                ],
                'description' => $operation['description'] ?? '',
            ],
            'response' => $this->generateSampleResponses($operation),
        ];

        // Add request body for POST/PUT/PATCH
        if (isset($operation['requestBody'])) {
            $request['request']['body'] = $this->generateRequestBody($operation['requestBody']);
        }

        // Add query parameters
        if (isset($operation['parameters'])) {
            $queryParams = array_filter($operation['parameters'], fn($p) => $p['in'] === 'query');
            if (!empty($queryParams)) {
                $request['request']['url']['query'] = array_map(function($param) {
                    return [
                        'key' => $param['name'],
                        'value' => $param['schema']['example'] ?? '',
                        'description' => $param['description'] ?? '',
                    ];
                }, $queryParams);
            }
        }

        return $request;
    }

    protected function generateHeaders($operation)
    {
        $headers = [
            [
                'key' => 'Accept',
                'value' => 'application/json',
            ],
            [
                'key' => 'Content-Type',
                'value' => 'application/json',
            ],
        ];

        // Add authorization header if required
        if (isset($operation['security'])) {
            $headers[] = [
                'key' => 'Authorization',
                'value' => 'Bearer {{auth_token}}',
            ];
        }

        return $headers;
    }

    protected function generateRequestBody($requestBody)
    {
        $jsonContent = $requestBody['content']['application/json'] ?? null;

        if ($jsonContent && isset($jsonContent['examples'])) {
            $example = array_values($jsonContent['examples'])[0];

            return [
                'mode' => 'raw',
                'raw' => json_encode($example['value'], JSON_PRETTY_PRINT),
                'options' => [
                    'raw' => [
                        'language' => 'json',
                    ],
                ],
            ];
        }

        return [
            'mode' => 'raw',
            'raw' => '{}',
            'options' => [
                'raw' => [
                    'language' => 'json',
                ],
            ],
        ];
    }

    protected function generateVariables()
    {
        return [
            [
                'key' => 'base_url',
                'value' => config('app.url') . '/api',
                'type' => 'string',
            ],
            [
                'key' => 'auth_token',
                'value' => '',
                'type' => 'string',
            ],
        ];
    }

    protected function generateEvents()
    {
        return [
            [
                'listen' => 'prerequest',
                'script' => [
                    'exec' => [
                        '// Pre-request script',
                        'console.log("Sending request to:", pm.request.url);',
                    ],
                    'type' => 'text/javascript',
                ],
            ],
            [
                'listen' => 'test',
                'script' => [
                    'exec' => [
                        '// Test script',
                        'pm.test("Status code is success", function () {',
                        '    pm.expect(pm.response.code).to.be.oneOf([200, 201, 204]);',
                        '});',
                        '',
                        'pm.test("Response time is less than 2000ms", function () {',
                        '    pm.expect(pm.response.responseTime).to.be.below(2000);',
                        '});',
                    ],
                    'type' => 'text/javascript',
                ],
            ],
        ];
    }
}
