### 4. Automated Documentation Updates

```php
<?php

namespace App\Console\Commands;

use App\Documentation\OpenAPIGenerator;
use App\Documentation\SwaggerUIGenerator;
use App\Documentation\PostmanCollectionGenerator;
use Illuminate\Console\Command;

class GenerateApiDocumentation extends Command
{
    protected $signature = 'api:docs {--format=all : Output format (openapi|swagger|postman|all)}
                                     {--output= : Output directory}
                                     {--watch : Watch for changes and auto-regenerate}';

    protected $description = 'Generate comprehensive API documentation';

    public function handle()
    {
        $this->info('üöÄ Generating API Documentation...');

        $generator = new OpenAPIGenerator();
        $openApiSpec = $generator->generate();

        $format = $this->option('format');
        $outputDir = $this->option('output') ?: public_path('docs/api');

        if (!is_dir($outputDir)) {
            mkdir($outputDir, 0755, true);
        }

        $this->generateDocumentation($openApiSpec, $format, $outputDir);

        if ($this->option('watch')) {
            $this->watchForChanges($outputDir);
        }

        $this->info('‚úÖ API documentation generated successfully!');
        $this->showGeneratedFiles($outputDir);
    }

    protected function generateDocumentation($openApiSpec, $format, $outputDir)
    {
        switch ($format) {
            case 'openapi':
                $this->generateOpenAPISpec($openApiSpec, $outputDir);
                break;
            case 'swagger':
                $this->generateSwaggerUI($openApiSpec, $outputDir);
                break;
            case 'postman':
                $this->generatePostmanCollection($openApiSpec, $outputDir);
                break;
            case 'all':
            default:
                $this->generateOpenAPISpec($openApiSpec, $outputDir);
                $this->generateSwaggerUI($openApiSpec, $outputDir);
                $this->generatePostmanCollection($openApiSpec, $outputDir);
                break;
        }
    }

    protected function generateOpenAPISpec($spec, $outputDir)
    {
        $this->info('üìÑ Generating OpenAPI specification...');

        file_put_contents(
            $outputDir . '/openapi.json',
            json_encode($spec, JSON_PRETTY_PRINT | JSON_UNESCAPED_SLASHES)
        );

        file_put_contents(
            $outputDir . '/openapi.yaml',
            yaml_emit($spec, YAML_UTF8_ENCODING)
        );
    }

    protected function generateSwaggerUI($spec, $outputDir)
    {
        $this->info('üåê Generating Swagger UI...');

        $swaggerGenerator = new SwaggerUIGenerator();
        $swaggerHTML = $swaggerGenerator->generateSwaggerUI($spec);

        file_put_contents($outputDir . '/index.html', $swaggerHTML);
    }

    protected function generatePostmanCollection($spec, $outputDir)
    {
        $this->info('üìÆ Generating Postman collection...');

        $postmanGenerator = new PostmanCollectionGenerator();
        $collection = $postmanGenerator->generateCollection($spec);

        file_put_contents(
            $outputDir . '/postman-collection.json',
            json_encode($collection, JSON_PRETTY_PRINT)
        );
    }

    protected function watchForChanges($outputDir)
    {
        $this->info('üëÄ Watching for changes... Press Ctrl+C to stop.');

        $lastChange = time();

        while (true) {
            $routeFiles = [
                base_path('routes/api.php'),
                app_path('Http/Controllers'),
                app_path('Http/Requests'),
                app_path('Http/Resources'),
            ];

            $latestChange = $this->getLatestFileChange($routeFiles);

            if ($latestChange > $lastChange) {
                $this->info('üìù Changes detected, regenerating documentation...');

                $generator = new OpenAPIGenerator();
                $openApiSpec = $generator->generate();
                $this->generateDocumentation($openApiSpec, 'all', $outputDir);

                $lastChange = time();
                $this->info('‚úÖ Documentation updated!');
            }

            sleep(2);
        }
    }

    protected function getLatestFileChange($paths)
    {
        $latest = 0;

        foreach ($paths as $path) {
            if (is_file($path)) {
                $latest = max($latest, filemtime($path));
            } elseif (is_dir($path)) {
                $files = new \RecursiveIteratorIterator(
                    new \RecursiveDirectoryIterator($path)
                );

                foreach ($files as $file) {
                    if ($file->isFile() && $file->getExtension() === 'php') {
                        $latest = max($latest, $file->getMTime());
                    }
                }
            }
        }

        return $latest;
    }

    protected function showGeneratedFiles($outputDir)
    {
        $this->info("\nüìÅ Generated files:");
        $this->line("   üìÑ OpenAPI Spec: {$outputDir}/openapi.json");
        $this->line("   üìÑ OpenAPI YAML: {$outputDir}/openapi.yaml");
        $this->line("   üåê Swagger UI: {$outputDir}/index.html");
        $this->line("   üìÆ Postman Collection: {$outputDir}/postman-collection.json");

        $baseUrl = config('app.url');
        $this->info("\nüîó Access your documentation:");
        $this->line("   üåê Interactive Docs: {$baseUrl}/docs/api");
        $this->line("   üìÑ OpenAPI JSON: {$baseUrl}/docs/api/openapi.json");
        $this->line("   üìÆ Postman Collection: {$baseUrl}/docs/api/postman-collection.json");
    }
}
