# Laravel Migration Safety Review

**Command**: `/laravel:migration-review [options]`

**Description**: Analyzes Laravel migrations for safety issues, potential data loss, performance impact, and provides rollback strategies before deployment.

## Usage

```bash
/laravel:migration-review --pending-only
/laravel:migration-review --production-check --data-loss-analysis
/laravel:migration-review --performance-impact --rollback-plan
/laravel:migration-review --generate-backup-script
```

## Options

- `--pending-only` - Review only pending migrations
- `--production-check` - Apply production-specific safety checks
- `--data-loss-analysis` - Analyze potential data loss scenarios
- `--performance-impact` - Estimate migration performance impact
- `--rollback-plan` - Generate comprehensive rollback strategies
- `--generate-backup-script` - Create pre-migration backup scripts
- `--parallel-execution` - Plan for parallel execution safety
- `--zero-downtime` - Analyze zero-downtime deployment compatibility

## Migration Analysis Categories

### 1. Data Loss Prevention Analysis

```php
<?php

namespace App\Database\Analysis;

use Illuminate\Database\Migrations\Migration;
use Illuminate\Support\Facades\DB;
use Illuminate\Support\Facades\Schema;

class DataLossAnalyzer
{
    protected $riskLevels = [
        'CRITICAL' => 'Immediate data loss risk',
        'HIGH' => 'Potential data loss under certain conditions',
        'MEDIUM' => 'Data modification with possible issues',
        'LOW' => 'Safe operation with minimal risk',
        'SAFE' => 'No data loss risk'
    ];

    public function analyzeMigrations($migrations)
    {
        $analysis = [];

        foreach ($migrations as $migration) {
            $analysis[] = $this->analyzeMigration($migration);
        }

        return [
            'migrations' => $analysis,
            'overall_risk' => $this->calculateOverallRisk($analysis),
            'recommendations' => $this->generateRecommendations($analysis),
        ];
    }

    protected function analyzeMigration($migrationFile)
    {
        $content = file_get_contents($migrationFile);
        $migrationName = basename($migrationFile, '.php');

        $risks = [];
        $warnings = [];
        $safeguards = [];

        // Analyze up() method
        $upMethod = $this->extractMethod($content, 'up');
        $risks = array_merge($risks, $this->analyzeUpMethod($upMethod));

        // Analyze down() method
        $downMethod = $this->extractMethod($content, 'down');
        $warnings = array_merge($warnings, $this->analyzeDownMethod($downMethod));

        // Check for safeguards
        $safeguards = $this->analyzeSafeguards($content);

        return [
            'file' => $migrationName,
            'path' => $migrationFile,
            'risk_level' => $this->calculateRiskLevel($risks),
            'risks' => $risks,
            'warnings' => $warnings,
            'safeguards' => $safeguards,
            'recommendations' => $this->generateMigrationRecommendations($risks, $warnings),
            'estimated_duration' => $this->estimateMigrationDuration($content),
            'rollback_complexity' => $this->assessRollbackComplexity($downMethod),
        ];
    }

    protected function analyzeUpMethod($upMethodContent)
    {
        $risks = [];

        // Check for column drops
        if (preg_match_all('/dropColumn\s*\(\s*[\'"]([^\'"]+)[\'"]/', $upMethodContent, $matches)) {
            foreach ($matches[1] as $column) {
                $risks[] = [
                    'type' => 'DATA_LOSS',
                    'severity' => 'CRITICAL',
                    'description' => "Dropping column '{$column}' will cause permanent data loss",
                    'recommendation' => "Consider renaming column instead of dropping, or ensure data is backed up",
                    'affected_column' => $column,
                ];
            }
        }

        // Check for table drops
        if (preg_match_all('/dropIfExists\s*\(\s*[\'"]([^\'"]+)[\'"]/', $upMethodContent, $matches)) {
            foreach ($matches[1] as $table) {
                $risks[] = [
                    'type' => 'DATA_LOSS',
                    'severity' => 'CRITICAL',
                    'description' => "Dropping table '{$table}' will cause permanent data loss",
                    'recommendation' => "Ensure table data is backed up or migrated before dropping",
                    'affected_table' => $table,
                ];
            }
        }

        // Check for data type changes that may cause data loss
        if (preg_match_all('/change\s*\(\s*\)\s*->\s*(\w+)\s*\(/', $upMethodContent, $matches)) {
            $risks[] = [
                'type' => 'DATA_TRUNCATION',
                'severity' => 'HIGH',
                'description' => 'Column type change may cause data truncation or loss',
                'recommendation' => 'Verify data compatibility before changing column types',
            ];
        }

        // Check for NOT NULL constraints on existing columns
        if (preg_match_all('/nullable\s*\(\s*false\s*\)/', $upMethodContent, $matches)) {
            $risks[] = [
                'type' => 'CONSTRAINT_VIOLATION',
                'severity' => 'HIGH',
                'description' => 'Adding NOT NULL constraint to existing column may fail if NULL values exist',
                'recommendation' => 'Update existing NULL values before adding constraint',
            ];
        }

        // Check for unique constraints
        if (preg_match_all('/unique\s*\(/', $upMethodContent, $matches)) {
            $risks[] = [
                'type' => 'CONSTRAINT_VIOLATION',
                'severity' => 'MEDIUM',
                'description' => 'Adding unique constraint may fail if duplicate values exist',
                'recommendation' => 'Check for duplicate values before adding unique constraint',
            ];
        }

        // Check for foreign key constraints
        if (preg_match_all('/foreign\s*\(\s*[\'"]([^\'"]+)[\'"]/', $upMethodContent, $matches)) {
            foreach ($matches[1] as $column) {
                $risks[] = [
                    'type' => 'CONSTRAINT_VIOLATION',
                    'severity' => 'MEDIUM',
                    'description' => "Adding foreign key constraint on '{$column}' may fail if orphaned records exist",
                    'recommendation' => 'Clean up orphaned records before adding foreign key constraint',
                    'affected_column' => $column,
                ];
            }
        }

        // Check for large table modifications
        $tableOperations = $this->extractTableOperations($upMethodContent);
        foreach ($tableOperations as $operation) {
            if ($this->isLargeTable($operation['table'])) {
                $risks[] = [
                    'type' => 'PERFORMANCE',
                    'severity' => 'MEDIUM',
                    'description' => "Operation on large table '{$operation['table']}' may cause downtime",
                    'recommendation' => 'Consider online schema change tools or maintenance window',
                    'affected_table' => $operation['table'],
                ];
            }
        }

        return $risks;
    }

    protected function analyzeDownMethod($downMethodContent)
    {
        $warnings = [];

        if (empty(trim($downMethodContent)) || trim($downMethodContent) === '//') {
            $warnings[] = [
                'type' => 'NO_ROLLBACK',
                'severity' => 'HIGH',
                'description' => 'Migration has no rollback implementation',
                'recommendation' => 'Implement proper rollback logic in down() method',
            ];
        }

        // Check if rollback will cause data loss
        if (preg_match('/dropColumn|dropIfExists/', $downMethodContent)) {
            $warnings[] = [
                'type' => 'ROLLBACK_DATA_LOSS',
                'severity' => 'CRITICAL',
                'description' => 'Rolling back this migration will cause data loss',
                'recommendation' => 'Ensure data backup strategy before migration',
            ];
        }

        // Check for irreversible operations
        if (preg_match('/DB::statement|DB::unprepared/', $downMethodContent)) {
            $warnings[] = [
                'type' => 'COMPLEX_ROLLBACK',
                'severity' => 'MEDIUM',
                'description' => 'Rollback contains raw SQL that may be difficult to reverse',
                'recommendation' => 'Verify rollback SQL statements thoroughly',
            ];
        }

        return $warnings;
    }

    protected function analyzeSafeguards($migrationContent)
    {
        $safeguards = [];

        // Check for backup operations
        if (strpos($migrationContent, 'backup') !== false || strpos($migrationContent, 'copy') !== false) {
            $safeguards[] = [
                'type' => 'BACKUP',
                'description' => 'Migration includes backup operations',
            ];
        }

        // Check for data validation
        if (preg_match('/if\s*\(\s*.*count|exists.*\)/', $migrationContent)) {
            $safeguards[] = [
                'type' => 'VALIDATION',
                'description' => 'Migration includes data validation checks',
            ];
        }

        // Check for transaction usage
        if (strpos($migrationContent, 'DB::transaction') !== false) {
            $safeguards[] = [
                'type' => 'TRANSACTION',
                'description' => 'Migration is wrapped in database transaction',
            ];
        }

        // Check for batch processing
        if (strpos($migrationContent, 'chunk') !== false || strpos($migrationContent, 'batch') !== false) {
            $safeguards[] = [
                'type' => 'BATCH_PROCESSING',
                'description' => 'Migration uses batch processing for large datasets',
            ];
        }

        return $safeguards;
    }

    protected function generateBackupScript($migrations)
    {
        $script = "#!/bin/bash\n\n";
        $script .= "# Pre-migration backup script\n";
        $script .= "# Generated on " . date('Y-m-d H:i:s') . "\n\n";

        $script .= "echo \"Starting pre-migration backup...\"\n\n";

        $affectedTables = $this->getAffectedTables($migrations);

        foreach ($affectedTables as $table) {
            $backupFile = "backup_{$table}_" . date('Y_m_d_H_i_s') . ".sql";
            $script .= "echo \"Backing up table {$table}...\"\n";
            $script .= "mysqldump --single-transaction {$this->getDatabaseName()} {$table} > {$backupFile}\n\n";
        }

        // Full database backup for critical migrations
        $hasCriticalRisks = $this->hasCriticalRisks($migrations);
        if ($hasCriticalRisks) {
            $fullBackupFile = "full_backup_" . date('Y_m_d_H_i_s') . ".sql";
            $script .= "echo \"Creating full database backup due to critical risks...\"\n";
            $script .= "mysqldump --single-transaction {$this->getDatabaseName()} > {$fullBackupFile}\n\n";
        }

        $script .= "echo \"Backup completed successfully!\"\n";

        return $script;
    }

    protected function generateRollbackPlan($migrations)
    {
        $plan = [
            'overview' => $this->generateRollbackOverview($migrations),
            'pre_rollback_checks' => $this->generatePreRollbackChecks($migrations),
            'rollback_steps' => $this->generateRollbackSteps($migrations),
            'post_rollback_verification' => $this->generatePostRollbackVerification($migrations),
            'emergency_procedures' => $this->generateEmergencyProcedures($migrations),
        ];

        return $plan;
    }

    protected function generateRollbackSteps($migrations)
    {
        $steps = [];

        // Reverse the order for rollback
        $reversedMigrations = array_reverse($migrations);

        foreach ($reversedMigrations as $migration) {
            $steps[] = [
                'step' => count($steps) + 1,
                'migration' => $migration['file'],
                'command' => "php artisan migrate:rollback --step=1",
                'verification' => $this->generateVerificationQueries($migration),
                'estimated_time' => $migration['estimated_duration'],
                'risk_level' => $migration['rollback_complexity'],
                'backup_required' => $this->requiresBackupForRollback($migration),
            ];
        }

        return $steps;
    }

    protected function generatePerformanceImpactAnalysis($migrations)
    {
        $analysis = [
            'overall_impact' => 'MEDIUM',
            'estimated_total_time' => 0,
            'peak_resource_usage' => [],
            'blocking_operations' => [],
            'recommendations' => [],
        ];

        foreach ($migrations as $migration) {
            $impact = $this->analyzeMigrationPerformance($migration);
            $analysis['estimated_total_time'] += $impact['duration'];

            if ($impact['blocks_reads'] || $impact['blocks_writes']) {
                $analysis['blocking_operations'][] = [
                    'migration' => $migration['file'],
                    'blocks_reads' => $impact['blocks_reads'],
                    'blocks_writes' => $impact['blocks_writes'],
                    'duration' => $impact['duration'],
                ];
            }

            $analysis['peak_resource_usage'][] = [
                'migration' => $migration['file'],
                'cpu_usage' => $impact['cpu_usage'],
                'memory_usage' => $impact['memory_usage'],
                'disk_io' => $impact['disk_io'],
            ];
        }

        // Generate recommendations based on analysis
        if ($analysis['estimated_total_time'] > 300) { // 5 minutes
            $analysis['recommendations'][] = [
                'type' => 'MAINTENANCE_WINDOW',
                'description' => 'Consider running migrations during maintenance window',
                'reason' => 'Total estimated time exceeds 5 minutes',
            ];
        }

        if (count($analysis['blocking_operations']) > 0) {
            $analysis['recommendations'][] = [
                'type' => 'ONLINE_SCHEMA_CHANGE',
                'description' => 'Consider using online schema change tools',
                'reason' => 'Migrations contain blocking operations',
            ];
        }

        return $analysis;
    }
}
