# Laravel Deployment Preparation

**Command**: `/laravel:deploy-prep [environment] [options]`

**Description**: Comprehensive deployment readiness validation, optimization, and preparation for Laravel applications across different environments.

## Usage

```bash
/laravel:deploy-prep production --full-check
/laravel:deploy-prep staging --performance-optimization
/laravel:deploy-prep production --security-hardening --generate-checklist
/laravel:deploy-prep production --zero-downtime --rollback-plan
```

## Options

- `--full-check` - Complete deployment readiness assessment
- `--performance-optimization` - Apply performance optimizations
- `--security-hardening` - Implement security best practices
- `--zero-downtime` - Prepare for zero-downtime deployment
- `--rollback-plan` - Generate comprehensive rollback strategy
- `--generate-checklist` - Create deployment checklist
- `--health-checks` - Set up application health monitoring
- `--config-validation` - Validate environment configuration

## Deployment Preparation Categories

### 1. Environment Configuration Validation

```php
<?php

namespace App\Deployment;

class EnvironmentValidator
{
    protected $requirements = [
        'php' => [
            'min_version' => '8.1.0',
            'required_extensions' => [
                'bcmath', 'ctype', 'fileinfo', 'json', 'mbstring', 
                'openssl', 'pdo', 'tokenizer', 'xml', 'curl', 'zip'
            ],
            'recommended_extensions' => [
                'redis', 'imagick', 'intl', 'gd', 'opcache'
            ],
        ],
        'database' => [
            'mysql' => '8.0.0',
            'postgresql' => '13.0',
            'redis' => '6.0.0',
        ],
        'server' => [
            'memory_limit' => '512M',
            'max_execution_time' => 300,
            'post_max_size' => '50M',
            'upload_max_filesize' => '50M',
        ],
    ];

    public function validateEnvironment($targetEnvironment)
    {
        $validation = [
            'environment' => $this->validateEnvironmentSettings($targetEnvironment),
            'php_requirements' => $this->validatePHPRequirements(),
            'database_requirements' => $this->validateDatabaseRequirements(),
            'server_configuration' => $this->validateServerConfiguration(),
            'security_settings' => $this->validateSecuritySettings($targetEnvironment),
            'performance_settings' => $this->validatePerformanceSettings(),
            'dependencies' => $this->validateDependencies(),
        ];

        return [
            'validation_results' => $validation,
            'overall_status' => $this->calculateOverallStatus($validation),
            'critical_issues' => $this->extractCriticalIssues($validation),
            'recommendations' => $this->generateEnvironmentRecommendations($validation),
        ];
    }

    protected function validateEnvironmentSettings($targetEnvironment)
    {
        $issues = [];

        // Check APP_ENV
        $currentEnv = config('app.env');
        if ($currentEnv !== $targetEnvironment) {
            $issues[] = [
                'type' => 'ENVIRONMENT_MISMATCH',
                'severity' => 'CRITICAL',
                'current' => $currentEnv,
                'expected' => $targetEnvironment,
                'message' => "APP_ENV is '{$currentEnv}' but deploying to '{$targetEnvironment}'",
            ];
        }

        // Check APP_DEBUG
        if ($targetEnvironment === 'production' && config('app.debug')) {
            $issues[] = [
                'type' => 'DEBUG_ENABLED',
                'severity' => 'CRITICAL',
                'message' => 'APP_DEBUG must be false in production',
                'recommendation' => 'Set APP_DEBUG=false in production .env',
            ];
        }

        // Check APP_KEY
        if (!config('app.key')) {
            $issues[] = [
                'type' => 'MISSING_APP_KEY',
                'severity' => 'CRITICAL',
                'message' => 'APP_KEY is not set',
                'recommendation' => 'Run php artisan key:generate',
            ];
        }

        // Validate required environment variables
        $requiredVars = $this->getRequiredEnvironmentVariables($targetEnvironment);
        foreach ($requiredVars as $var => $description) {
            if (!env($var)) {
                $issues[] = [
                    'type' => 'MISSING_ENV_VAR',
                    'severity' => 'HIGH',
                    'variable' => $var,
                    'message' => "Required environment variable {$var} is not set",
                    'description' => $description,
                ];
            }
        }

        return $issues;
    }

    protected function validatePHPRequirements()
    {
        $issues = [];

        // Check PHP version
        $currentVersion = PHP_VERSION;
        $minVersion = $this->requirements['php']['min_version'];

        if (version_compare($currentVersion, $minVersion, '<')) {
            $issues[] = [
                'type' => 'PHP_VERSION',
                'severity' => 'CRITICAL',
                'current' => $currentVersion,
                'required' => $minVersion,
                'message' => "PHP version {$currentVersion} is below minimum required {$minVersion}",
            ];
        }

        // Check required extensions
        foreach ($this->requirements['php']['required_extensions'] as $extension) {
            if (!extension_loaded($extension)) {
                $issues[] = [
                    'type' => 'MISSING_EXTENSION',
                    'severity' => 'CRITICAL',
                    'extension' => $extension,
                    'message' => "Required PHP extension '{$extension}' is not loaded",
                ];
            }
        }

        // Check recommended extensions
        foreach ($this->requirements['php']['recommended_extensions'] as $extension) {
            if (!extension_loaded($extension)) {
                $issues[] = [
                    'type' => 'MISSING_RECOMMENDED_EXTENSION',
                    'severity' => 'MEDIUM',
                    'extension' => $extension,
                    'message' => "Recommended PHP extension '{$extension}' is not loaded",
                ];
            }
        }

        // Check PHP configuration
        $phpConfig = [
            'memory_limit' => ini_get('memory_limit'),
            'max_execution_time' => ini_get('max_execution_time'),
            'post_max_size' => ini_get('post_max_size'),
            'upload_max_filesize' => ini_get('upload_max_filesize'),
            'opcache.enable' => ini_get('opcache.enable'),
        ];

        foreach ($phpConfig as $setting => $value) {
            $recommendation = $this->validatePHPSetting($setting, $value);
            if ($recommendation) {
                $issues[] = $recommendation;
            }
        }

        return $issues;
    }

    protected function validateDatabaseRequirements()
    {
        $issues = [];

        try {
            // Test database connection
            DB::connection()->getPdo();

            // Check database version
            $dbConfig = config('database.connections.' . config('database.default'));
            $driver = $dbConfig['driver'];

            switch ($driver) {
                case 'mysql':
                    $version = DB::select('SELECT VERSION() as version')[0]->version;
                    $minVersion = $this->requirements['database']['mysql'];

                    if (version_compare($version, $minVersion, '<')) {
                        $issues[] = [
                            'type' => 'DATABASE_VERSION',
                            'severity' => 'HIGH',
                            'driver' => $driver,
                            'current' => $version,
                            'required' => $minVersion,
                        ];
                    }
                    break;

                case 'pgsql':
                    $version = DB::select('SELECT version()')[0]->version;
                    $versionNumber = preg_match('/PostgreSQL (\d+\.\d+)/', $version, $matches) ? $matches[1] : '0';
                    $minVersion = $this->requirements['database']['postgresql'];

                    if (version_compare($versionNumber, $minVersion, '<')) {
                        $issues[] = [
                            'type' => 'DATABASE_VERSION',
                            'severity' => 'HIGH',
                            'driver' => $driver,
                            'current' => $versionNumber,
                            'required' => $minVersion,
                        ];
                    }
                    break;
            }

            // Test Redis connection if configured
            if (config('cache.default') === 'redis' || config('session.driver') === 'redis') {
                $this->validateRedisConnection($issues);
            }

        } catch (\Exception $e) {
            $issues[] = [
                'type' => 'DATABASE_CONNECTION',
                'severity' => 'CRITICAL',
                'message' => 'Cannot connect to database: ' . $e->getMessage(),
            ];
        }

        return $issues;
    }

    protected function validateSecuritySettings($targetEnvironment)
    {
        $issues = [];

        if ($targetEnvironment === 'production') {
            // HTTPS enforcement
            if (!config('app.url') || !str_starts_with(config('app.url'), 'https://')) {
                $issues[] = [
                    'type' => 'HTTPS_NOT_ENFORCED',
                    'severity' => 'HIGH',
                    'message' => 'APP_URL should use HTTPS in production',
                ];
            }

            // Session security
            if (!config('session.secure')) {
                $issues[] = [
                    'type' => 'INSECURE_SESSION',
                    'severity' => 'HIGH',
                    'message' => 'SESSION_SECURE_COOKIE should be true in production',
                ];
            }

            if (!config('session.http_only')) {
                $issues[] = [
                    'type' => 'SESSION_XSS_RISK',
                    'severity' => 'MEDIUM',
                    'message' => 'SESSION_HTTP_ONLY should be true to prevent XSS',
                ];
            }

            // Check for sensitive data exposure
            if ($this->checkSensitiveDataExposure()) {
                $issues[] = [
                    'type' => 'SENSITIVE_DATA_EXPOSURE',
                    'severity' => 'CRITICAL',
                    'message' => 'Sensitive files detected in public directory',
                ];
            }
        }

        return $issues;
    }

    protected function validatePerformanceSettings()
    {
        $issues = [];

        // Check OPcache
        if (!ini_get('opcache.enable')) {
            $issues[] = [
                'type' => 'OPCACHE_DISABLED',
                'severity' => 'MEDIUM',
                'message' => 'OPcache is not enabled',
                'recommendation' => 'Enable OPcache for better performance',
            ];
        }

        // Check cache configuration
        $cacheDriver = config('cache.default');
        if (in_array($cacheDriver, ['file', 'array'])) {
            $issues[] = [
                'type' => 'SUBOPTIMAL_CACHE',
                'severity' => 'MEDIUM',
                'current' => $cacheDriver,
                'message' => "Cache driver '{$cacheDriver}' is not optimal for production",
                'recommendation' => 'Use Redis or Memcached for production caching',
            ];
        }

        // Check session driver
        $sessionDriver = config('session.driver');
        if ($sessionDriver === 'file') {
            $issues[] = [
                'type' => 'SUBOPTIMAL_SESSION',
                'severity' => 'LOW',
                'current' => $sessionDriver,
                'message' => "Session driver '{$sessionDriver}' may not scale well",
                'recommendation' => 'Consider using database or Redis for session storage',
            ];
        }

        return $issues;
    }
}
```

### 2. Performance Optimization

```php
<?php

namespace App\Deployment;

class PerformanceOptimizer
{
    public function optimizeForDeployment($environment)
    {
        $optimizations = [
            'config_caching' => $this->optimizeConfigCaching(),
            'route_caching' => $this->optimizeRouteCaching(),
            'view_caching' => $this->optimizeViewCaching(),
            'autoloader_optimization' => $this->optimizeAutoloader(),
            'asset_optimization' => $this->optimizeAssets(),
            'database_optimization' => $this->optimizeDatabaseConnections(),
            'opcache_optimization' => $this->optimizeOPCache(),
        ];

        return [
            'optimizations' => $optimizations,
            'performance_impact' => $this->calculatePerformanceImpact($optimizations),
            'monitoring_setup' => $this->setupPerformanceMonitoring(),
        ];
    }

    protected function optimizeConfigCaching()
    {
        $results = [];

        // Clear existing config cache
        Artisan::call('config:clear');
        $results[] = 'Config cache cleared';

        // Cache configuration
        Artisan::call('config:cache');
        $results[] = 'Configuration cached';

        // Verify cache exists
        if (file_exists(bootstrap_path('cache/config.php'))) {
            $results[] = 'Config cache file verified';
        } else {
            $results[] = 'WARNING: Config cache file not found';
        }

        return [
            'status' => 'completed',
            'actions' => $results,
            'performance_gain' => '15-25% faster config access',
        ];
    }

    protected function optimizeRouteCaching()
    {
        $results = [];

        // Check for route caching compatibility
        if ($this->hasClosureRoutes()) {
            return [
                'status' => 'skipped',
                'reason' => 'Closure routes detected - cannot cache routes',
                'recommendation' => 'Convert closure routes to controller methods',
            ];
        }

        // Clear existing route cache
        Artisan::call('route:clear');
        $results[] = 'Route cache cleared';

        // Cache routes
        Artisan::call('route:cache');
        $results[] = 'Routes cached';

        // Verify cache exists
        if (file_exists(bootstrap_path('cache/routes-v7.php'))) {
            $results[] = 'Route cache file verified';
        } else {
            $results[] = 'WARNING: Route cache file not found';
        }

        return [
            'status' => 'completed',
            'actions' => $results,
            'performance_gain' => '30-50% faster route resolution',
        ];
    }

    protected function optimizeViewCaching()
    {
        $results = [];

        // Clear existing view cache
        Artisan::call('view:clear');
        $results[] = 'View cache cleared';

        // Pre-compile views
        Artisan::call('view:cache');
        $results[] = 'Views compiled and cached';

        // Count cached views
        $viewCacheDir = storage_path('framework/views');
        $cachedViewCount = count(glob($viewCacheDir . '/*.php'));
        $results[] = "Cached {$cachedViewCount} view files";

        return [
            'status' => 'completed',
            'actions' => $results,
            'cached_views' => $cachedViewCount,
            'performance_gain' => '20-30% faster view rendering',
        ];
    }

    protected function optimizeAutoloader()
    {
        $results = [];

        // Optimize Composer autoloader
        $command = 'composer dump-autoload --optimize --no-dev';
        $output = shell_exec($command . ' 2>&1');
        $results[] = 'Composer autoloader optimized';

        // Check if autoloader files exist
        $autoloadFiles = [
            base_path('vendor/composer/autoload_classmap.php'),
            base_path('vendor/composer/autoload_static.php'),
        ];

        foreach ($autoloadFiles as $file) {
            if (file_exists($file)) {
                $results[] = 'Autoloader file: ' . basename($file) . ' - OK';
            } else {
                $results[] = 'WARNING: Missing autoloader file: ' . basename($file);
            }
        }

        return [
            'status' => 'completed',
            'actions' => $results,
            'performance_gain' => '10-15% faster class loading',
        ];
    }

    protected function optimizeAssets()
    {
        $results = [];

        // Check if Laravel Mix/Vite is configured
        $packageJson = base_path('package.json');
        if (!file_exists($packageJson)) {
            return [
                'status' => 'skipped',
                'reason' => 'No package.json found',
            ];
        }

        $packageData = json_decode(file_get_contents($packageJson), true);

        // Check for build scripts
        if (isset($packageData['scripts']['build'])) {
            // Run production build
            $buildCommand = 'npm run build';
            $output = shell_exec($buildCommand . ' 2>&1');
            $results[] = 'Assets compiled for production';

            // Check for generated manifest
            $manifestFiles = [
                public_path('build/manifest.json'), // Vite
                public_path('mix-manifest.json'),   // Laravel Mix
            ];

            foreach ($manifestFiles as $manifest) {
                if (file_exists($manifest)) {
                    $results[] = 'Asset manifest found: ' . basename($manifest);
                    break;
                }
            }
        }

        // Analyze asset sizes
        $assetAnalysis = $this->analyzeAssetSizes();
        $results[] = "Total asset size: {$assetAnalysis['total_size']}";

        return [
            'status' => 'completed',
            'actions' => $results,
            'asset_analysis' => $assetAnalysis,
        ];
    }

    protected function setupPerformanceMonitoring()
    {
        $monitoring = [
            'application_metrics' => $this->setupApplicationMetrics(),
            'database_monitoring' => $this->setupDatabaseMonitoring(),
            'cache_monitoring' => $this->setupCacheMonitoring(),
            'health_checks' => $this->setupHealthChecks(),
        ];

        return $monitoring;
    }

    protected function setupHealthChecks()
    {
        $healthChecks = [
            'database' => [
                'endpoint' => '/health/database',
                'check' => 'SELECT 1',
                'timeout' => 5,
            ],
            'cache' => [
                'endpoint' => '/health/cache',
                'check' => 'cache connection test',
                'timeout' => 3,
            ],
            'storage' => [
                'endpoint' => '/health/storage',
                'check' => 'filesystem write test',
                'timeout' => 3,
            ],
            'external_services' => [
                'endpoint' => '/health/external',
                'check' => 'external API connectivity',
                'timeout' => 10,
            ],
        ];

        // Create health check routes
        $this->generateHealthCheckRoutes($healthChecks);

        return [
            'checks' => $healthChecks,
            'monitoring_endpoint' => config('app.url') . '/health',
            'alert_thresholds' => $this->getHealthCheckThresholds(),
        ];
    }
}
