    protected function generateDeploymentChecklist($environment, $deploymentType)
    {
        $baseItems = [
            [
                'category' => 'Application Deployment',
                'tasks' => [
                    ['task' => 'Put application in maintenance mode', 'owner' => 'DevOps', 'duration' => '1 min'],
                    ['task' => 'Deploy code to servers', 'owner' => 'DevOps', 'duration' => '5 min'],
