{
  "name": "API Endpoint Creation",
  "description": "Complete workflow for creating RESTful API endpoints with proper validation, resources, and tests",
  "type": "workflow",
  "category": "api",
  "steps": [
    {
      "name": "Generate Model",
      "description": "Create Eloquent model with relationships and attributes",
      "command": "php artisan make:model {ModelName} -m",
      "template": "model-with-relationships",
      "files": [
        "app/Models/{ModelName}.php",
        "database/migrations/create_{table_name}_table.php"
      ]
    },
    {
      "name": "Generate API Controller",
      "description": "Create API controller with CRUD operations",
      "command": "php artisan make:controller Api/{ModelName}Controller --api",
      "template": "controller-api",
      "files": [
        "app/Http/Controllers/Api/{ModelName}Controller.php"
      ]
    },
    {
      "name": "Generate API Resource",
      "description": "Create API resource for data transformation",
      "command": "php artisan make:resource {ModelName}Resource",
      "template": "api-resource",
      "files": [
        "app/Http/Resources/{ModelName}Resource.php"
      ]
    },
    {
      "name": "Generate Form Request",
      "description": "Create form request for validation",
      "command": "php artisan make:request {ModelName}Request",
      "template": "form-request",
      "files": [
        "app/Http/Requests/{ModelName}Request.php"
      ]
    },
    {
      "name": "Generate Factory",
      "description": "Create model factory for testing",
      "command": "php artisan make:factory {ModelName}Factory",
      "template": "factory",
      "files": [
        "database/factories/{ModelName}Factory.php"
      ]
    },
    {
      "name": "Generate API Tests",
      "description": "Create comprehensive API tests",
      "command": "php artisan make:test {ModelName}ApiTest",
      "template": "api-test",
      "files": [
        "tests/Feature/{ModelName}ApiTest.php"
      ]
    },
    {
      "name": "Add API Routes",
      "description": "Register API routes with proper middleware",
      "action": "append_to_file",
      "file": "routes/api.php",
      "content": "Route::apiResource('{route_name}', Api\\{ModelName}Controller::class)->middleware('auth:sanctum');"
    },
    {
      "name": "Run Migration",
      "description": "Execute database migration",
      "command": "php artisan migrate",
      "validation": "database_table_exists"
    },
    {
      "name": "Run Tests",
      "description": "Execute API tests to ensure functionality",
      "command": "php artisan test --filter={ModelName}ApiTest",
      "validation": "tests_pass"
    }
  ],
  "validations": [
    {
      "name": "model_exists",
      "description": "Verify model file exists and is valid",
      "check": "file_exists:app/Models/{ModelName}.php"
    },
    {
      "name": "routes_registered",
      "description": "Verify routes are properly registered",
      "check": "routes_contain:{route_name}"
    },
    {
      "name": "tests_pass",
      "description": "All API tests pass successfully",
      "check": "phpunit_success"
    }
  ],
  "cleanup": [
    {
      "name": "optimize_autoloader",
      "command": "composer dump-autoload -o"
    },
    {
      "name": "clear_caches",
      "command": "php artisan optimize:clear"
    }
  ]
}

