{
  "name": "Livewire Component Creation",
  "description": "Complete workflow for creating interactive Livewire components with real-time features",
  "type": "workflow",
  "category": "frontend",
  "steps": [
    {
      "name": "Generate Livewire Component",
      "description": "Create Livewire component class and view",
      "command": "php artisan make:livewire {ComponentName}",
      "template": "livewire-component",
      "files": [
        "app/Http/Livewire/{ComponentName}.php",
        "resources/views/livewire/{component-name}.blade.php"
      ]
    },
    {
      "name": "Generate Model (if needed)",
      "description": "Create associated Eloquent model",
      "command": "php artisan make:model {ModelName} -m",
      "template": "model-with-relationships",
      "files": [
        "app/Models/{ModelName}.php",
        "database/migrations/create_{table_name}_table.php"
      ],
      "conditional": true
    },
    {
      "name": "Generate Policy",
      "description": "Create authorization policy for the model",
      "command": "php artisan make:policy {ModelName}Policy",
      "template": "policy",
      "files": [
        "app/Policies/{ModelName}Policy.php"
      ]
    },
    {
      "name": "Create Blade View",
      "description": "Create comprehensive Blade template with Alpine.js integration",
      "template": "livewire-blade-view",
      "files": [
        "resources/views/livewire/{component-name}.blade.php"
      ]
    },
    {
      "name": "Generate Factory",
      "description": "Create model factory for testing",
      "command": "php artisan make:factory {ModelName}Factory",
      "template": "factory",
      "files": [
        "database/factories/{ModelName}Factory.php"
      ]
    },
    {
      "name": "Generate Component Tests",
      "description": "Create Livewire component tests",
      "command": "php artisan make:test {ComponentName}Test",
      "template": "livewire-test",
      "files": [
        "tests/Feature/{ComponentName}Test.php"
      ]
    },
    {
      "name": "Add Routes",
      "description": "Register web routes for the component",
      "action": "append_to_file",
      "file": "routes/web.php",
      "content": "Route::get('/{route_name}', {ComponentName}::class)->middleware('auth')->name('{route_name}');"
    },
    {
      "name": "Compile Assets",
      "description": "Compile frontend assets with Vite",
      "command": "npm run build",
      "validation": "assets_compiled"
    },
    {
      "name": "Run Tests",
      "description": "Execute component tests",
      "command": "php artisan test --filter={ComponentName}Test",
      "validation": "tests_pass"
    }
  ],
  "validations": [
    {
      "name": "component_exists",
      "description": "Verify Livewire component exists and is registered",
      "check": "livewire_component_exists:{ComponentName}"
    },
    {
      "name": "view_exists",
      "description": "Verify Blade view exists and is syntactically correct",
      "check": "blade_view_valid:livewire.{component-name}"
    },
    {
      "name": "tests_pass",
      "description": "All component tests pass successfully",
      "check": "phpunit_success"
    }
  ],
  "cleanup": [
    {
      "name": "clear_view_cache",
      "command": "php artisan view:clear"
    },
    {
      "name": "clear_livewire_cache",
      "command": "php artisan livewire:discover"
    }
  ]
}

