input.content:
{
  "name": "Deployment Preparation",
  "description": "Complete checklist and automation for preparing Laravel applications for production deployment",
  "type": "workflow",
  "category": "deployment",
  "steps": [
    {
      "name": "Environment Validation",
      "description": "Validate all environment configurations",
      "actions": [
        {
          "name": "Check Environment File",
          "command": "php artisan config:show",
          "validation": "env_file_valid"
        },
        {
          "name": "Validate Database Connection",
          "command": "php artisan migrate:status",
          "validation": "database_accessible"
        },
        {
          "name": "Check Queue Configuration",
          "command": "php artisan queue:monitor",
          "validation": "queue_configured"
        }
      ]
    },
    {
      "name": "Security Hardening",
      "description": "Apply security best practices",
      "actions": [
        {
          "name": "Generate Application Key",
          "command": "php artisan key:generate --force",
          "validation": "app_key_exists"
        },
        {
          "name": "Clear Debug Mode",
          "action": "env_update",
          "key": "APP_DEBUG",
          "value": "false"
        },
        {
          "name": "Set Production Environment",
          "action": "env_update",
          "key": "APP_ENV",
          "value": "production"
        },
        {
          "name": "Configure Session Security",
          "action": "env_update",
          "key": "SESSION_SECURE_COOKIE",
          "value": "true"
        }
      ]
    },
    {
      "name": "Performance Optimization",
      "description": "Optimize application for production performance",
      "actions": [
        {
          "name": "Optimize Autoloader",
          "command": "composer install --optimize-autoloader --no-dev"
        },
        {
          "name": "Cache Configuration",
          "command": "php artisan config:cache"
        },
        {
          "name": "Cache Routes",
          "command": "php artisan route:cache"
        },
        {
          "name": "Cache Views",
          "command": "php artisan view:cache"
        },
        {
          "name": "Cache Events",
          "command": "php artisan event:cache"
        }
      ]
    },
    {
      "name": "Database Preparation",
      "description": "Prepare database for production",
      "actions": [
        {
          "name": "Run Migrations",
          "command": "php artisan migrate --force",
          "validation": "migrations_current"
        },
        {
          "name": "Seed Production Data",
          "command": "php artisan db:seed --class=ProductionSeeder",
          "conditional": true
        }
      ]
    },
    {
      "name": "Asset Compilation",
      "description": "Build and optimize frontend assets",
      "actions": [
        {
          "name": "Install Node Dependencies",
          "command": "npm ci --production"
        },
        {
          "name": "Build Production Assets",
          "command": "npm run build"
        },
        {
          "name": "Optimize Images",
          "command": "npm run optimize-images",
          "conditional": true
        }
      ]
    },
    {
      "name": "Testing & Validation",
      "description": "Run comprehensive tests before deployment",
      "actions": [
        {
          "name": "Run Unit Tests",
          "command": "php artisan test --testsuite=Unit",
          "validation": "unit_tests_pass"
        },
        {
          "name": "Run Feature Tests",
          "command": "php artisan test --testsuite=Feature",
          "validation": "feature_tests_pass"
        },
        {
          "name": "Run Security Scan",
          "command": "php artisan security:scan",
          "validation": "security_scan_pass"
        },
        {
          "name": "Performance Benchmark",
          "command": "php artisan performance:benchmark",
          "validation": "performance_acceptable"
        }
      ]
    },
    {
      "name": "Health Checks",
      "description": "Verify application health and readiness",
      "actions": [
        {
          "name": "Application Health Check",
          "command": "php artisan health:check",
          "validation": "health_check_pass"
        },
        {
          "name": "Queue Health Check",
          "command": "php artisan queue:health",
          "validation": "queue_healthy"
        },
        {
          "name": "Cache Health Check",
          "command": "php artisan cache:health",
          "validation": "cache_healthy"
        }
      ]
    }
  ],
  "validations": [
    {
      "name": "env_file_valid",
      "description": "Environment file contains all required variables",
      "check": "env_variables_complete"
    },
    {
      "name": "database_accessible",
      "description": "Database connection is working properly",
      "check": "database_connection_success"
    },
    {
      "name": "all_tests_pass",
      "description": "All test suites pass successfully",
      "check": "phpunit_exit_code_zero"
    },
    {
      "name": "security_scan_clean",
      "description": "No security vulnerabilities detected",
      "check": "security_scan_no_issues"
    },
    {
      "name": "performance_acceptable",
      "description": "Application meets performance benchmarks",
      "check": "response_time_under_threshold"
    }
  ],
  "rollback": [
    {
      "name": "Clear All Caches",
      "command": "php artisan optimize:clear"
    },
    {
      "name": "Restore Development Config",
      "action": "env_restore_backup"
    }
  ],
  "post_deployment": [
    {
      "name": "Queue Workers",
      "description": "Start queue workers for background job processing",
      "command": "php artisan queue:work --daemon"
    },
    {
      "name": "Schedule Runner",
      "description": "Ensure task scheduler is running",
      "command": "php artisan schedule:run"
    },
    {
      "name": "Log Monitoring",
      "description": "Set up log monitoring and alerting",
      "action": "configure_log_monitoring"
    }
  ]
}

