#!/usr/bin/env node

/**
 * Laravel Template Manager for Claude Code
 * 
 * Manages code templates, project types, and workflow automation
 * for Laravel development with intelligent context awareness.
 */

const fs = require('fs').promises;
const path = require('path');

class LaravelTemplateManager {
    constructor() {
        this.templatesDir = path.join(__dirname, '..', 'templates');
        this.projectTypes = {};
        this.codeTemplates = {};
        this.workflows = {};
    }

    /**
     * Initialize the template manager
     */
    async initialize() {
        try {
            await this.loadProjectTypes();
            await this.loadCodeTemplates();
            await this.loadWorkflows();
            console.log(' Laravel Template Manager initialized successfully');
        } catch (error) {
            console.error(' Failed to initialize Template Manager:', error.message);
            throw error;
        }
    }

    /**
     * Load all project type configurations
     */
    async loadProjectTypes() {
        const projectTypesDir = path.join(this.templatesDir, 'project-types');

        try {
            const files = await fs.readdir(projectTypesDir);

            for (const file of files) {
                if (file.endsWith('.json')) {
                    const filePath = path.join(projectTypesDir, file);
                    const content = await fs.readFile(filePath, 'utf8');
                    const config = JSON.parse(content);

                    this.projectTypes[config.type] = config;
                }
            }

            console.log(` Loaded ${Object.keys(this.projectTypes).length} project types`);
        } catch (error) {
            console.error('Failed to load project types:', error.message);
        }
    }

    /**
     * Load all code templates
     */
    async loadCodeTemplates() {
        const codeTemplatesDir = path.join(this.templatesDir, 'code');

        try {
            const files = await fs.readdir(codeTemplatesDir);

            for (const file of files) {
                if (file.endsWith('.stub')) {
                    const templateName = file.replace('.stub', '');
                    const filePath = path.join(codeTemplatesDir, file);
                    const content = await fs.readFile(filePath, 'utf8');

                    this.codeTemplates[templateName] = content;
                }
            }

            console.log(` Loaded ${Object.keys(this.codeTemplates).length} code templates`);
        } catch (error) {
            console.error('Failed to load code templates:', error.message);
        }
    }

    /**
     * Load all workflow configurations
     */
    async loadWorkflows() {
        const workflowsDir = path.join(this.templatesDir, 'workflows');

        try {
            const files = await fs.readdir(workflowsDir);

            for (const file of files) {
                if (file.endsWith('.json')) {
                    const filePath = path.join(workflowsDir, file);
                    const content = await fs.readFile(filePath, 'utf8');
                    const workflow = JSON.parse(content);

                    this.workflows[workflow.name.toLowerCase().replace(/\s+/g, '-')] = workflow;
                }
            }

            console.log(` Loaded ${Object.keys(this.workflows).length} workflows`);
        } catch (error) {
            console.error('Failed to load workflows:', error.message);
        }
    }

    /**
     * Get project type configuration
     */
    getProjectType(type) {
        return this.projectTypes[type] || null;
    }

    /**
     * Get available project types
     */
    getAvailableProjectTypes() {
        return Object.keys(this.projectTypes).map(type => ({
            type,
            name: this.projectTypes[type].name,
            description: this.projectTypes[type].description
        }));
    }

    /**
     * Generate code from template
     */
    generateCode(templateName, variables = {}) {
        const template = this.codeTemplates[templateName];
        if (!template) {
            throw new Error(`Template '${templateName}' not found`);
        }

        let code = template;

        // Replace template variables
        for (const [key, value] of Object.entries(variables)) {
            const regex = new RegExp(`\\{\\{\\s*${key}\\s*\\}\\}`, 'g');
            code = code.replace(regex, value);
        }

        return code;
    }

    /**
     * Get workflow by name
     */
    getWorkflow(workflowName) {
        return this.workflows[workflowName] || null;
    }

    /**
     * Get recommended templates for project type
     */
    getRecommendedTemplates(projectType) {
        const config = this.getProjectType(projectType);
        return config ? config.templates || [] : [];
    }

    /**
     * Get recommended workflows for project type
     */
    getRecommendedWorkflows(projectType) {
        const config = this.getProjectType(projectType);
        return config ? config.workflows || [] : [];
    }

    /**
     * Validate template variables
     */
    validateTemplateVariables(templateName, variables) {
        const template = this.codeTemplates[templateName];
        if (!template) {
            return { valid: false, error: 'Template not found' };
        }

        // Extract required variables from template
        const requiredVars = [...template.matchAll(/\{\{\s*(\w+)\s*\}\}/g)]
            .map(match => match[1])
            .filter((v, i, arr) => arr.indexOf(v) === i); // unique

        const missingVars = requiredVars.filter(varName => 
            !(varName in variables) || variables[varName] === ''
        );

        if (missingVars.length > 0) {
            return {
                valid: false,
                error: `Missing required variables: ${missingVars.join(', ')}`
            };
        }

        return { valid: true };
    }

    /**
     * Generate project structure for given type
     */
    async generateProjectStructure(projectType, projectPath) {
        const config = this.getProjectType(projectType);
        if (!config) {
            throw new Error(`Project type '${projectType}' not found`);
        }

        const structure = config.structure || {};

        for (const [dirPath, description] of Object.entries(structure)) {
            const fullPath = path.join(projectPath, dirPath);

            try {
                await fs.mkdir(fullPath, { recursive: true });

                // Create .gitkeep for empty directories
                const gitkeepPath = path.join(fullPath, '.gitkeep');
                await fs.writeFile(gitkeepPath, `# ${description}\n`);

                console.log(` Created directory: ${dirPath}`);
            } catch (error) {
                console.error(`Failed to create directory ${dirPath}:`, error.message);
            }
        }
    }

    /**
     * Get template suggestions based on context
     */
    getTemplateSuggestions(context) {
        const suggestions = [];

        // Analyze context to suggest appropriate templates
        if (context.includes('api') || context.includes('rest')) {
            suggestions.push('controller-api', 'api-resource', 'form-request');
        }

        if (context.includes('livewire') || context.includes('interactive')) {
            suggestions.push('livewire-component');
        }

        if (context.includes('model') || context.includes('database')) {
            suggestions.push('model-with-relationships');
        }

        if (context.includes('test') || context.includes('testing')) {
            suggestions.push('api-test', 'livewire-test');
        }

        return suggestions.filter(template => template in this.codeTemplates);
    }

    /**
     * Export configuration summary
     */
    exportSummary() {
        return {
            projectTypes: Object.keys(this.projectTypes).length,
            codeTemplates: Object.keys(this.codeTemplates).length,
            workflows: Object.keys(this.workflows).length,
            availableProjectTypes: this.getAvailableProjectTypes(),
            availableTemplates: Object.keys(this.codeTemplates),
            availableWorkflows: Object.keys(this.workflows)
        };
    }
}

// Export for use in other modules
if (typeof module !== 'undefined' && module.exports) {
    module.exports = LaravelTemplateManager;
}

// CLI functionality when run directly
if (require.main === module) {
    const manager = new LaravelTemplateManager();

    const command = process.argv[2];
    const args = process.argv.slice(3);

    (async () => {
        await manager.initialize();

        switch (command) {
            case 'list-types':
                console.log('\n Available Project Types:');
                manager.getAvailableProjectTypes().forEach(type => {
                    console.log(`   ${type.name} (${type.type})`);
                    console.log(`    ${type.description}`);
                });
                break;

            case 'list-templates':
                console.log('\n Available Code Templates:');
                Object.keys(manager.codeTemplates).forEach(template => {
                    console.log(`   ${template}`);
                });
                break;

            case 'list-workflows':
                console.log('\n Available Workflows:');
                Object.keys(manager.workflows).forEach(workflow => {
                    const config = manager.workflows[workflow];
                    console.log(`   ${config.name}`);
                    console.log(`    ${config.description}`);
                });
                break;

            case 'summary':
                const summary = manager.exportSummary();
                console.log('\n Template Manager Summary:');
                console.log(`  Project Types: ${summary.projectTypes}`);
                console.log(`  Code Templates: ${summary.codeTemplates}`);
                console.log(`  Workflows: ${summary.workflows}`);
                break;

            default:
                console.log(`
Laravel Template Manager

Usage:
  node template-manager.js <command>

Commands:
  list-types     List available project types
  list-templates List available code templates  
  list-workflows List available workflows
  summary        Show summary statistics

Example:
  node template-manager.js summary
                `);
        }
    })().catch(console.error);
}

