#!/usr/bin/env node

/**
 * Laravel Team Collaboration Manager for Claude Code
 * 
 * Manages team settings, synchronization, and collaboration features
 * while maintaining individual developer preferences and security.
 */

const fs = require('fs').promises;
const path = require('path');
const crypto = require('crypto');

class LaravelTeamManager {
    constructor() {
        this.configDir = path.join(__dirname, '..', 'config');
        this.sharedConfigPath = path.join(this.configDir, 'shared.json');
        this.localConfigPath = path.join(this.configDir, 'local.json');
        this.teamConfigPath = path.join(this.configDir, 'team-collaboration.json');

        this.sharedConfig = {};
        this.localConfig = {};
        this.teamConfig = {};
    }

    /**
     * Initialize the team manager
     */
    async initialize() {
        try {
            await this.loadConfigurations();
            console.log(' Laravel Team Manager initialized successfully');
        } catch (error) {
            console.error(' Failed to initialize Team Manager:', error.message);
            throw error;
        }
    }

    /**
     * Load all configuration files
     */
    async loadConfigurations() {
        try {
            // Load team collaboration config
            const teamConfigContent = await fs.readFile(this.teamConfigPath, 'utf8');
            this.teamConfig = JSON.parse(teamConfigContent);

            // Load shared config if exists
            try {
                const sharedConfigContent = await fs.readFile(this.sharedConfigPath, 'utf8');
                this.sharedConfig = JSON.parse(sharedConfigContent);
            } catch (error) {
                console.log(' No shared config found, will create default');
                this.sharedConfig = this.createDefaultSharedConfig();
            }

            // Load local config if exists
            try {
                const localConfigContent = await fs.readFile(this.localConfigPath, 'utf8');
                this.localConfig = JSON.parse(localConfigContent);
            } catch (error) {
                console.log(' No local config found, will create default');
                this.localConfig = this.createDefaultLocalConfig();
            }

        } catch (error) {
            console.error('Failed to load configurations:', error.message);
            throw error;
        }
    }

    /**
     * Create default shared configuration
     */
    createDefaultSharedConfig() {
        return {
            version: "1.0.0",
            updated_at: new Date().toISOString(),
            updated_by: "system",
            project_standards: {
                php_version: "8.2+",
                laravel_version: "10.x",
                coding_standard: "PSR-12",
                documentation: "PHPDoc required",
                testing: "PHPUnit + Pest",
                static_analysis: "PHPStan Level 8"
            },
            required_packages: [
                "laravel/sanctum",
                "spatie/laravel-query-builder",
                "spatie/laravel-permission"
            ],
            development_tools: [
                "laravel/telescope",
                "barryvdh/laravel-debugbar",
                "nunomaduro/larastan"
            ],
            coding_standards: {
                php_cs_fixer: {
                    preset: "PSR12",
                    rules: {
                        "array_syntax": {"syntax": "short"},
                        "ordered_imports": {"sort_algorithm": "alpha"},
                        "no_unused_imports": true
                    }
                },
                phpstan: {
                    level: 8,
                    paths: ["app", "database", "routes", "tests"],
                    ignoreErrors: []
                }
            },
            workflow_templates: [
                "feature-branch-workflow",
                "code-review-checklist",
                "deployment-checklist"
            ]
        };
    }

    /**
     * Create default local configuration
     */
    createDefaultLocalConfig() {
        return {
            version: "1.0.0",
            created_at: new Date().toISOString(),
            developer_id: this.generateDeveloperId(),
            preferences: {
                preferred_ide: "cursor",
                code_style: "team_standard",
                debugging_tools: ["telescope"],
                testing_framework: "pest"
            },
            environment: {
                database: "mysql",
                queue_driver: "database",
                cache_driver: "file",
                mail_driver: "log"
            },
            personal_shortcuts: {},
            custom_templates: {},
            local_overrides: {}
        };
    }

    /**
     * Generate unique developer ID
     */
    generateDeveloperId() {
        return crypto.randomBytes(8).toString('hex');
    }

    /**
     * Synchronize team standards
     */
    async syncTeamStandards(options = {}) {
        const { force = false, dryRun = false, backup = false } = options;

        console.log(' Starting team standards synchronization...');

        try {
            // Create backup if requested
            if (backup && !dryRun) {
                await this.createBackup();
            }

            // Validate shared configuration
            const validation = this.validateSharedConfig();
            if (!validation.valid && !force) {
                throw new Error(`Shared config validation failed: ${validation.errors.join(', ')}`);
            }

            // Merge configurations
            const mergedConfig = this.mergeConfigurations();

            if (dryRun) {
                return this.showDryRunResults(mergedConfig);
            }

            // Apply changes
            await this.applyConfigurationChanges(mergedConfig);

            // Verify synchronization
            if (options.verify) {
                await this.verifySynchronization();
            }

            console.log(' Team standards synchronized successfully');
            return { success: true, changes: mergedConfig.changes };

        } catch (error) {
            console.error(' Synchronization failed:', error.message);
            throw error;
        }
    }

    /**
     * Validate shared configuration
     */
    validateSharedConfig() {
        const errors = [];

        // Check required fields
        const requiredFields = ['project_standards', 'required_packages', 'coding_standards'];
        for (const field of requiredFields) {
            if (!this.sharedConfig[field]) {
                errors.push(`Missing required field: ${field}`);
            }
        }

        // Validate PHP version format
        if (this.sharedConfig.project_standards?.php_version) {
            const phpVersion = this.sharedConfig.project_standards.php_version;
            if (!/^\d+\.\d+\+?$/.test(phpVersion)) {
                errors.push(`Invalid PHP version format: ${phpVersion}`);
            }
        }

        // Validate package names
        if (this.sharedConfig.required_packages) {
            for (const pkg of this.sharedConfig.required_packages) {
                if (!/^[a-z0-9-]+\/[a-z0-9-]+$/.test(pkg)) {
                    errors.push(`Invalid package name: ${pkg}`);
                }
            }
        }

        return {
            valid: errors.length === 0,
            errors
        };
    }

    /**
     * Merge shared and local configurations
     */
    mergeConfigurations() {
        const changes = {
            updated: [],
            conflicts: [],
            added: []
        };

        const merged = JSON.parse(JSON.stringify(this.localConfig));

        // Merge project standards (shared takes precedence)
        if (this.sharedConfig.project_standards) {
            const standardsChanges = this.mergeSection(
                merged.project_standards || {},
                this.sharedConfig.project_standards,
                'project_standards'
            );
            changes.updated.push(...standardsChanges.updated);
            changes.conflicts.push(...standardsChanges.conflicts);
            merged.project_standards = standardsChanges.merged;
        }

        // Merge coding standards (shared takes precedence)
        if (this.sharedConfig.coding_standards) {
            const codingChanges = this.mergeSection(
                merged.coding_standards || {},
                this.sharedConfig.coding_standards,
                'coding_standards'
            );
            changes.updated.push(...codingChanges.updated);
            merged.coding_standards = codingChanges.merged;
        }

        // Handle packages (merge lists)
        if (this.sharedConfig.required_packages) {
            const currentPackages = merged.required_packages || [];
            const newPackages = this.sharedConfig.required_packages.filter(
                pkg => !currentPackages.includes(pkg)
            );

            if (newPackages.length > 0) {
                changes.added.push(`Added ${newPackages.length} required packages`);
                merged.required_packages = [...currentPackages, ...newPackages];
            }
        }

        return { merged, changes };
    }

    /**
     * Merge configuration section
     */
    mergeSection(local, shared, sectionName) {
        const changes = { updated: [], conflicts: [], merged: {} };
        const merged = { ...local };

        for (const [key, sharedValue] of Object.entries(shared)) {
            const localValue = local[key];

            if (localValue === undefined) {
                // New setting from shared config
                merged[key] = sharedValue;
                changes.updated.push(`${sectionName}.${key}: added`);
            } else if (JSON.stringify(localValue) !== JSON.stringify(sharedValue)) {
                // Conflict between local and shared
                if (this.shouldPreferShared(sectionName, key)) {
                    merged[key] = sharedValue;
                    changes.conflicts.push({
                        key: `${sectionName}.${key}`,
                        local: localValue,
                        shared: sharedValue,
                        resolution: 'used_shared'
                    });
                } else {
                    // Keep local value
                    changes.conflicts.push({
                        key: `${sectionName}.${key}`,
                        local: localValue,
                        shared: sharedValue,
                        resolution: 'kept_local'
                    });
                }
            }
        }

        changes.merged = merged;
        return changes;
    }

    /**
     * Determine if shared value should be preferred
     */
    shouldPreferShared(section, key) {
        const sharedPreference = [
            'project_standards',
            'coding_standards',
            'required_packages',
            'testing'
        ];

        const localPreference = [
            'preferences',
            'environment',
            'personal_shortcuts',
            'custom_templates'
        ];

        if (sharedPreference.includes(section)) return true;
        if (localPreference.includes(section)) return false;

        // Default to shared for code quality settings
        const codeQualityKeys = ['php_version', 'coding_standard', 'static_analysis'];
        return codeQualityKeys.includes(key);
    }

    /**
     * Show dry run results
     */
    showDryRunResults(mergedConfig) {
        console.log('\n Dry run - showing what would be synced:\n');

        if (mergedConfig.changes.updated.length > 0) {
            console.log(' Settings that would be updated:');
            mergedConfig.changes.updated.forEach(change => {
                console.log(`    ${change}`);
            });
        }

        if (mergedConfig.changes.conflicts.length > 0) {
            console.log('\n  Conflicts detected:');
            mergedConfig.changes.conflicts.forEach(conflict => {
                console.log(`    ${conflict.key}`);
                console.log(`     Local: ${JSON.stringify(conflict.local)}`);
                console.log(`     Shared: ${JSON.stringify(conflict.shared)}`);
                console.log(`     Resolution: ${conflict.resolution}`);
            });
        }

        if (mergedConfig.changes.added.length > 0) {
            console.log('\n New additions:');
            mergedConfig.changes.added.forEach(addition => {
                console.log(`    ${addition}`);
            });
        }

        console.log('\nRun without --dry-run to apply these changes.');
        return { dryRun: true, changes: mergedConfig.changes };
    }

    /**
     * Apply configuration changes
     */
    async applyConfigurationChanges(mergedConfig) {
        // Update local configuration
        await fs.writeFile(
            this.localConfigPath,
            JSON.stringify(mergedConfig.merged, null, 2)
        );

        // Update composer scripts if needed
        await this.updateComposerScripts();

        // Update configuration files
        await this.updateConfigurationFiles();

        console.log(' Configuration files updated');
    }

    /**
     * Update composer scripts
     */
    async updateComposerScripts() {
        const composerPath = path.join(process.cwd(), 'composer.json');

        try {
            const composerContent = await fs.readFile(composerPath, 'utf8');
            const composer = JSON.parse(composerContent);

            // Add team scripts
            if (!composer.scripts) composer.scripts = {};

            composer.scripts['team:lint'] = 'vendor/bin/php-cs-fixer fix --dry-run --diff';
            composer.scripts['team:fix'] = 'vendor/bin/php-cs-fixer fix';
            composer.scripts['team:test'] = 'vendor/bin/pest --coverage';
            composer.scripts['team:analyze'] = 'vendor/bin/phpstan analyse';

            await fs.writeFile(composerPath, JSON.stringify(composer, null, 2));
            console.log(' Updated composer scripts');
        } catch (error) {
            console.warn('  Could not update composer.json:', error.message);
        }
    }

    /**
     * Update configuration files
     */
    async updateConfigurationFiles() {
        // Update PHP CS Fixer config
        await this.updatePhpCsFixerConfig();

        // Update PHPStan config
        await this.updatePhpStanConfig();
    }

    /**
     * Update PHP CS Fixer configuration
     */
    async updatePhpCsFixerConfig() {
        const config = this.sharedConfig.coding_standards?.php_cs_fixer;
        if (!config) return;

        const configContent = `<?php

return (new PhpCsFixer\\Config())
    ->setRiskyAllowed(true)
    ->setRules(${JSON.stringify(config.rules, null, 8)})
    ->setFinder(
        PhpCsFixer\\Finder::create()
            ->exclude('bootstrap/cache')
            ->exclude('storage')
            ->exclude('vendor')
            ->in(getcwd())
            ->name('*.php')
            ->notName('*.blade.php')
            ->ignoreDotFiles(true)
            ->ignoreVCS(true)
    );
`;

        const configPath = path.join(process.cwd(), '.php-cs-fixer.dist.php');
        await fs.writeFile(configPath, configContent);
        console.log(' Updated PHP CS Fixer configuration');
    }

    /**
     * Update PHPStan configuration
     */
    async updatePhpStanConfig() {
        const config = this.sharedConfig.coding_standards?.phpstan;
        if (!config) return;

        const configContent = `parameters:
    level: ${config.level}
    paths:
${config.paths.map(path => `        - ${path}`).join('\n')}
    excludePaths:
        - bootstrap/cache
        - storage/framework
        - vendor
    checkMissingIterableValueType: false
    reportUnmatchedIgnoredErrors: false
`;

        const configPath = path.join(process.cwd(), 'phpstan.neon.dist');
        await fs.writeFile(configPath, configContent);
        console.log(' Updated PHPStan configuration');
    }

    /**
     * Create backup of current configuration
     */
    async createBackup() {
        const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
        const backupPath = path.join(this.configDir, `local.json.backup.${timestamp}`);

        try {
            await fs.copyFile(this.localConfigPath, backupPath);
            console.log(` Created backup: ${path.basename(backupPath)}`);
        } catch (error) {
            console.warn('  Could not create backup:', error.message);
        }
    }

    /**
     * Verify synchronization
     */
    async verifySynchronization() {
        console.log(' Verifying synchronization...');

        const checks = [
            this.verifyComposerScripts(),
            this.verifyConfigurationFiles(),
            this.verifyPackages()
        ];

        const results = await Promise.allSettled(checks);
        const passed = results.filter(r => r.status === 'fulfilled').length;

        console.log(` Verification completed: ${passed}/${results.length} checks passed`);

        return { passed, total: results.length, results };
    }

    /**
     * Verify composer scripts exist
     */
    async verifyComposerScripts() {
        const composerPath = path.join(process.cwd(), 'composer.json');
        const content = await fs.readFile(composerPath, 'utf8');
        const composer = JSON.parse(content);

        const requiredScripts = ['team:lint', 'team:test', 'team:analyze'];
        const missing = requiredScripts.filter(script => !composer.scripts?.[script]);

        if (missing.length > 0) {
            throw new Error(`Missing composer scripts: ${missing.join(', ')}`);
        }

        return true;
    }

    /**
     * Verify configuration files exist and are valid
     */
    async verifyConfigurationFiles() {
        const files = [
            '.php-cs-fixer.dist.php',
            'phpstan.neon.dist'
        ];

        for (const file of files) {
            const filePath = path.join(process.cwd(), file);
            try {
                await fs.access(filePath);
            } catch {
                throw new Error(`Missing configuration file: ${file}`);
            }
        }

        return true;
    }

    /**
     * Verify required packages are installed
     */
    async verifyPackages() {
        const composerPath = path.join(process.cwd(), 'composer.json');
        const content = await fs.readFile(composerPath, 'utf8');
        const composer = JSON.parse(content);

        const installedPackages = {
            ...composer.require,
            ...composer['require-dev']
        };

        const required = this.sharedConfig.required_packages || [];
        const missing = required.filter(pkg => !installedPackages[pkg]);

        if (missing.length > 0) {
            throw new Error(`Missing required packages: ${missing.join(', ')}`);
        }

        return true;
    }

    /**
     * Get team status summary
     */
    getTeamStatus() {
        return {
            shared_config: {
                version: this.sharedConfig.version,
                updated_at: this.sharedConfig.updated_at,
                standards_count: Object.keys(this.sharedConfig.project_standards || {}).length
            },
            local_config: {
                developer_id: this.localConfig.developer_id,
                created_at: this.localConfig.created_at,
                preferences_set: Object.keys(this.localConfig.preferences || {}).length
            },
            synchronization: {
                last_sync: this.localConfig.last_sync,
                sync_required: this.isSyncRequired()
            }
        };
    }

    /**
     * Check if synchronization is required
     */
    isSyncRequired() {
        if (!this.localConfig.last_sync) return true;

        const lastSync = new Date(this.localConfig.last_sync);
        const sharedUpdated = new Date(this.sharedConfig.updated_at);

        return sharedUpdated > lastSync;
    }
}

// Export for use in other modules
if (typeof module !== 'undefined' && module.exports) {
    module.exports = LaravelTeamManager;
}

// CLI functionality when run directly
if (require.main === module) {
    const manager = new LaravelTeamManager();

    const command = process.argv[2];
    const options = {
        force: process.argv.includes('--force'),
        dryRun: process.argv.includes('--dry-run'),
        backup: process.argv.includes('--backup'),
        verify: process.argv.includes('--verify')
    };

    (async () => {
        await manager.initialize();

        switch (command) {
            case 'sync':
                await manager.syncTeamStandards(options);
                break;

            case 'status':
                const status = manager.getTeamStatus();
                console.log('\n Team Status:');
                console.log(JSON.stringify(status, null, 2));
                break;

            case 'validate':
                const validation = manager.validateSharedConfig();
                if (validation.valid) {
                    console.log(' Shared configuration is valid');
                } else {
                    console.log(' Validation errors:');
                    validation.errors.forEach(error => console.log(`   ${error}`));
                }
                break;

            default:
                console.log(`
Laravel Team Manager

Usage:
  node team-manager.js <command> [options]

Commands:
  sync      Synchronize team standards
  status    Show team configuration status
  validate  Validate shared configuration

Options:
  --force    Force sync even with conflicts
  --dry-run  Show what would change without applying
  --backup   Create backup before changes
  --verify   Verify sync after completion

Examples:
  node team-manager.js sync --verify
  node team-manager.js sync --dry-run --backup
                `);
        }
    })().catch(console.error);
}

