#!/usr/bin/env node

/**
 * Laravel Setup Validation System for Claude Code
 * 
 * Comprehensive validation and testing system for generated Laravel configurations,
 * ensuring all components work correctly and follow best practices.
 */

const fs = require('fs').promises;
const path = require('path');
const { execSync, spawn } = require('child_process');

class LaravelValidationSystem {
    constructor() {
        this.claudeDir = path.join(__dirname, '..');
        this.projectRoot = process.cwd();
        this.validationResults = {
            passed: 0,
            failed: 0,
            warnings: 0,
            details: []
        };
    }

    /**
     * Run complete validation suite
     */
    async runValidation(options = {}) {
        const { verbose = false, fix = false, subset = null } = options;

        console.log(' Starting Laravel Setup Validation...\n');

        try {
            const validators = this.getValidators(subset);

            for (const validator of validators) {
                await this.runValidator(validator, { verbose, fix });
            }

            this.printSummary();
            return this.validationResults;

        } catch (error) {
            console.error(' Validation system error:', error.message);
            return { error: error.message };
        }
    }

    /**
     * Get list of validators to run
     */
    getValidators(subset) {
        const allValidators = [
            // Core System Validation
            { name: 'claude-directory-structure', category: 'core', critical: true },
            { name: 'command-files-exist', category: 'core', critical: true },
            { name: 'script-files-executable', category: 'core', critical: true },
            { name: 'configuration-files-valid', category: 'core', critical: true },

            // Template System Validation
            { name: 'template-syntax-valid', category: 'templates', critical: false },
            { name: 'template-variables-consistent', category: 'templates', critical: false },
            { name: 'workflow-definitions-valid', category: 'templates', critical: false },

            // Laravel Integration Validation
            { name: 'laravel-project-detected', category: 'laravel', critical: true },
            { name: 'laravel-commands-functional', category: 'laravel', critical: true },
            { name: 'permissions-appropriate', category: 'laravel', critical: true },

            // Team Collaboration Validation
            { name: 'team-config-valid', category: 'team', critical: false },
            { name: 'shared-local-config-sync', category: 'team', critical: false },

            // Performance & Security Validation
            { name: 'performance-benchmarks', category: 'performance', critical: false },
            { name: 'security-configuration', category: 'security', critical: true },
            { name: 'sensitive-data-protection', category: 'security', critical: true }
        ];

        if (subset) {
            return allValidators.filter(v => 
                subset.includes(v.category) || subset.includes(v.name)
            );
        }

        return allValidators;
    }

    /**
     * Run individual validator
     */
    async runValidator(validator, options) {
        const { verbose, fix } = options;
        const startTime = Date.now();

        try {
            if (verbose) {
                console.log(` Running: ${validator.name}`);
            }

            const result = await this[this.getValidatorMethod(validator.name)](fix);
            const duration = Date.now() - startTime;

            if (result.passed) {
                this.validationResults.passed++;
                if (verbose) {
                    console.log(` ${validator.name} (${duration}ms)`);
                }
            } else {
                if (validator.critical) {
                    this.validationResults.failed++;
                    console.log(` ${validator.name}: ${result.error}`);
                } else {
                    this.validationResults.warnings++;
                    console.log(`  ${validator.name}: ${result.error}`);
                }
            }

            this.validationResults.details.push({
                name: validator.name,
                category: validator.category,
                critical: validator.critical,
                passed: result.passed,
                error: result.error,
                duration,
                fixes: result.fixes || []
            });

        } catch (error) {
            this.validationResults.failed++;
            console.log(` ${validator.name}: ${error.message}`);

            this.validationResults.details.push({
                name: validator.name,
                category: validator.category,
                critical: validator.critical,
                passed: false,
                error: error.message,
                duration: Date.now() - startTime
            });
        }
    }

    /**
     * Get validator method name
     */
    getValidatorMethod(validatorName) {
        return validatorName.replace(/-/g, '_') + '_validator';
    }

    /**
     * Validate Claude directory structure
     */
    async claude_directory_structure_validator(fix) {
        const requiredDirs = [
            'commands',
            'commands/laravel',
            'commands/laravel/feature-generation',
            'commands/laravel/performance',
            'commands/laravel/security',
            'commands/laravel/testing',
            'commands/laravel/database',
            'commands/laravel/deployment',
            'commands/team',
            'scripts',
            'templates',
            'templates/project-types',
            'templates/code',
            'templates/workflows',
            'config'
        ];

        const missingDirs = [];

        for (const dir of requiredDirs) {
            const dirPath = path.join(this.claudeDir, dir);
            try {
                await fs.access(dirPath);
            } catch {
                missingDirs.push(dir);
            }
        }

        if (missingDirs.length > 0) {
            if (fix) {
                for (const dir of missingDirs) {
                    const dirPath = path.join(this.claudeDir, dir);
                    await fs.mkdir(dirPath, { recursive: true });
                }
                return { passed: true, fixes: missingDirs.map(d => `Created directory: ${d}`) };
            }
            return { passed: false, error: `Missing directories: ${missingDirs.join(', ')}` };
        }

        return { passed: true };
    }

    /**
     * Validate command files exist
     */
    async command_files_exist_validator(fix) {
        const requiredCommands = [
            'commands/setup-laravel.md',
            'commands/laravel/feature-generation/feature.md',
            'commands/laravel/feature-generation/api-resource.md',
            'commands/laravel/feature-generation/livewire-component.md',
            'commands/laravel/feature-generation/inertia-page.md',
            'commands/laravel/performance/optimize-queries.md',
            'commands/laravel/performance/cache-strategy.md',
            'commands/laravel/performance/performance-audit.md',
            'commands/laravel/security/security-audit.md',
            'commands/laravel/security/code-quality.md',
            'commands/laravel/security/vulnerability-scan.md',
            'commands/laravel/testing/test-suite.md',
            'commands/laravel/testing/api-docs.md',
            'commands/laravel/database/migration-review.md',
            'commands/laravel/deployment/deploy-prep.md',
            'commands/team/sync-standards.md'
        ];

        const missingCommands = [];

        for (const command of requiredCommands) {
            const commandPath = path.join(this.claudeDir, command);
            try {
                await fs.access(commandPath);

                // Validate file has content
                const content = await fs.readFile(commandPath, 'utf8');
                if (content.trim().length < 100) {
                    missingCommands.push(`${command} (empty or too short)`);
                }
            } catch {
                missingCommands.push(command);
            }
        }

        if (missingCommands.length > 0) {
            return { passed: false, error: `Missing or invalid commands: ${missingCommands.join(', ')}` };
        }

        return { passed: true };
    }

    /**
     * Validate script files are executable
     */
    async script_files_executable_validator(fix) {
        const scriptFiles = [
            'scripts/analyze-project.js',
            'scripts/generate-permissions.js',
            'scripts/generate-claude-md.js',
            'scripts/template-manager.js',
            'scripts/team-manager.js'
        ];

        const issues = [];

        for (const script of scriptFiles) {
            const scriptPath = path.join(this.claudeDir, script);

            try {
                await fs.access(scriptPath);

                // Check if file is executable (has shebang)
                const content = await fs.readFile(scriptPath, 'utf8');
                if (!content.startsWith('#!/usr/bin/env node')) {
                    issues.push(`${script} missing shebang`);
                }

                // Validate basic syntax
                if (!content.includes('class ') || !content.includes('module.exports')) {
                    issues.push(`${script} invalid structure`);
                }

            } catch {
                issues.push(`${script} not found`);
            }
        }

        if (issues.length > 0) {
            return { passed: false, error: `Script issues: ${issues.join(', ')}` };
        }

        return { passed: true };
    }

    /**
     * Validate configuration files are valid JSON
     */
    async configuration_files_valid_validator(fix) {
        const configFiles = [
            'config/team-collaboration.json',
            'templates/project-types/api-only.json',
            'templates/project-types/full-stack-spa.json',
            'templates/project-types/traditional-web.json',
            'templates/project-types/microservice.json',
            'templates/workflows/api-endpoint-creation.json',
            'templates/workflows/livewire-component-creation.json',
            'templates/workflows/deployment-preparation.json'
        ];

        const invalidFiles = [];

        for (const configFile of configFiles) {
            const configPath = path.join(this.claudeDir, configFile);

            try {
                const content = await fs.readFile(configPath, 'utf8');
                JSON.parse(content); // Validate JSON syntax

                // Basic structure validation
                const config = JSON.parse(content);
                if (!config.name || !config.description) {
                    invalidFiles.push(`${configFile} missing name/description`);
                }

            } catch (error) {
                invalidFiles.push(`${configFile}: ${error.message}`);
            }
        }

        if (invalidFiles.length > 0) {
            return { passed: false, error: `Invalid config files: ${invalidFiles.join(', ')}` };
        }

        return { passed: true };
    }

    /**
     * Validate template syntax
     */
    async template_syntax_valid_validator(fix) {
        const templateDir = path.join(this.claudeDir, 'templates', 'code');

        try {
            const files = await fs.readdir(templateDir);
            const templateFiles = files.filter(f => f.endsWith('.stub'));

            const issues = [];

            for (const templateFile of templateFiles) {
                const templatePath = path.join(templateDir, templateFile);
                const content = await fs.readFile(templatePath, 'utf8');

