                if (dir.endsWith('.json')) {
                    files.push(dir);
                } else {
                    const dirFiles = await fs.readdir(dir, { recursive: true });
                    files.push(...dirFiles
                        .filter(f => f.endsWith('.json'))
                        .map(f => path.join(dir, f))
                    );
                }
            } catch {
                // Directory doesn't exist, skip
            }
        }

        return files;
