    }

    async performance_benchmarks_validator(fix) {
        return { passed: true }; // Placeholder - implement if needed
    }

    /**
     * Print validation summary
     */
    printSummary() {
        const { passed, failed, warnings, details } = this.validationResults;
        const total = passed + failed + warnings;

        console.log('\n Validation Summary:');
        console.log(` Passed: ${passed}`);
        console.log(` Failed: ${failed}`);
        console.log(`  Warnings: ${warnings}`);
        console.log(` Total: ${total}\n`);

        if (failed > 0) {
            console.log(' Critical Issues:');
            details
                .filter(d => !d.passed && d.critical)
                .forEach(d => console.log(`    ${d.name}: ${d.error}`));
        }

        if (warnings > 0) {
            console.log('  Warnings:');
            details
                .filter(d => !d.passed && !d.critical)
                .forEach(d => console.log(`    ${d.name}: ${d.error}`));
        }

        const successRate = Math.round((passed / total) * 100);
        console.log(`\n Success Rate: ${successRate}%`);

        if (successRate >= 90) {
            console.log(' Excellent! Your Laravel setup is ready to go!');
        } else if (successRate >= 70) {
            console.log(' Good setup with minor issues to address.');
        } else {
            console.log(' Several issues need attention before proceeding.');
        }
    }
}

// Export for use in other modules
if (typeof module !== 'undefined' && module.exports) {
    module.exports = LaravelValidationSystem;
}

// CLI functionality when run directly
if (require.main === module) {
    const validator = new LaravelValidationSystem();

