#!/usr/bin/env node

/**
 * Laravel Maintenance Manager for Claude Code
 * 
 * Handles ongoing maintenance, updates, and health monitoring
 * for the Laravel + Claude Code integration system.
 */

const fs = require('fs').promises;
const path = require('path');
const { execSync } = require('child_process');

class LaravelMaintenanceManager {
    constructor() {
        this.claudeDir = path.join(__dirname, '..');
        this.projectRoot = process.cwd();
        this.maintenanceLog = [];
        this.healthChecks = [];
    }

    /**
     * Run maintenance routine
     */
    async runMaintenance(options = {}) {
        const { 
            checkUpdates = true, 
            cleanCache = true,
            validateSetup = true,
            updateDocs = false,
            verbose = false 
        } = options;

        console.log(' Starting Laravel + Claude Code maintenance...\n');

        try {
            if (checkUpdates) await this.checkForUpdates(verbose);
            if (cleanCache) await this.cleanSystemCache(verbose);
            if (validateSetup) await this.validateSystemHealth(verbose);
            if (updateDocs) await this.updateDocumentation(verbose);

            await this.generateMaintenanceReport();

            console.log('\n Maintenance completed successfully!');
            return { success: true, log: this.maintenanceLog };

        } catch (error) {
            console.error(' Maintenance failed:', error.message);
            return { success: false, error: error.message };
        }
    }

    /**
     * Check for system updates
     */
    async checkForUpdates(verbose) {
        if (verbose) console.log(' Checking for updates...');

        const updateChecks = [
            this.checkLaravelFrameworkUpdates(),
            this.checkNodeDependencyUpdates(),
            this.checkClaudeCodeUpdates(),
            this.checkTemplateUpdates()
        ];

        const results = await Promise.allSettled(updateChecks);

        let updatesAvailable = 0;
        results.forEach((result, index) => {
            if (result.status === 'fulfilled' && result.value.updatesAvailable) {
                updatesAvailable += result.value.count;
                this.maintenanceLog.push({
                    type: 'update_check',
                    category: ['laravel', 'node', 'claude', 'templates'][index],
                    updates: result.value.updates
                });
            }
        });

        if (updatesAvailable > 0) {
            console.log(` ${updatesAvailable} updates available`);
        } else {
            console.log(' All systems up to date');
        }
    }

    /**
     * Check Laravel framework updates
     */
    async checkLaravelFrameworkUpdates() {
        try {
            const composerPath = path.join(this.projectRoot, 'composer.json');
            const composerContent = await fs.readFile(composerPath, 'utf8');
            const composer = JSON.parse(composerContent);

            const currentLaravel = composer.require?.['laravel/framework'];
            if (!currentLaravel) {
                return { updatesAvailable: false };
            }

            // Check for outdated packages using composer
            const outdatedOutput = execSync('composer outdated --direct --format=json', {
                cwd: this.projectRoot,
                encoding: 'utf8'
            });

            const outdated = JSON.parse(outdatedOutput);
            const laravelUpdates = outdated.installed?.filter(pkg => 
                pkg.name.startsWith('laravel/') || pkg.name.includes('laravel')
            ) || [];

            return {
                updatesAvailable: laravelUpdates.length > 0,
                count: laravelUpdates.length,
                updates: laravelUpdates
            };

        } catch (error) {
            return { updatesAvailable: false, error: error.message };
        }
    }

    /**
     * Check Node.js dependency updates
     */
    async checkNodeDependencyUpdates() {
        try {
            const packagePath = path.join(this.projectRoot, 'package.json');
            await fs.access(packagePath);

            const outdatedOutput = execSync('npm outdated --json', {
                cwd: this.projectRoot,
                encoding: 'utf8'
            });

            const outdated = JSON.parse(outdatedOutput || '{}');
            const updateCount = Object.keys(outdated).length;

            return {
                updatesAvailable: updateCount > 0,
                count: updateCount,
                updates: outdated
            };

        } catch (error) {
            // No package.json or npm not available
            return { updatesAvailable: false };
        }
    }

    /**
     * Check Claude Code system updates
     */
    async checkClaudeCodeUpdates() {
        try {
            // Check if there are newer versions of our scripts/templates
            const lastUpdateFile = path.join(this.claudeDir, '.last-update');
            let lastUpdate = new Date(0);

            try {
                const lastUpdateContent = await fs.readFile(lastUpdateFile, 'utf8');
                lastUpdate = new Date(lastUpdateContent.trim());
            } catch {
                // No last update file, consider as needing update
            }

            // Check modification times of key files
            const keyFiles = [
                'commands/setup-laravel.md',
                'scripts/analyze-project.js',
                'scripts/template-manager.js',
                'scripts/team-manager.js'
            ];

            let hasUpdates = false;
            for (const file of keyFiles) {
                const filePath = path.join(this.claudeDir, file);
                try {
                    const stats = await fs.stat(filePath);
                    if (stats.mtime > lastUpdate) {
                        hasUpdates = true;
                        break;
                    }
                } catch {
                    // File doesn't exist
                }
            }

            return {
                updatesAvailable: hasUpdates,
                count: hasUpdates ? 1 : 0,
                updates: hasUpdates ? ['Claude Code system files updated'] : []
            };

        } catch (error) {
            return { updatesAvailable: false, error: error.message };
        }
    }

    /**
     * Check template updates
     */
    async checkTemplateUpdates() {
        try {
            const templatesDir = path.join(this.claudeDir, 'templates');
            const templateFiles = await this.getAllTemplateFiles(templatesDir);

            // For now, just return no updates
            // In a real system, this would check against a remote repository
            return {
                updatesAvailable: false,
                count: 0,
                updates: []
            };

        } catch (error) {
            return { updatesAvailable: false, error: error.message };
        }
    }

    /**
     * Clean system cache
     */
    async cleanSystemCache(verbose) {
        if (verbose) console.log(' Cleaning system cache...');

        const cleanTasks = [
            this.cleanLaravelCache(),
            this.cleanNodeCache(),
            this.cleanClaudeCache()
        ];

        const results = await Promise.allSettled(cleanTasks);

        let cleanedItems = 0;
        results.forEach((result, index) => {
            if (result.status === 'fulfilled') {
                cleanedItems += result.value.cleaned || 0;
                this.maintenanceLog.push({
                    type: 'cache_clean',
                    category: ['laravel', 'node', 'claude'][index],
                    cleaned: result.value.cleaned,
                    size: result.value.size
                });
            }
        });

        console.log(` Cleaned ${cleanedItems} cache items`);
    }

    /**
     * Clean Laravel cache
     */
    async cleanLaravelCache() {
        try {
            const cacheCommands = [
                'php artisan config:clear',
                'php artisan route:clear',
                'php artisan view:clear',
                'php artisan cache:clear'
            ];

            let cleaned = 0;
            for (const command of cacheCommands) {
                try {
                    execSync(command, { cwd: this.projectRoot, stdio: 'pipe' });
                    cleaned++;
                } catch {
                    // Command failed, skip
                }
            }

            return { cleaned };

        } catch (error) {
            return { cleaned: 0, error: error.message };
        }
    }

    /**
     * Clean Node.js cache
     */
    async cleanNodeCache() {
        try {
            // Clean npm cache
            execSync('npm cache clean --force', { 
                cwd: this.projectRoot, 
                stdio: 'pipe' 
            });

            return { cleaned: 1 };

        } catch (error) {
            return { cleaned: 0, error: error.message };
        }
    }

    /**
     * Clean Claude Code cache
     */
    async cleanClaudeCache() {
        try {
            const cacheDir = path.join(this.claudeDir, '.cache');

            try {
                await fs.rmdir(cacheDir, { recursive: true });
                await fs.mkdir(cacheDir, { recursive: true });
                return { cleaned: 1 };
            } catch {
                return { cleaned: 0 };
            }

        } catch (error) {
            return { cleaned: 0, error: error.message };
        }
    }

    /**
     * Validate system health
     */
    async validateSystemHealth(verbose) {
        if (verbose) console.log(' Validating system health...');

        const healthChecks = [
            this.checkLaravelHealth(),
            this.checkDatabaseHealth(),
            this.checkFilePermissions(),
            this.checkConfigurationHealth(),
            this.checkSecurityHealth()
        ];

        const results = await Promise.allSettled(healthChecks);

        let healthyChecks = 0;
        let totalChecks = results.length;

        results.forEach((result, index) => {
            const checkNames = [
                'laravel', 'database', 'permissions', 
                'configuration', 'security'
            ];

            if (result.status === 'fulfilled' && result.value.healthy) {
                healthyChecks++;
            }

            this.healthChecks.push({
                name: checkNames[index],
                healthy: result.status === 'fulfilled' && result.value.healthy,
                details: result.status === 'fulfilled' ? result.value : result.reason,
                timestamp: new Date().toISOString()
            });
        });

        const healthScore = Math.round((healthyChecks / totalChecks) * 100);
        console.log(`  System health: ${healthScore}% (${healthyChecks}/${totalChecks})`);

        if (healthScore < 80) {
            console.log('  Some health checks failed - review maintenance log');
        }
    }

    /**
     * Check Laravel application health
     */
    async checkLaravelHealth() {
        try {
            // Check if artisan commands work
            execSync('php artisan --version', { 
                cwd: this.projectRoot, 
                stdio: 'pipe' 
            });

            // Check if key Laravel files exist
            const laravelFiles = [
                'app/Http/Kernel.php',
                'config/app.php',
                'routes/web.php'
            ];

            for (const file of laravelFiles) {
                await fs.access(path.join(this.projectRoot, file));
            }

            return { healthy: true, details: 'Laravel core is functional' };

        } catch (error) {
            return { healthy: false, details: `Laravel health check failed: ${error.message}` };
        }
    }

    /**
     * Check database health
     */
    async checkDatabaseHealth() {
        try {
            execSync('php artisan migrate:status', { 
                cwd: this.projectRoot, 
                stdio: 'pipe' 
            });

            return { healthy: true, details: 'Database connection is working' };

        } catch (error) {
            return { healthy: false, details: `Database check failed: ${error.message}` };
        }
    }

    /**
     * Check file permissions
     */
    async checkFilePermissions() {
        try {
            const criticalDirs = [
                'storage/logs',
                'storage/framework/cache',
                'storage/framework/sessions',
                'storage/framework/views',
                'bootstrap/cache'
            ];

            for (const dir of criticalDirs) {
                const dirPath = path.join(this.projectRoot, dir);
                try {
                    await fs.access(dirPath, fs.constants.W_OK);
                } catch {
                    return { 
                        healthy: false, 
                        details: `Directory not writable: ${dir}` 
                    };
                }
            }

            return { healthy: true, details: 'File permissions are correct' };

        } catch (error) {
            return { healthy: false, details: `Permission check failed: ${error.message}` };
        }
    }

    /**
     * Check configuration health
     */
    async checkConfigurationHealth() {
        try {
            const configFiles = [
                path.join(this.claudeDir, 'config/team-collaboration.json'),
                path.join(this.claudeDir, '..', 'settings.local.json')
            ];

            for (const configFile of configFiles) {
                try {
                    const content = await fs.readFile(configFile, 'utf8');
                    JSON.parse(content); // Validate JSON
                } catch {
                    return { 
                        healthy: false, 
                        details: `Invalid config file: ${path.basename(configFile)}` 
                    };
                }
            }

            return { healthy: true, details: 'Configuration files are valid' };

        } catch (error) {
            return { healthy: false, details: `Config check failed: ${error.message}` };
        }
    }

    /**
     * Check security health
     */
    async checkSecurityHealth() {
        try {
            const securityChecks = [
                this.checkEnvFileSecurity(),
                this.checkGitignoreSecurity(),
                this.checkPermissionsSecurity()
            ];

            const results = await Promise.allSettled(securityChecks);
            const failedChecks = results.filter(r => 
                r.status === 'rejected' || !r.value.secure
            );

            if (failedChecks.length > 0) {
                return { 
                    healthy: false, 
                    details: `${failedChecks.length} security issues found` 
                };
            }

            return { healthy: true, details: 'Security checks passed' };

        } catch (error) {
            return { healthy: false, details: `Security check failed: ${error.message}` };
        }
    }

    /**
     * Check .env file security
     */
    async checkEnvFileSecurity() {
        const envPath = path.join(this.projectRoot, '.env');

        try {
            await fs.access(envPath);

            // Check if .env is in .gitignore
            const gitignorePath = path.join(this.projectRoot, '.gitignore');
            const gitignoreContent = await fs.readFile(gitignorePath, 'utf8');

            if (!gitignoreContent.includes('.env')) {
                return { secure: false, issue: '.env not in .gitignore' };
            }

            return { secure: true };

        } catch {
            // .env doesn't exist, that's okay
            return { secure: true };
        }
    }

    /**
     * Check .gitignore security
     */
    async checkGitignoreSecurity() {
        const gitignorePath = path.join(this.projectRoot, '.gitignore');

        try {
            const content = await fs.readFile(gitignorePath, 'utf8');

            const requiredEntries = [
                '.env',
                'storage/logs/',
                '.claude/settings.local.json'
            ];

            const missingEntries = requiredEntries.filter(entry => 
                !content.includes(entry)
            );

            if (missingEntries.length > 0) {
                return { 
                    secure: false, 
                    issue: `Missing .gitignore entries: ${missingEntries.join(', ')}` 
                };
            }

            return { secure: true };

        } catch {
            return { secure: false, issue: '.gitignore file not found' };
        }
    }

    /**
     * Check permissions security
     */
    async checkPermissionsSecurity() {
        const settingsPath = path.join(this.claudeDir, '..', 'settings.local.json');

        try {
            const content = await fs.readFile(settingsPath, 'utf8');
            const settings = JSON.parse(content);

            // Check if permissions are overly broad
            const bashPerms = settings.permissions?.bash;
            if (bashPerms?.execute?.includes('*') || bashPerms?.write?.includes('*')) {
                return { 
                    secure: false, 
                    issue: 'Overly broad permissions detected' 
                };
            }

            return { secure: true };

        } catch {
            return { secure: false, issue: 'Settings file not accessible' };
        }
    }

    /**
     * Update documentation
     */
    async updateDocumentation(verbose) {
        if (verbose) console.log(' Updating documentation...');

        try {
            // Update CLAUDE.md if it exists
            const claudeMdPath = path.join(this.projectRoot, 'CLAUDE.md');

            try {
                await fs.access(claudeMdPath);

                // Re-generate CLAUDE.md with latest information
                const { LaravelClaudeDocGenerator } = require('./generate-claude-md.js');
                const generator = new LaravelClaudeDocGenerator();
                await generator.generateDocumentation();

                console.log(' CLAUDE.md updated');

            } catch {
                // CLAUDE.md doesn't exist, skip
            }

            // Update maintenance log timestamp
            const lastUpdateFile = path.join(this.claudeDir, '.last-update');
            await fs.writeFile(lastUpdateFile, new Date().toISOString());

        } catch (error) {
            console.warn('  Documentation update failed:', error.message);
        }
    }

    /**
     * Generate maintenance report
     */
    async generateMaintenanceReport() {
        const report = {
            timestamp: new Date().toISOString(),
            maintenance_log: this.maintenanceLog,
            health_checks: this.healthChecks,
            summary: {
                total_actions: this.maintenanceLog.length,
                health_score: this.calculateHealthScore(),
                recommendations: this.generateRecommendations()
            }
        };

        const reportPath = path.join(this.claudeDir, '.maintenance-report.json');
        await fs.writeFile(reportPath, JSON.stringify(report, null, 2));

        console.log(` Maintenance report saved: ${reportPath}`);
    }

    /**
     * Calculate overall health score
     */
    calculateHealthScore() {
        if (this.healthChecks.length === 0) return 100;

        const healthyChecks = this.healthChecks.filter(check => check.healthy).length;
        return Math.round((healthyChecks / this.healthChecks.length) * 100);
    }

    /**
     * Generate maintenance recommendations
     */
    generateRecommendations() {
        const recommendations = [];

        // Check health issues
        const unhealthyChecks = this.healthChecks.filter(check => !check.healthy);
        if (unhealthyChecks.length > 0) {
            recommendations.push('Address health check failures');
        }

        // Check for updates
        const updateLogs = this.maintenanceLog.filter(log => log.type === 'update_check');
        const hasUpdates = updateLogs.some(log => log.updates && log.updates.length > 0);
        if (hasUpdates) {
            recommendations.push('Apply available updates');
        }

        // General recommendations
        if (recommendations.length === 0) {
            recommendations.push('System is healthy - continue regular maintenance');
        }

        return recommendations;
    }

    /**
     * Get all template files recursively
     */
    async getAllTemplateFiles(dir) {
        const files = [];

        try {
            const entries = await fs.readdir(dir, { withFileTypes: true });

            for (const entry of entries) {
                const fullPath = path.join(dir, entry.name);

                if (entry.isDirectory()) {
                    const subFiles = await this.getAllTemplateFiles(fullPath);
                    files.push(...subFiles);
                } else if (entry.name.endsWith('.json') || entry.name.endsWith('.stub')) {
                    files.push(fullPath);
                }
            }
        } catch {
            // Directory doesn't exist or isn't accessible
        }

        return files;
    }

    /**
     * Schedule automatic maintenance
     */
    async scheduleAutomaticMaintenance() {
        const schedule = {
            daily: ['cleanCache'],
            weekly: ['checkUpdates', 'validateSystemHealth'],
            monthly: ['updateDocumentation', 'generateMaintenanceReport']
        };

        // This would integrate with a job scheduler in a real system
        console.log(' Maintenance schedule configured');
        return schedule;
    }
}

// Export for use in other modules
if (typeof module !== 'undefined' && module.exports) {
    module.exports = LaravelMaintenanceManager;
}

// CLI functionality when run directly
if (require.main === module) {
    const manager = new LaravelMaintenanceManager();

    const command = process.argv[2] || 'all';
    const options = {
        verbose: process.argv.includes('--verbose') || process.argv.includes('-v'),
        checkUpdates: !process.argv.includes('--no-updates'),
        cleanCache: !process.argv.includes('--no-clean'),
        validateSetup: !process.argv.includes('--no-validate'),
        updateDocs: process.argv.includes('--update-docs')
    };

    (async () => {
        switch (command) {
            case 'all':
            case 'run':
                await manager.runMaintenance(options);
                break;

            case 'health':
                await manager.validateSystemHealth(true);
                break;

            case 'updates':
                await manager.checkForUpdates(true);
                break;

            case 'clean':
                await manager.cleanSystemCache(true);
                break;

            case 'schedule':
                await manager.scheduleAutomaticMaintenance();
                break;

            default:
                console.log(`
Laravel Maintenance Manager

Usage:
  node maintenance-manager.js <command> [options]

Commands:
  all, run    Run complete maintenance routine (default)
  health      Check system health only
  updates     Check for updates only
  clean       Clean system cache only
  schedule    Set up automatic maintenance

Options:
  --verbose, -v       Verbose output
  --no-updates        Skip update checks
  --no-clean          Skip cache cleaning
  --no-validate       Skip health validation
  --update-docs       Force documentation update

Examples:
  node maintenance-manager.js run --verbose
  node maintenance-manager.js health
  node maintenance-manager.js clean --verbose
                `);
        }
    })().catch(error => {
        console.error(' Maintenance failed:', error.message);
        process.exit(1);
    });
}

