import { debounce } from 'lodash'
import AppLayout from '@/Layouts/AppLayout'
import ProductCard from '@/Components/Products/ProductCard'
import Pagination from '@/Components/Pagination'

export default function ProductsIndex({ products, categories, filters, sort }) {
  const [localFilters, setLocalFilters] = useState({
    search: filters.search || '',
    category: filters.category || '',
    price_range: filters.price_range || '',
  })

  const [sortBy, setSortBy] = useState(
    `${sort.sort_by || 'created_at'},${sort.sort_order || 'desc'}`
  )

  // Memoized values
  const hasActiveFilters = useMemo(() => {
    return Object.values(localFilters).some(value => value !== '')
  }, [localFilters])

  // Debounced search function
  const debouncedSearch = useCallback(
    debounce(() => {
      applyFilters()
    }, 300),
    [localFilters]
  )

  const applyFilters = useCallback(() => {
    const query = { ...localFilters }

    // Remove empty filters
    Object.keys(query).forEach(key => {
      if (query[key] === '') delete query[key]
    })

    router.get(route('products.index'), query, {
      preserveState: true,
      preserveScroll: true,
    })
  }, [localFilters])

  const applySort = useCallback(() => {
    const [sort_by, sort_order] = sortBy.split(',')

