      ...localFilters,
      sort_by,
      sort_order,
    }, {
      preserveState: true,
      preserveScroll: true,
    })
  }, [localFilters, sortBy])

  const handleFilterChange = (key, value) => {
    setLocalFilters(prev => ({ ...prev, [key]: value }))
    if (key === 'search') {
      debouncedSearch()
    } else {
      applyFilters()
    }
