        Product::factory()->create(['category_id' => $category->id]);
        Product::factory()->create(); // Different category

        $response = $this->get(route('products.index', ['category' => $category->id]));

        $response->assertInertia(fn (Assert $page) => 
            $page->component('Products/Index')
                ->has('products.data', 1)
                ->where('products.data.0.category_id', $category->id)
        );
    }

    public function test_can_create_product()
    {
        $category = Category::factory()->create();
        $data = [
            'name' => 'Test Product',
            'description' => 'Test Description',
            'price' => 99.99,
            'category_id' => $category->id,
        ];

        $response = $this->post(route('products.store'), $data);

        $response->assertRedirect(route('products.index'))
            ->assertSessionHas('success');

        $this->assertDatabaseHas('products', $data);
    }

    public function test_can_update_product()
    {
        $product = Product::factory()->create();
        $data = [
            'name' => 'Updated Product',
            'description' => 'Updated Description',
            'price' => 199.99,
            'category_id' => $product->category_id,
        ];

        $response = $this->put(route('products.update', $product), $data);

        $response->assertRedirect(route('products.show', $product))
            ->assertSessionHas('success');

        $this->assertDatabaseHas('products', $data);
    }
