import { ref, reactive, computed, watch } from 'vue'
import { router, Link } from '@inertiajs/vue3'
import { debounce } from 'lodash'
import AppLayout from '@/layouts/app-layout.vue'
import ProductCard from '@/components/products/product-card.vue'
import Pagination from '@/components/pagination.vue'

const props = defineProps({
  products: Object,
  categories: Array,
  filters: Object,
  sort: Object,
})

// Reactive state
const filters = reactive({
  search: props.filters.search || '',
  category: props.filters.category || '',
  price_range: props.filters.price_range || '',
})

const sortBy = ref(`${props.sort.sort_by || 'created_at'},${props.sort.sort_order || 'desc'}`)

// Computed properties
const hasActiveFilters = computed(() => {
  return Object.values(filters).some(value => value !== '')
})

// Methods
const applyFilters = () => {
  const query = { ...filters }

  // Remove empty filters
  Object.keys(query).forEach(key => {
    if (query[key] === '') delete query[key]
  })

  router.get(route('products.index'), query, {
    preserveState: true,
    preserveScroll: true,
  })
}

const applySort = () => {
  const [sort_by, sort_order] = sortBy.value.split(',')

  router.get(route('products.index'), {
    ...filters,
    sort_by,
    sort_order,
  }, {
    preserveState: true,
    preserveScroll: true,
  })
}

const debounceSearch = debounce(() => {
  applyFilters()
}, 300)

const clearFilters = () => {
  Object.keys(filters).forEach(key => {
    filters[key] = ''
  })
  sortBy.value = 'created_at,desc'

  router.get(route('products.index'), {}, {
    preserveState: true,
  })
}

const handleDelete = (productId) => {
  if (confirm('Are you sure you want to delete this product?')) {
    router.delete(route('products.destroy', productId), {
      preserveScroll: true,
      onSuccess: () => {
        // Handle success notification
      }
    })
  }
}

// Watch for filter changes
watch(() => props.filters, (newFilters) => {
  Object.assign(filters, newFilters)
}, { deep: true })
</script>
